import React, { useState } from "react";
import { Link } from "react-router-dom";
import Modal from "react-modal";
import { useEffect } from "react";
import axios from "axios";
import "./Promotion.css";

const customStyles = {
  content: {
    top: "50%",
    left: "50%",
    right: "auto",
    bottom: "auto",
    marginRight: "-50%",
    transform: "translate(-50%, -50%)",
  },
};

function Promotion() {
  let subtitle;
  const [modalIsOpenPromotion, setIsOpenPromotion] = useState(false);
  const [modalIsOpenSelect, setIsOpenSelect] = useState(false);

  function openModalPromotion() {
    setIsOpenPromotion(true);
  }

  function openModalSelect() {
    setIsOpenSelect(true);
  }

  function afterOpenModal() {
    // references are now sync'd and can be accessed.
    subtitle.style.color = "black";
  }

  function closeModal() {
    setIsOpenPromotion(false);
    setIsOpenSelect(false);
  }
  //-----------------------------------------------------

  const [shoplist, setShoplist] = useState([]);
  const [promotionlist, setPromotionList] = useState([]);
  const [exchange,setexchange] = useState();
  const [shopname,setShopname] = useState("");

  const logout = () => {
    axios.get("/logout");
    window.location.replace("/");
  };

  const getpromotion = (shopname) => {
    axios
      .post("/selectshop", {
        shop_name: shopname,
      })
      .then((response) => {
        //ใส่ modal
        setPromotionList(response.data);
        openModalPromotion();
      });

    //window.location.replace('/promotiondetails');
  };

  const selectpromotion = (id) => {  
    axios.post("/selectpromotion", {
      id : id,
    }).then((response) =>{
      if(response) {
        setexchange(response.data[0].amount);
        setShopname(response.data[0].shop_name);
        openModalSelect();
      }
    });
  };

  useEffect(() => {
    axios.get("http://localhost:3001/login").then((response) => {
      if (response.data.loggedIn == true) {
      }
      if (
        response.data.loggedIn == false ||
        response.data.user[0].role != "member"
      ) {
        return window.location.replace("/");
      }
    });
    axios.get("/getshoplistpromotion").then((response) => {
      if (response) {
        setShoplist(response.data);
      }
    });
  }, []);

  return (
    <div>
      <link
        href="https://maxcdn.bootstrapcdn.com/font-awesome/4.7.0/css/font-awesome.min.css"
        rel="stylesheet"
      />

      <nav class="navbar navbar-light bg-light justify-content-between">
        <Link className="buttonblack" to="/">
          <i className="fas fa-angle-left">
            <a className="bl">ย้อนกลับ</a>
          </i>
        </Link>
      </nav>

      <div>
        <Modal
          isOpen={modalIsOpenPromotion}
          onAfterOpen={afterOpenModal}
          onRequestClose={closeModal}
          style={customStyles}
          contentLabel="Example Modal"
        >
          <h2
            className="h2h2promotion"
            ref={(_subtitle) => (subtitle = _subtitle)}
          >
            รายการ Promotion
          </h2>
          {promotionlist.map((val) => {
            return (
              <div
                className="cotainerpromotion-test"
                onClick={() => {
                  selectpromotion(val.id);
                }}
              >
                <div onClick={() =>{selectpromotion(val.id)}} className="boxpromotion">
                  <p>{val.product_name}</p>
                  <p>{val.amount}</p>
                </div>
              </div>
            );
          })}
        </Modal>
      </div>

      <div className="div-transferpointmember">
        <Modal
          isOpen={modalIsOpenSelect}
          onAfterOpen={afterOpenModal}
          onRequestClose={closeModal}
          style={customStyles}
          contentLabel="Example Modal"
        >
          <h2
            className="h2transferpointmember"
            ref={(_subtitle) => (subtitle = _subtitle)}
          >
            <i className="fas fa-check-circle"></i>ร้านค้า {shopname} จำนวน {exchange} คะแนน
          </h2>
        </Modal>
      </div>

      <div className="whos-speaking-area speakers pad100">
        <div className="container">
          <div className="row">
            <div className="col-lg-12">
              <div className="section-title text-center">
                <div className="title-text mb50">
                  <h2 className="h2promotionmember">โปรโมชั่นร้านค้า</h2>
                  <input
                    className="insearch"
                    type="text"
                    placeholder="ค้นหาร้านค้า"
                  />
                  <i className="fas fa-search"></i>
                </div>
              </div>
            </div>
          </div>
          {shoplist.map((val) => {
            return (
              <div
                className="cotainerpromotion"
                onClick={() => {
                  getpromotion(val.shop_name);
                }}
              >
                <div className="boxpromotion">
                  <p>{val.shop_name}</p>
                </div>
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
}
export default Promotion;
