import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { Buttonshop } from './Buttonshop';
import axios from 'axios';
import { useEffect } from 'react';
import './Promotionshop.css';

import Promotiondetails from './Promotiondetails';


function Promotionshop() {
    const [click, setClick] = useState(false);
    const [button, setButton] = useState(true);


    const handleClick = () => setClick(!click);
    const closeMobileMenu = () => setClick(false);

    const showButton = () => {
        if (window.innerWidth <= 960) {
            setButton(false)
        } else {
            setButton(true)
        }
    };

    window.addEventListener('resize', showButton);
    //-----------------------------------------------

    const [shoplist, setShoplist] = useState([]);
    const [shopname, setShopname] = useState([]);

    const logout = () => {
        axios.get("/logout");
        window.location.replace('/');
    };

    const gotopromotion = (shopname) => {
        axios.post("/selectpromotionshop", {
            shop_name : shopname
        })
            .then((response) => {
                console.log(response.data);
            });

        //window.location.replace('/promotiondetails');
    };

    useEffect(() => {
        axios.get("/login").then((response) => {
            if (response.data.loggedIn == true) {

            }
            if (response.data.loggedIn == false || response.data.user[0].role != 'shop') {
                return (window.location.replace('/'));
            }
        });

        axios.get("/getshoplistpromotion").then((response) => {
            if (response) {
                setShoplist(response.data);
            }
        });

    }, []);

    return (
        <>
            <nav className="navbar">
                <div className="navbar-container">
                    <Link to="/" className="navbar-logo">
                        TNC
                    </Link>
                    <div className='menu-icon' onClick={handleClick}>
                        <i className={click ? 'fas fa-times' : 'fas fa-angle-down'} />

                    </div>


                    <ul className={click ? 'nav-menu active' : 'nav-menu'}>

                        <li className='nav-item'>
                            <Link className='nav-links' onClick={closeMobileMenu}>

                            </Link>
                        </li>

                        {button && <Buttonshop buttonStyle='btn--outline'>MY PROFILE</Buttonshop>}
                        <Link to="/ออกจากระบบ" className="navbar-logo">
                            <i class='fas fa-sign-out-alt'></i>
                        </Link>
                    </ul>


                </div>

            </nav>

            <div className='add-promotion'>
                <Link to="/addpromotion" className="add">
                    <i class='fas fa-plus'></i> เพิ่มโปรโมชั่น
                </Link><br />
                <Link to="/myshop">
                    ร้านค้าของฉัน
                </Link>
                {shoplist.map((val) => {
                    return (
                        <div>
                            <button onClick={() => { gotopromotion(val.shop_name) }}>{val.shop_name}</button>
                        </div>
                    )
                })}

            </div>


        </>

    );
}
export default Promotionshop;
