import React, { useState } from 'react';
import { Link, Redirect } from 'react-router-dom';
import { useEffect } from 'react';
import Modal from 'react-modal';
import axios from 'axios';

import './Memberprofile.css';


function Memberprofile() {
  const [click, setClick] = useState(false);


  const handleClick = () => setClick(!click);
  const closeMobileMenu = () => setClick(false);

  //----------------------------------------------------------
  const [id, setId] = useState();
  const [name, setName] = useState("");
  const [phone, setPhone] = useState("");
  const [email, setEmail] = useState("");
  const [point, setPoint] = useState();

  const [statusPhone, setStatusPhone] = useState("");

  const [newname, setnewName] = useState("");
  const [newphone, setnewPhone] = useState("");

  const [show, setShow] = useState(false);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  axios.defaults.withCredentials = true;
  const logout = () => {
    axios.get("/logout");
    window.location.replace('/');
  }
  const refresh = () => {
    axios.get("/member");
    window.location.replace('/memberprofile');
  }
  const nameUpdate = () => {
    axios.put("/updatememberName", { name: newname, id: id }).then((response) => {
        return setName(response.data.user[0].name);
    });
    
  }
  const phoneUpdate = () => {
    axios.put("/updatememberPhone", { phone: newphone, id: id }).then((response) => {
      if (response.data.message) {
        return setStatusPhone(response.data.message);
      }
        setPhone(response.data.user[0].phone);
    });
  }

  useEffect(() => {
    axios.get("/login").then((response) => {
      if (response.data.loggedIn == true) {
        setId(response.data.user[0].id);
        setName(response.data.user[0].name);
        setPhone(response.data.user[0].phone);
        setEmail(response.data.user[0].email);
        setPoint(response.data.user[0].point);
      }
    });
  }, []);
  return (
    <>
      <nav className="navbar">
        <div className="navbar-container">
          <Link to="/" className="navbar-logo">
            TNC
          </Link>
          <div className='menu-icon' onClick={handleClick}>
            <i className={click ? 'fas fa-times' : 'fas fa-angle-down'} />

          </div>


          <ul className={click ? 'nav-menu active' : 'nav-menu'}>

            <li className='nav-item'>
              <p className='nav-links' onClick={closeMobileMenu}></p>
            </li>

            <p onClick={logout} className="navbar-logo">
              <i className='fas fa-sign-out-alt'></i>
            </p>
          </ul>


        </div>

      </nav>


      <Button variant="primary" onClick={handleShow}>
        Launch demo modal
      </Button>

      <Modal show={show} onHide={handleClose}>
        <Modal.Header closeButton>
          <Modal.Title>Modal heading</Modal.Title>
        </Modal.Header>
        <Modal.Body>Woohoo, you're reading this text in a modal!</Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Close
          </Button>
          <Button variant="primary" onClick={handleClose}>
            Save Changes
          </Button>
        </Modal.Footer>
      </Modal>

      
      <div>
        <h1>member</h1>
        <p>Hi  {name} </p>
        <p>Phone : {phone} <br />Email : {email} <br />Point :  {point} <button onClick={refresh}>refresh</button></p>
        <br />
        <input type="text" placeholder="name"
          onChange={(e) => { setnewName(e.target.value) }
          }></input>
        <button className="btn" onClick={nameUpdate}>update</button>

        <input type="text" placeholder="Phone"
          onChange={(e) => { setnewPhone(e.target.value) }
          }></input>
        {statusPhone}
        <button className="btn" onClick={phoneUpdate}>update</button>

        <br /><button className="logout" onClick={logout}>Logout</button>
      </div>

    </>

  );
}
export default Memberprofile;
