import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { Buttonmember } from './Buttonmember';
import { useEffect } from 'react';
import axios from 'axios';
import './Promotion.css';




function Promotion() {

    //-----------------------------------------------------
    const logout = () => {
        axios.get("/logout");
        window.location.replace('/');
    }

    useEffect(() => {
        axios.get("http://localhost:3001/login").then((response) => {
            if (response.data.loggedIn == true) {

            }
            if (response.data.loggedIn == false || response.data.user[0].role != 'member') {
                return (window.location.replace('/'));
            }
        });
        axios.get("/getshoplistpromotion").then((response) => {
            if (response) {
                setShoplist(response.data);
            }
        });

    }, []);

    return (
        <div>
            <link href="https://maxcdn.bootstrapcdn.com/font-awesome/4.7.0/css/font-awesome.min.css" rel="stylesheet" />

            <nav class="navbar navbar-light bg-light justify-content-between">
                <Link to="/"  ><i className="fas fa-angle-left"><a className="bl">ย้อนกลับ</a></i></Link>
            </nav>

            <div className="whos-speaking-area speakers pad100">
                <div className="container">
                    <div className="row">
                        <div className="col-lg-12">
                            <div className="section-title text-center">
                                <div className="title-text mb50">
                                    <h2>โปรโมชั่นร้านค้า</h2>
                                    <input type="text" placeholder="search" /> <i className="fas fa-search"></i>
                                </div>
                            </div>
                        </div>

                    </div>
                    {shoplist.map((val) => {
                        return (
                            <div>
                                <button onClick={() => { getpromotion(val.shop_name) }}>{val.shop_name}</button>
                            </div>
                        )
                    })}
                    <div className="row mb50"  >
                        <div className="col-xl-3 col-lg-3 col-md-4 col-sm-12">
                            <div className="speakers xs-mb30">
                                <div className="spk-info">
                                    <Link className="shopname" to="/promotiondetails"><h1>shopname</h1></Link>
                                </div>
                            </div>
                        </div>


                    </div>

                </div>

            </div>

        </div>

    );
}
export default Promotion;
