import React, { useState } from 'react'
import { Link, useHistory } from 'react-router-dom'
import { useEffect } from 'react'
import axios from 'axios'
import Modal from 'react-modal'
import './Calculatepointshop.css'
import { motion } from 'framer-motion'

const customStyles = {
  content: {
    width: '100%',
    height: '50%',
    top: '50%',
    left: '50%',
    right: 'auto',
    bottom: 'auto',
    marginRight: '-50%',
    transform: 'translate(-50%, -50%)',
  },
}

function Calculatepointshop() {
  const [modalIsOpen, setIsOpen] = React.useState(false)

  function openModal() {
    setIsOpen(true)
  }

  function afterOpenModal() {
    // references are now sync'd and can be accessed.
  }

  function closeModal() {
    setIsOpen(false)
  }

  //-----------------------------------------------------
  const history = useHistory()
  //-----------------------------------------------------
  const [product_code, setproduct_code] = useState('')
  const [productlist, setProductlist] = useState([])
  const [promotionlist, setPromotionlist] = useState([])
  const [total_price, setTotal_Price] = useState(0)
  const [id, setId] = useState()
  const [shoppoint, setShopPoint] = useState()
  const [phone, setPhone] = useState('')
  const [destPhone, setdestPhone] = useState('')
  const [total_point, setTotal_Point] = useState(0)
  const [product_amount, setproduct_amount] = useState(1)

  const [namesearch, setnamesearch] = useState('')
  const [allproduct, setALLPRODUCT] = useState([])
  const [fillterALLPRODUCT, setFilteredALLPRODUCT] = useState([])

  const refresh = () => {
    axios.get('/shop')
    window.location.reload()
  }

  const searchproductname = (event) => {
    let value = event.toLowerCase()
    let result = []
    result = allproduct.filter((data) => {
      return data.name.search(value) != -1
    })
    setFilteredALLPRODUCT(result)
  }

  const entername = () => {
    if (!namesearch) {
    } else {
      axios
        .post('/enternamesearch', { namesearch: namesearch })
        .then((response) => {})
    }
  }

  const entercode = (number) => {
    var a
    if (!product_code) {
    } else {
      axios
        .post('/enterproductcode', {
          product_code: product_code,
        })
        .then((response) => {
          if (response.data.productlist) {
            for (a = 0; a < `${productlist.length}`; a++) {}
            setProductlist([
              ...productlist,
              {
                id: a,
                product_name: response.data.productlist[0].product_name,
                product_code: response.data.productlist[0].product_code,
                price: response.data.productlist[0].price,
                number: number,
              },
            ])
            setTotal_Price(
              total_price + response.data.productlist[0].price * number,
            )
          } else if (response.data.result2) {
            for (a = 0; a < `${productlist.length}`; a++) {}
            setProductlist([
              ...productlist,
              {
                id: a,
                product_name: response.data.result1[0].product_name,
                product_code: response.data.result1[0].product_code,
                price: response.data.result1[0].price,
                number: number,
              },
            ])
            setPromotionlist([
              ...promotionlist,
              {
                id: a,
                product_name: response.data.result1[0].product_name,
                product_code: response.data.result1[0].product_code,
                price: response.data.result1[0].price,
                amount_point: response.data.result2.amount_point,
                number: number,
              },
            ])
            setTotal_Price(
              total_price + response.data.result1[0].price * number,
            )
          }
        })
      setproduct_code('')
    }
  }

  const transferTocustomer = () => {
    if (!destPhone) {
      axios.post('/transaction/buying', {
        list: productlist,
        destPhone: destPhone,
      })
      alert('success')
      history.go()
    } else {
      if (shoppoint < total_point) {
        alert('คะแนนร้านค้าไม่เพียงพอ กรุณาเติมคะแนน')
      } else {
        axios
          .post('/calculateprice', {
            total_point: total_point,
            destPhone: destPhone,
          })
          .then((response) => {
            if (response.data.calculate) {
              axios.post('/transaction/buying', {
                list: productlist,
                destPhone: destPhone,
              })
              alert(response.data.calculate)
              axios.get('/shop')
              history.go()
            } else {
              alert(response.data.message)
            }
          })
      }
    }
  }

  const calculatepoint = () => {
    var promotionpoint = 0
    for (let i = 0; i < `${promotionlist.length}`; i++) {
      promotionpoint +=
        (promotionlist[i].amount_point -
          Math.floor(promotionlist[i].price / 50)) *
        promotionlist[i].number
    }
    setTotal_Point(Math.floor(total_price / 50) + promotionpoint)
    if (productlist.length === 0) {
    } else {
      axios.get('/checksystemcoin').then((response) => {
        if (response.data.message) {
          alert(response.data.message)
          history.push('/')
        }
      })
      openModal()
    }
  }

  const deleteproduct = (e) => {
    const id = e.target.getAttribute('name')
    setProductlist(
      productlist.filter(
        (productlist) => JSON.stringify(productlist.id) !== id,
      ),
    )
    setPromotionlist(
      promotionlist.filter(
        (promotionlist) => JSON.stringify(promotionlist.id) !== id,
      ),
    )
  }

  const updateprice = (price) => {
    setTotal_Price(total_price - price)
  }

  const gotoTopupPage = () => {
    history.push('/top-up-point-shop')
  }

  /* ********************************** */

  const pageTransition = {
    in: {
      opacity: 1,
      x: 0,
    },
    out: {
      opacity: 0,
      x: 100,
    },
  }

  /* ********************************** */

  useEffect(() => {
    axios.get('/checksystemcoin').then((response) => {
      if (response.data.message) {
        alert(response.data.message)
        history.push('/')
      }
    })
    axios.get('/login').then((response) => {
      if (response.data.loggedIn === true) {
        axios.get('/shop').then((response) => {
          setId(response.data.user[0].id)
          setShopPoint(response.data.user[0].shoppoint)
          setPhone(response.data.user[0].phone)
        })
        axios.get('/getproduct').then((response) => {
          if (response.data) {
            setALLPRODUCT(response.data)
            setFilteredALLPRODUCT(response.data)
          }
        })
      }
      if (
        response.data.loggedIn === false ||
        response.data.user[0].role !== 'shop'
      ) {
        return history.push('/login')
      }
    })
  }, [])

  return (
    <div className="calculatepointpage">
      <div className="calculate-modal">
        <Modal
          isOpen={modalIsOpen}
          onAfterOpen={afterOpenModal}
          onRequestClose={closeModal}
          style={customStyles}
          contentLabel="Example Modal"
        >
          <motion.div
            initial={{ opacity: 0, y: 100 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: 100 }}
          >
            <div className="divcalculate-modal">
              <h2 className="pcalculate-modal">ได้รับ : {total_point} คะแนน</h2>
              <div>
                <button
                  className="buttoncalculate-modal1"
                  onClick={transferTocustomer}
                >
                  ยืนยัน
                </button>
                <button className="buttoncalculate-modal2" onClick={closeModal}>
                  ยกเลิก
                </button>
              </div>
            </div>
          </motion.div>
        </Modal>
      </div>
      <link
        rel="stylesheet"
        type="text/css"
        href="//netdna.bootstrapcdn.com/font-awesome/4.1.0/css/font-awesome.min.css"
      />
      <link
        href="//maxcdn.bootstrapcdn.com/font-awesome/4.2.0/css/font-awesome.min.css"
        rel="stylesheet"
      ></link>

      <nav className="navbar navbar-light bg-light justify-content-between">
        <Link className="buttonblack" to="/">
          <i className="fas fa-angle-left">
            <a className="bl">ย้อนกลับ</a>
          </i>
        </Link>
        <div className="div-img-logo2">
          <img className="img-logoo" src="/images/logo.png" />
        </div>
      </nav>

      <motion.div
        initial="out"
        animate="in"
        exit="out"
        variants={pageTransition}
      >
        <div className="calculate-point-shop">
          <button onClick={gotoTopupPage} className="buttontop-up">
            <i className="fas fa-plus"></i>
            เติมคะแนน
          </button>

          <div className="calculate-con">
            <h2 className="h2promotion">คำนวณคะแนน</h2>
            <h1 className="h1promotion">
              คะแนนของร้านค้า : {shoppoint}{' '}
              <i
                onClick={refresh}
                className="fas fa-sync-alt"
                style={{ color: 'black' }}
              />
            </h1>
          </div>

          <form
            className="from-calculatepointshop"
            onSubmit={(e) => {
              e.preventDefault()
              entercode(product_amount)
            }}
          >
            <label className="label-input1-calculatepointshopp">
              เบอร์โทรศัพท์ลูกค้า
            </label>
            <input
              className="input-input1-calculatepointshopp"
              type="tel"
              placeholder="เบอร์โทรลูกค้า"
              onChange={(e) => {
                setdestPhone(e.target.value)
              }}
            />
            <label className="label-input2-calculatepointshopp">
              รหัสสินค้า
            </label>
            <input
              className="input-input2-calculatepointshopp"
              type="text"
              placeholder="รหัสสินค้า"
              value={product_code}
              onChange={(e) => {
                setproduct_code(e.target.value)
              }}
            />
            <label>ชื่อสินค้า</label>
            <input
              placeholder="ป้อนชื่อสินค้า"
              onChange={(e) => {
                searchproductname(e.target.value)
              }}
            />
            {fillterALLPRODUCT.map((val) => {
              return <p>{val.product_name}</p>
            })}
            <label className="label-input3-calculatepointshopp">จำนวน</label>
            <input
              className="input-input3-calculatepointshopp"
              type="number"
              min="1"
              placeholder="จำนวน"
              value={product_amount}
              onChange={(e) => {
                setproduct_amount(e.target.value)
              }}
            />
            <button
              type="reset"
              className="btn btn-primary reset-product-amount"
              onClick={() => setproduct_amount(1)}
            >
              รีเซ็ตจำนวน
            </button>
            <button className="button-input-calculatepointshop" type="submit">
              ตกลง
            </button>
          </form>
        </div>

        <div className="table-wrapper-scroll-y my-custom-scrollbar">
          <div className="table-responsive">
            <center>
              <table className="table table-bordered">
                <thead>
                  <tr>
                    <th className="th-table" scope="col">
                      รายการสินค้า
                    </th>
                    <th className="th-table" scope="col">
                      ราคา
                    </th>
                    <th className="th-table" scope="col">
                      จำนวน
                    </th>
                    <th className="th-table" scope="col">
                      ตัวเลือก
                    </th>
                  </tr>
                </thead>
                {productlist.map((post) => {
                  return (
                    <tbody>
                      <tr>
                        <td className="td-table">
                          <p>{post.product_name}</p>
                        </td>
                        <td className="td-table">
                          <p price={post.price}>{post.price}</p>
                        </td>
                        <td className="td-table">
                          <p>{post.number}</p>
                        </td>
                        <td className="td-table">
                          <button
                            className="button-delete-item"
                            name={post.id}
                            onClick={(e) => {
                              deleteproduct(e)
                              updateprice(post.price * post.number)
                            }}
                          >
                            <i className="far fa-times-circle"></i> ลบรายการนี้{' '}
                            <i className="far fa-times-circle"></i>
                          </button>
                        </td>
                      </tr>
                    </tbody>
                  )
                })}
                <tr>
                  {promotionlist.map((promotion) => {
                    return (
                      <div style={{ display: 'flex' }}>
                        <p name={promotion.id}>
                          สินค้าโปรโมชัน : {promotion.product_name} x{' '}
                          {promotion.number}
                        </p>
                      </div>
                    )
                  })}
                </tr>
              </table>
            </center>
          </div>
        </div>
        <div className="div-total-price">
          <h3 className="h3-total-price">ราคารวม : {total_price} </h3>
          <button className="button-total-price" onClick={calculatepoint}>
            คำนวณเงิน
          </button>
        </div>
      </motion.div>
    </div>
  )
}
export default Calculatepointshop
