import React, { useState } from "react";
import { Link } from "react-router-dom";
import { Buttonmember } from "./Buttonmember";
import { useEffect } from "react";
import axios from "axios";
import "./Promotion.css";

function Promotion() {
  const values = [true, "sm-down", "md-down", "lg-down", "xl-down", "xxl-down"];
  const [fullscreen, setFullscreen] = useState(true);
  const [show, setShow] = useState(false);

  function handleShow(breakpoint) {
    setFullscreen(breakpoint);
    setShow(true);
  }
  //-----------------------------------------------------

  const [shoplist, setShoplist] = useState([]);

  const logout = () => {
    axios.get("/logout");
    window.location.replace("/");
  };

  const getpromotion = (shopname) => {
    axios
      .post("/selectpromotionshop", {
        shop_name: shopname,
      })
      .then((response) => {
        //ใส่ modal
        console.log(response.data);
      });

    //window.location.replace('/promotiondetails');
  };

  useEffect(() => {
    axios.get("http://localhost:3001/login").then((response) => {
      if (response.data.loggedIn == true) {
      }
      if (
        response.data.loggedIn == false ||
        response.data.user[0].role != "member"
      ) {
        return window.location.replace("/");
      }
    });
    axios.get("/getshoplistpromotion").then((response) => {
      if (response) {
        setShoplist(response.data);
      }
    });
  }, []);

  return (
    <div>
      <link
        href="https://maxcdn.bootstrapcdn.com/font-awesome/4.7.0/css/font-awesome.min.css"
        rel="stylesheet"
      />

      {values.map((v, idx) => (
        <button key={idx} className="me-2" onClick={() => handleShow(v)}>
          Full screen
          {typeof v === "string" && `below ${v.split("-")[0]}`}
        </button>
      ))}
      <Modal show={show} fullscreen={fullscreen} onHide={() => setShow(false)}>
        <Modal.Header closeButton>
          <Modal.Title>Modal</Modal.Title>
        </Modal.Header>
        <Modal.Body>Modal body content</Modal.Body>
      </Modal>

      <nav class="navbar navbar-light bg-light justify-content-between">
        <Link className="buttonblack" to="/">
          <i className="fas fa-angle-left">
            <a className="bl">ย้อนกลับ</a>
          </i>
        </Link>
      </nav>

      <div className="whos-speaking-area speakers pad100">
        <div className="container">
          <div className="row">
            <div className="col-lg-12">
              <div className="section-title text-center">
                <div className="title-text mb50">
                  <h2 className="h2promotionmember">โปรโมชั่นร้านค้า</h2>
                  <input
                    className="insearch"
                    type="text"
                    placeholder="ค้นหาร้านค้า"
                  />
                  <i className="fas fa-search"></i>
                </div>
              </div>
            </div>
          </div>
          {shoplist.map((val) => {
            return (
              <div
                className="cotainerpromotion"
                onClick={() => {
                  getpromotion(val.shop_name);
                }}
              >
                <div className="boxpromotion">
                  <p>{val.shop_name}</p>
                </div>
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
}
export default Promotion;
