import React from "react";
import { Link } from "react-router-dom";
import { useEffect, useState } from "react";
import { useHistory } from "react-router";
import axios from "axios";
import Modal from "react-modal";
import "./Addpromotion.css";
import { AnimatePresence, motion } from "framer-motion";

const customStyles = {
  content: {
    width: "100%",
    top: "50%",
    left: "50%",
    right: "auto",
    bottom: "auto",
    marginRight: "-50%",
    transform: "translate(-50%, -50%)",
  },
};

function Addpromotion(option) {
  const history = useHistory();
  const [name, setName] = useState("");

  const [product_name, setProductName] = useState("");
  const [product_code, setProductCode] = useState("");
  const [picture, setPicture] = useState("");
  const [category, setCategory] = useState("");
  const [amount_point, setAmount_Point] = useState();
  const [expired, setExpired] = useState("");

  const [productlist, setProductlist] = useState([]);
  const [modalIsOpen1, setIsOpen1] = React.useState(false);
  const [modalIsOpen2, setIsOpen2] = React.useState(false);
  //---------------------------------------------------
  const addpromotionpoint = () => {
    axios
      .post("/postpromotionpoint", {
        product_name: product_name,
        product_code: product_code,
        amount: amount_point,
        picture: picture,
        category: category,
        expired: expired,
      })
      .then((response) => {
        if (response.data.message) {
          alert(response.data.message);
          history.go();
        }
      });
  };
  const addpromotion = () => {
    axios
      .post("/postpromotion", {
        product_name: product_name,
        product_code: product_code,
        amount: amount_point,
        picture: picture,
        category: category,
      })
      .then((response) => {
        if (response.data.message) {
          alert(response.data.message);
          history.go();
        }
      });
  };

  /* ********************************** */

  const pageTransition = {

    in: {
      opacity: 1,
      x: 0,

    },
    out: {
      opacity: 0,
      x: "-100vh",

    }
  };


  /* ********************************** */

  function openModal(product_name, product_code, picture, category) {
    setProductName(product_name);
    setProductCode(product_code);
    setPicture(picture);
    setCategory(category);
    if (option.location.pathname === "/addpromotion/option1") {
      setIsOpen1(true);
    } else if (option.location.pathname === "/addpromotion/option2") {
      setIsOpen2(true);
    }
  }

  function afterOpenModal() {
    // references are now sync'd and can be accessed.
  }

  function closeModal() {
    setIsOpen1(false);
    setIsOpen2(false);
  }

  //---------------------------------------------------
  useEffect(() => {
    axios.get("/login").then((response) => {
      if (response.data.loggedIn === true) {
        setName(response.data.user[0].name);
        axios.get("/getproduct").then((response) => {
          if (response.data) {
            setProductlist(response.data);
            console.log(response.data);
          }
        });
      }
      if (
        response.data.loggedIn === false ||
        response.data.user[0].role !== "shop"
      ) {
        return history.push("/login");
      }
    });
  }, []);

  return (
    <div>
      <nav className="navbar navbar-light bg-light justify-content-between">
        <Link className="buttonblack" to="/myshop">
          <i className="fas fa-angle-left">
            <a className="bl">ย้อนกลับ</a>
          </i>
        </Link>
        <div className="div-img-logo2">
          <img className="img-logoo" src="/images/logo.png" />
        </div>
      </nav>

      <div className="addpromotionModal1">
        <Modal
          isOpen={modalIsOpen1}
          onAfterOpen={afterOpenModal}
          onRequestClose={closeModal}
          style={customStyles}
          contentLabel="Example Modal"
        >
           <motion.div initial={{ opacity: 0, y: 100 }} animate={{ opacity: 1, y: 0 }} exit={{ opacity: 0, y: 100 }}>
          {/*แต่งไว้ที่ promotion*/}
          <div className="toppic-promotin">
            <h2 className="h2promotionmember">เพิ่มโปรโมชั่นคะแนน</h2>
          </div>

          <div className="div-showproduct-detail">
            <p className="showproduct-detail">รหัสสินค้า : {product_code}</p>
            <p className="showproduct-detail">รหัสสินค้า : {product_code}</p>
            <p className="showproduct-detail">หมวดหมู่ : {category}</p>
          </div>
          <label className="label-showproduct-detail">
            จำนวนคะแนนสินค้าชิ้นนี้
          </label>
          <div className="divaddpromotionpoint-1">
            <input
              className="input-showproduct-detail"
              type="number"
              placeholder="จำนวนคะแนนสินค้าชิ้นนี้"
              onChange={(e) => {
                setAmount_Point(e.target.value);
              }}
            />
          </div>

          <label className="label-showproduct-detail">
            วันหมดอายุโปรโมชั่น
          </label>
          <div className="divaddpromotionpoint-1">
            <input
              className="input-showproduct-detail"
              type="date"
              onChange={(e) => {
                setExpired(e.target.value);
              }}
            />
            <div>
              <button
                className="buttoncalculate-modal1"
                onClick={addpromotionpoint}
              >
                ยืนยัน
              </button>
              <button className="buttoncalculate-modal2" onClick={closeModal}>
                ยกเลิก
              </button>
            </div>
          </div>
          </motion.div>
        </Modal>
      </div>

      <div className="addpromotionModal2">
        <Modal
          isOpen={modalIsOpen2}
          onAfterOpen={afterOpenModal}
          onRequestClose={closeModal}
          style={customStyles}
          contentLabel="Example Modal"
        >
           <motion.div initial={{ opacity: 0, y: 100 }} animate={{ opacity: 1, y: 0 }} exit={{ opacity: 0, y: 100 }}>
          {/*แต่งไว้ที่ promotion*/}
          <div className="toppic-promotin">
            <h2 className="h2promotionmember">เพิ่มโปรโมชั่นแลกสินค้า</h2>
          </div>

          <div className="div-showproduct-detail">
            <p className="showproduct-detail">รหัสสินค้า : {product_code}</p>
            <p className="showproduct-detail">รหัสสินค้า : {product_code}</p>
            <p className="showproduct-detail">หมวดหมู่ : {category}</p>
          </div>

          <label className="label-showproduct-detail">
            จำนวนคะแนนที่ใช้แลกสินค้าชิ้นนี้
          </label>
          <div className="divaddpromotionpoint-1">
            <input
              className="input-showproduct-detail"
              type="number"
              placeholder="0"
              onChange={(e) => {
                setAmount_Point(e.target.value);
              }}
            />
            <div>
              <button className="buttoncalculate-modal1" onClick={addpromotion}>
                ยืนยัน
              </button>
              <button className="buttoncalculate-modal2" onClick={closeModal}>
                ยกเลิก
              </button>
            </div>
          </div>
          </motion.div>
        </Modal>
      </div>
      {/*--------------------------------------------------------------*/}

      <motion.div initial="out" animate="in" exit="out" variants={pageTransition}>
      <div className="top-addpromotion">
        <div className="container-addpromotion">
          {productlist.map((product) => {
            return (
              <div
                className="shopbox-addpromotin"
                onClick={() => {
                  openModal(
                    product.product_name,
                    product.product_code,
                    product.picture,
                    product.category
                  );
                }}
              >
                {/*shopbox-img แต่งไว้ที่ promotionshop*/}
                <div className="shopbox-img">
                  <img className="img-fluid-shopbox" src={product.picture} style={{height : "100px"}} />
                </div>
                
                <div className="boxaddpromotion">
                  <p className="p-addpromotion">
                    สินค้า : {product.product_name}
                  </p>
                  <p className="p-addpromotion">ราคา : {product.price}</p>
                  <p className="p-addpromotion">
                    หมวดหมู่ : {product.category}
                  </p>
                </div>
              </div>
            );
          })}
        </div>
      </div>
      </motion.div>
    </div>
  );
}
export default Addpromotion;
