import axios from "axios";
import React from "react";
import { useState } from "react";
import { useHistory } from "react-router";
import "./Forgotpwd.css";
import { motion } from "framer-motion";

function Forgotpwd() {
  const history = useHistory();
  const [email, setemail] = useState("");
  const [status, setStatus] = useState("");
  const forgotpassword = (e) => {
    e.preventDefault();
    axios.post("/forgotpassword" + email).then((response) => {
      if (response.data.email) {
        history.push("/changepwd/" + response.data.email);
      } else if (response.data.message) {
        setStatus(response.data.message);
      }
    });
  };
  return (
    <div>
      <form onSubmit={forgotpassword}>
        <h1 className="h1-forgotpwd">Forgot Password</h1>
        <label className="label-forgotpwd">Email</label>
        <input
          type="email"
          className="input-forgotpwd"
          placeholder="sample@hotmail.com"
          onChange={(e) => {
            setemail(e.target.value);
          }}
          required
        />
        <p style={{ color: "red" }}>{status}</p>
        <button className="btn btn-primary forgotpwd-btn-submit">ยืนยัน</button>
        <a href="/login" className="cancel-forgotpwd">
          ยกเลิก
        </a>
      </form>
    </div>
  );
}

export default Forgotpwd;
