import React from 'react';
import { Route,Redirect } from 'react-router-dom';


const ProtectedRoute = ({ component: Component, Auth,role, ...rest }) => {
    return (
        <Route {...rest} render={
            props => {
                if (Auth) {
                    if(role) {
                    return <Component {...rest} {...props} />
                    }
                } else {
                    return <Redirect to={
                        {
                            pathname: '/login',
                            state: {
                                from: props.location
                            }
                        }
                    } />
                }
            }
        } />
    )
}

export default ProtectedRoute;
