import React, { useState } from 'react';
import { Router } from 'react-router-dom';
import { Link } from 'react-router-dom';
import { Buttonshop } from './Buttonshop';
import './Transactionshop.css';


function Transactionshop() {
    const [click, setClick] = useState(false);
    const [button, setButton] = useState(true);

    const handleClick = () => setClick(!click);
    const closeMobileMenu = () => setClick(false);

    const showButton = () => {
        if (window.innerWidth <= 960) {
            setButton(false)
        } else {
            setButton(true)
        }
    };

    window.addEventListener('resize', showButton);

    return (
        <div>
            <nav className="navbar">
                <div className="navbar-container">
                    <Link to="/" className="navbar-logo">
                        TNC
                    </Link>
                    <div className='menu-icon' onClick={handleClick}>
                        <i className={click ? 'fas fa-times' : 'fas fa-angle-down'} />

                    </div>


                    <ul className={click ? 'nav-menu active' : 'nav-menu'}>

                        <li className='nav-item'>
                            <Link className='nav-links' onClick={closeMobileMenu}>

                            </Link>
                        </li>

                        {button && <Buttonshop buttonStyle='btn--outline'>MY PROFILE</Buttonshop>}
                        <Link to="/ออกจากระบบ" className="navbar-logo">
                            <i class='fas fa-sign-out-alt'></i>
                        </Link>
                    </ul>


                </div>

            </nav>

            <div className="history">
                <table className="table">
                    <thead>
                        <tr>
                            <th scope="col">วันที่โอน</th>
                            <th scope="col">ผู้โอน</th>
                            <th scope="col">ผู้รับโอน</th>
                            <th scope="col">จำนวน</th>
                        </tr>
                    </thead>
                    {history.map((val) => {
                        return (
                            <tbody>
                                <tr>
                                    <th scope="row">{moment({ val }).format("L")}</th>
                                    <td key={val.id}>{val.transferor}</td>
                                    <td>{val.recipient}</td>
                                    <td>{val.amount}</td>
                                </tr>

                            </tbody>
                        )
                    })}
                </table>

            </div>


        </div>

    );
}
export default Transactionshop;
