import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { Buttonmember } from './Buttonmember';
import { useEffect } from 'react';
import axios from 'axios';
import './Promotiondetails.css';




function Promotiondetails() {
    const [click, setClick] = useState(false);
    const [button, setButton] = useState(true);


    const handleClick = () => setClick(!click);
    const closeMobileMenu = () => setClick(false);

    const showButton = () => {
        if (window.innerWidth <= 960) {
            setButton(false)
        } else {
            setButton(true)
        }
    };

    window.addEventListener('resize', showButton);
    //-----------------------------------------------------
    const logout = () => {
        axios.get("/logout");
        window.location.replace('/');
    }

    useEffect(() => {
        axios.get("http://localhost:3001/login").then((response) => {
            if (response.data.loggedIn == true) {

            }
            if (response.data.loggedIn == false || response.data.user[0].role != 'member') {
                return (window.location.replace('/'));
            }
        });
    }, []);

    return (
        <>
            <link href="https://maxcdn.bootstrapcdn.com/font-awesome/4.7.0/css/font-awesome.min.css" rel="stylesheet" />
            <nav className="navbar">
                <div className="navbar-container">
                    <Link to="/" className="navbar-logo">
                        TNC
                    </Link>
                    <div className='menu-icon' onClick={handleClick}>
                        <i className={click ? 'fas fa-times' : 'fas fa-angle-down'} />

                    </div>


                    <ul className={click ? 'nav-menu active' : 'nav-menu'}>

                        <li className='nav-item'>
                            <p className='nav-links' onClick={closeMobileMenu}></p>
                        </li>

                        {button && <Buttonmember buttonStyle='btn--outline'>MY PROFILE</Buttonmember>}
                        <p onClick={logout} className="navbar-logo">
                            <i className='fas fa-sign-out-alt'></i>
                        </p>
                    </ul>


                </div>

            </nav>

            <div className="whos-speaking-area speakers pad100">
                <div className="container">
                    <div className="row">
                        <div className="col-lg-12">
                            <div className="section-title text-center">
                                <div className="title-text mb50">
                                    <h2>โปรโมชั่นร้านค้า</h2>
                                    <input type="text" placeholder="search" /> <i className="fas fa-search"></i>
                                </div>
                            </div>
                        </div>

                    </div>

                    <div className="row mb50">
                        <div className="col-xl-3 col-lg-3 col-md-4 col-sm-12">
                            <div className="speakers xs-mb30">
                                <div className="spk-img">
                                    <img className="img-fluid" src="https://bootdey.com/img/Content/avatar/avatar1.png" alt="trainer-img" />
                                </div>
                                <div className="spk-info">
                                    <h3>Product : name</h3>
                                    <p>Point : amount</p>
                                </div>
                            </div>
                        </div>


                    </div>

                </div>

            </div>

        </>

    );
}
export default Promotiondetails;
