import React from "react";
import { useEffect } from "react";
import axios from "axios";
import { useState } from "react";
import "./login_and_register.css";
import { Redirect } from "react-router";
import { Link } from "react-router-dom";
import { useHistory } from "react-router";
import { Loader } from "semantic-ui-react";
import "./Login.css";
import { motion } from "framer-motion";

const Login = (props) => {
  const [active, setactive] = useState(false); //loader
  //----------------------------------------------------------
  const history = useHistory();
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const [loginStatus, setLoginSatus] = useState("");
  const [isLoggedIn, setIsLoggedIn] = useState("flase");

  axios.defaults.withCredentials = true;

  //login
  const login = (e) => {
    e;
    if (!email || !password) {
    } else {
      setactive(true);
      const timer = setTimeout(() => {
        setactive(false);
        axios
          .post("/login", {
            email: email,
            password: password,
          })
          .then((response) => {
            if (response.data.message) {
              setLoginSatus(response.data.message);
            } else {
              window.location.replace("/");
            }
          });
      }, 2000);
      return () => clearTimeout(timer);
    }
  };

  /* ********************************** */

  const pageTransition = {
    in: {
      opacity: 1,
      x: 0,
    },
    out: {
      opacity: 0,
      x: 100,
    },
  };

  /* ********************************** */

  useEffect(() => {
    axios.get("/login").then((response) => {
      if (response.data.loggedIn === true) {
        setIsLoggedIn("true");
      }
    });
  }, []);

  if (isLoggedIn === "true") {
    return <Redirect to="/" />;
  }
  return (
    <div className="login-page">
      <Loader active={active} />
      <nav className="navbar navbar-light bg-light justify-content-between">
        <Link className="buttonblack" to="/home">
          <i className="fas fa-angle-left">
            <a className="bl">ย้อนกลับ</a>
          </i>
        </Link>
      </nav>

      <motion.div
        initial="out"
        animate="in"
        exit="out"
        variants={pageTransition}
      >
        <div className="login-page-con">
          <form
            onSubmit={(e) => {
              login(e.preventDefault());
            }}
            className="signin-container"
          >
            <div className="form-signin">
              <i className="fas fa-sign-in-alt"></i>
              <h4 className="title-login">เข้าสู่ระบบ</h4>
            </div>
            <label className="form-signin-email">Email</label>

            <div className="form2-signin">
              <input
                type="email"
                className="input-email-login"
                placeholder="Email"
                onChange={(e) => {
                  setEmail(e.target.value);
                }}
                required
              />
            </div>

            <label className="form-signin-password">Password</label>
            <div className="form2-signin">
              <input
                type="password"
                className="input-password-login"
                placeholder="Password"
                onChange={(e) => {
                  setPassword(e.target.value);
                }}
                required
              />
              <p className="p-loginstatus" style={{ color: "white" }}>
                {loginStatus}
              </p>
              <a href="/registration" className="bn13">
                สมัครสมาชิก
              </a>
            </div>

            <div className="div-button-login-n">
              <button className="button-login-n" type="submit">
                Sign in
              </button>
            </div>
          </form>
        </div>
      </motion.div>
    </div>
  );
};
export default Login;
