import axios from "axios";
import React from "react";
import { useState, useEffect } from "react";
import { useHistory } from "react-router";

import "./Changepwd.css";

function Changepwd(emailreq) {
  const history = useHistory();
  const [email, setemail] = useState("");
  const [Newpwd, setNewpwd] = useState("");
  const [ConfirmNewpwd, setConfirmNewpwd] = useState("");
  const [status, setStatus] = useState("");

  const checkPassword = (e) => {
    e.preventDefault();
    if (Newpwd === ConfirmNewpwd) {
      changepassword();
    } else if (!Newpwd) {
      alert("กรุณาป้อนรหัสผ่าน");
    } else {
      alert("รหัสผ่านไม่ตรงกัน");
    }
  };
  const changepassword = () => {
    axios.put("/changepassword", { email: email, password: ConfirmNewpwd });
  };

  useEffect(() => {
    setemail(emailreq.match.params.email);
  }, []);
  return (
    <div>
      <form onSubmit={checkPassword}>
        <h1 className="changepwd-email">Email : {email}</h1>
        <label className="changepwd-new">รหัสผ่านใหม่</label>
        <input
          type="password"
          className="input-changepwd-new"
          placeholder="รหัสผ่านใหม่"
          onChange={(e) => {
            setNewpwd(e.target.value);
          }}
        />
        <label className="changepwd-new">ยืนยันรหัสผ่านใหม่</label>
        <input
          type="password"
          className="input-changepwd-new"
          placeholder="ยืนยันรหัสผ่านใหม่"
          onChange={(e) => {
            setConfirmNewpwd(e.target.value);
          }}
        />
        <button className="btn btn-success changepwd-btn">ยืนยัน</button>
      </form>
    </div>
  );
}

export default Changepwd;
