import React from "react";
import { Link } from "react-router-dom";
import { Buttonshop } from "./Buttonshop";
import { useEffect } from "react";
import axios from "axios";
import { useState } from "react";
import moment from "moment";
import "./Transactionshop.css";

function Transactionshop() {
  //------------------------------------------------
  axios.defaults.withCredentials = true;

  const [history, setHistory] = useState([]);
  const [phone, setPhone] = useState("");

  const logout = () => {
    axios.get("/logout");
    window.location.replace("/");
  };

  useEffect(() => {
    axios.get("/login").then((response) => {
      if (response.data.loggedIn == true) {
        setPhone(response.data.user[0].phone);
        axios.get("/transaction").then((response) => {
          if (response) {
            setHistory(response.data);
          }
        });
      }
      if (
        response.data.loggedIn == false ||
        response.data.user[0].role != "shop"
      ) {
        return window.location.replace("/");
      }
    });
  }, []);

  return (
    <div>
      <nav class="navbar navbar-light bg-light justify-content-between">
        <Link to="/">
          <i className="fas fa-angle-left">
            <a className="bl">ย้อนกลับ</a>
          </i>
        </Link>
      </nav>

      <div className="history">
        <table className="table table-dark">
          <thead></thead>
          {history.map((val) => {
            return (
              <tbody>
                <tr>
                  {val.recipient == phone ? (
                    <td key={val.id}>
                      <p className="deposit">ฝาก</p>
                      <p className="deposit-detail">
                        {val.transferor} โอนไปยัง {val.recipient}{" "}
                      </p>
                    </td>
                  ) : (
                    <td key={val.id}>
                      <p className="withdraw">ถอน</p>
                      <p className="withdraw-detail">
                        {val.transferor} โอนไปยัง {val.recipient}{" "}
                      </p>
                    </td>
                  )}

                  {val.transferor == phone ? (
                    <td>
                      <p className="amountminus">- {val.amount}</p>
                      <p className="time">
                        {moment(val.time).format("DD/MMMM - LT")}
                      </p>
                    </td>
                  ) : (
                    <td>
                      <p className="amountplus">+ {val.amount}</p>
                      <p className="time">
                        {moment(val.time).format("DD/MMMM - LT")}
                      </p>
                    </td>
                  )}
                </tr>
              </tbody>
            );
          })}
        </table>
      </div>
    </div>
  );
}
export default Transactionshop;
