import React from "react";
import { useEffect } from 'react';
import axios from 'axios';
import { useState } from 'react';
import './login_and_register.css';
import { Redirect } from "react-router";
import { Link } from "react-router-dom";
import './Login.css';

export default function Login() {

  const [click, setClick] = useState(false);
    

  const handleClick = () => setClick(!click);
  const closeMobileMenu = () => setClick(false);

   


  //----------------------------------------------------------

  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const [loginStatus, setLoginStatus] = useState("");
  const [isLoggedIn, setIsLoggedIn] = useState("flase");

  axios.defaults.withCredentials = true;
  //login
  const login = () => {
    axios
      .post("/login", {
        email: email,
        password: password,
      })
      .then((response) => {
        if (response.data.message) {
          setLoginStatus(response.data.message);
          console.log(response);
        } else {
          setLoginStatus(response.data[0].name);
          window.location.replace('/');
        }
      });
  };
  useEffect(() => {
    axios.get("/login").then((response) => {
      if (response.data.loggedIn == true) {
        setLoginStatus(response.data.user[0].username);
        setIsLoggedIn("true");
      }
    });
  }, []);

  if (isLoggedIn == "true") {
    return (
      <Redirect to="/" />
    );
  }
  return (
    <>
            <nav className="navbar">
                <div className="navbar-container">
                    <Link to="/" className="navbar-logo" onClick={closeMobileMenu}>
                        TNC
                    </Link>
                    <div className='menu-icon' onClick={handleClick}>
                        <i className={click ? 'fas fa-times' : 'fas fa-bars'} />
                    </div>
                    <ul className={click ? 'nav-menu active' : 'nav-menu'}>
                        <li className='nav-item'>
                            <Link to='/registration' className='nav-links-mobile' onClick={closeMobileMenu}>สมัครสมาชิก</Link>
                        </li>
                        <li className='nav-item'>
                            <Link to='/login' className='nav-links-mobile' onClick={closeMobileMenu}>เข้าสู่ระบบ</Link>
                        </li>
                        
                    </ul>
                    <ul className='nav-menu'>
                        <li className='nav-item'>
                            <Link to='/registration' className='nav-links' onClick={closeMobileMenu}>สมัครสมาชิก</Link>
                        </li>
                        <li className='nav-item'>
                            <Link to='/login' className='nav-links' onClick={closeMobileMenu}>เข้าสู่ระบบ</Link>
                        </li>
                        
                    </ul>
                </div>

            </nav>

    <div className="App">
      <div className="base-container">
        <div className="header">Login</div>
        <div className="content">
          <div className="image">
            <img className="login-img" src="/images/login-img.jpg" />
          </div>
          <div className="form">
            <div className="form-group">
              <label>Email</label>
              <input
                type="text"
                placeholder="Email"
                onChange={(e) => {
                  setEmail(e.target.value);
                }}
              />
            </div>
            <div className="form-group">
            <label>Password</label>
              <input
                type="password"
                placeholder="Password"
                onChange={(e) => {
                  setPassword(e.target.value);
                }}
              />
            </div>
            <div className="notification">
              <p>{loginStatus}</p>
            </div>

          </div>
        </div>
        <div className="footer">
            <Link to='/registration'>register</Link><br/><br/>
          <button type="button" className="btn" onClick={login} >
            Login
          </button>

        </div>
      </div>
    </div>
    </>
  );
}
