import React, { useState } from "react";
import { Link, useHistory } from "react-router-dom";
import { useEffect } from "react";
import axios from "axios";
import "./Mypromotionshop.css";
import { Collapse } from "react-bootstrap";
import { Loader } from "semantic-ui-react";
import { motion } from "framer-motion";
import { Button, Header, Icon, Modal } from "semantic-ui-react";

function Mypromotionshop() {
  //-----------------------------------------------------
  const history = useHistory();
  const [openModal, setOpenModal] = React.useState(false);
  const [open, setOpen] = useState(false); //collapse
  const [active, setactive] = useState(true); //loader
  //-----------------------------------------------------

  const [id, setId] = useState("");
  const [name, setName] = useState("");
  const [phone, setPhone] = useState("");
  const [email, setEmail] = useState("");
  const [point, setPoint] = useState();

  const [productID, setProductID] = useState();

  const option1 = "option1";
  const option2 = "option2";

  const [mypromotionpoint, setMypromotionPoint] = useState([]);
  const [mypromotion, setMypromotion] = useState([]);

  const goAddpromotion = (option) => {
    history.push("/addpromotion/" + option);
  };

  const deletepromotion = () => {
    axios.delete("/deletepromotion" + productID).then((response) => {
      if (response.data.message) {
        alert(response.data.message);
        history.go();
      }
    });
  };

  /* ********************************** */

  const pageTransition = {
    in: {
      opacity: 1,
      x: 0,
    },
    out: {
      opacity: 0,
      x: 100,
    },
  };

  /* ********************************** */

  useEffect(() => {
    const timer = setTimeout(() => {
      setactive(false);
      axios.get("/login").then((response) => {
        if (response.data.loggedIn === true) {
          setId(response.data.user[0].id);
          setName(response.data.user[0].name);
          setPhone(response.data.user[0].phone);
          setEmail(response.data.user[0].email);
          setPoint(response.data.user[0].point);
          axios.get("/getmypromotionpoint").then((response) => {
            if (response) {
              setMypromotionPoint(response.data);
            }
          });
          axios.get("/getmypromotion").then((response) => {
            if (response) {
              setMypromotion(response.data);
            }
          });
        }
        if (
          response.data.loggedIn === false ||
          response.data.user[0].role !== "shop"
        ) {
          return history.push("/login");
        }
      });
    }, 1000);
    return () => clearTimeout(timer);
  }, []);

  return (
    <div>
      <Modal
        basic
        onClose={() => setOpenModal(false)}
        onOpen={() => setOpenModal(true)}
        open={openModal}
        size="small"
        style={{ width: "100%", top: "40%" }}
      >
        <Header icon>
          <motion.div
            initial={{ opacity: 0, y: 100 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: 100 }}
          >
            <div className="buttoniconedit">
              <Button
                className="buttoniconedit"
                color="red"
                inverted
                onClick={() => deletepromotion()}
              >
                <div className="iconedit">
                  <Icon name="trash" />
                </div>
                <p>ลบ</p>
              </Button>
            </div>
            <div className="buttoniconedit">
              <Button
                className="buttoniconedit"
                color="red"
                inverted
                onClick={() => setOpenModal(false)}
              >
                <div className="iconedit">
                  <Icon name="cancel" />
                </div>
                <p>ยกเลิก</p>
              </Button>
            </div>
          </motion.div>
        </Header>
      </Modal>

      <link
        href="https://maxcdn.bootstrapcdn.com/font-awesome/4.7.0/css/font-awesome.min.css"
        rel="stylesheet"
      />

      <nav className="navbar navbar-light bg-light justify-content-between">
        <Link className="buttonblack" to="/promotion-shop">
          <i className="fas fa-angle-left">
            <a className="bl">ย้อนกลับ</a>
          </i>
        </Link>
        <div className="div-img-logo2">
          <img className="img-logoo" src="/images/logo.png" />
        </div>
      </nav>

      <motion.div
        initial="out"
        animate="in"
        exit="out"
        variants={pageTransition}
      >
        <div className="top-promotion">
          <button
            className="button-top-addpromotion1"
            onClick={() => setOpen(!open)}
            aria-controls="example-collapse-text"
            aria-expanded={open}
          >
            <i class="fas fa-plus"></i>
            เพิ่มโปรโมชั่น
          </button>
          <Collapse in={open}>
            <div id="example-collapse-text" className="top-promotion-1">
              <button
                className="button-top-addpromotion"
                onClick={() => {
                  goAddpromotion(option1);
                }}
              >
                <i class="fas fa-plus"></i>
                เพิ่มโปรโมชั่นคะแนน
              </button>
              <button
                className="button-top-addpromotion"
                onClick={() => {
                  goAddpromotion(option2);
                }}
              >
                <i class="fas fa-plus"></i>
                เพิ่มโปรโมชั่นแลกสินค้า
              </button>
            </div>
          </Collapse>
          {/*<div className="toppic-promotin">
          <h2 className="h2promotionmember">โปรโมชั่นร้านค้า</h2>
            </div>*/}
          <div className="divinsearch-1">
            <input
              className="insearch-divinsearch"
              type="text"
              placeholder="ค้นหาร้านค้า"
            />
            <i className="fas fa-search"></i>
          </div>

          {/*แต่งไว้หน้า promotiondetails*/}
          <div className="divh1promotiondetails1">
            <h1 className="h1promotiondetails1">โปรโมชั่นคะแนน</h1>
          </div>

          <div className="container-promotion">
            <Loader active={active} />
            {mypromotionpoint.map((promopoint) => {
              return (
                <div className="shopbox-promotin">
                  {/*shopbox-img แต่งไว้ที่ promotionshop*/}
                  <div className="shopbox-img">
                    <img
                      className="img-fluid-shopbox"
                      src={promopoint.picture}
                      alt="trainer-img"
                      style={{ height: "100px" }}
                    />
                  </div>
                  <div className="boxpromotion" onClick="">
                    <p>{promopoint.product_name}</p>
                    <p>ได้รับ : {promopoint.amount_point} คะแนน</p>
                  </div>
                </div>
              );
            })}
          </div>

          {/*แต่งไว้หน้า promotiondetails*/}
          <div className="divh1promotiondetails2">
            <h1 className="h1promotiondetails2">โปรโมชั่นแลกสินค้า</h1>
          </div>

          <div className="container-promotion">
            <Loader active={active} />
            {mypromotion.map((val) => {
              return (
                <div
                  onClick={() => {
                    setProductID(val.id);
                    setOpenModal(true);
                  }}
                  className="shopbox-promotin"
                >
                  {/*shopbox-img แต่งไว้ที่ promotionshop*/}
                  <div className="shopbox-img">
                    <img
                      className="img-fluid-shopbox"
                      style={{ height: "100px" }}
                      src={val.picture}
                      alt="trainer-img"
                    />
                  </div>
                  <div className="boxpromotion">
                    <p className="p-promotindetail">{val.product_name}</p>
                    <p className="p-promotindetail-1">{val.amount} คะแนน</p>
                  </div>
                </div>
              );
            })}
          </div>
        </div>
      </motion.div>
    </div>
  );
}
export default Mypromotionshop;
