import React from "react";
import { useEffect } from "react";
import axios from "axios";
import { useState } from "react";
import "./login_and_register.css";
import { Redirect } from "react-router";
import { Link } from "react-router-dom";
import { useHistory } from "react-router";
import { Loader } from "semantic-ui-react";
import "./Login.css";
import { motion } from "framer-motion";

const Login = (props) => {
  const [active, setactive] = useState(false); //loader
  //----------------------------------------------------------
  const history = useHistory();
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const [loginStatus, setLoginSatus] = useState("");
  const [isLoggedIn, setIsLoggedIn] = useState("flase");

  axios.defaults.withCredentials = true;

  //login
  const login = (e) => {
    e.preventDefault();
    if (!email || !password) {
    } else {
      setactive(true);
      const timer = setTimeout(() => {
        setactive(false);
        axios
          .post("/login", {
            email: email,
            password: password,
          })
          .then((response) => {
            if (response.data.message) {
              setLoginSatus(response.data.message);
            } else {
              window.location.replace("/");
            }
          });
      }, 2000);
      return () => clearTimeout(timer);
    }
  };

  /* ********************************** */

  const pageTransition = {
    in: {
      opacity: 1,
      x: 0,
    },
    out: {
      opacity: 0,
      x: 100,
    },
  };

  /* ********************************** */

  useEffect(() => {
    axios.get("/login").then((response) => {
      if (response.data.loggedIn === true) {
        setIsLoggedIn("true");
      }
    });
  }, []);

  if (isLoggedIn === "true") {
    return <Redirect to="/" />;
  }
  return (
    <div className="login-page">
      <Loader active={active} />
      <nav className="navbar navbar-light bg-light justify-content-between">
        <Link className="buttonblack" to="/home">
          <i className="fas fa-angle-left">
            <a className="bl">ย้อนกลับ</a>
          </i>
        </Link>
      </nav>

      <motion.div
        initial="out"
        animate="in"
        exit="out"
        variants={pageTransition}
      >
        {/*<div className="login-page-con">
          <form
            onSubmit={(e) => {
              login(e);
            }}
            className="signin-container"
          >
            <div className="form-signin">
              <img
                className="im_user1"
                src="images/user1.png"
              ></img>
            </div>
            <div className="form-signin1">
              <h3 className="title-login">เข้าสู่ระบบ</h3>
            </div>
            <label className="form-signin-email">Email</label>

            <div className="iemail">
              <i className="fas fa-envelope"></i></div>
            <div className="form2-signin">
              <input
                type="email"
                className="input-email-login"
                placeholder="Email"
                onChange={(e) => {
                  setEmail(e.target.value);
                }}
                required
              />
            </div>

            <label className="form-signin-password">Password</label>
            <div className="iemail">
              <i className="fas fa-unlock-alt"></i></div>
            <div className="form2-signin">
              <input
                type="password"
                className="input-password-login"
                placeholder="Password"
                onChange={(e) => {
                  setPassword(e.target.value);
                }}
                required
              />
              <p className="p-loginstatus" style={{ color: "white" }}>
                {loginStatus}
              </p>
              <a href="/forgotpwd" className="bn13">
                ลืมรหัสผ่าน
              </a>
            </div>

            <div className="div-button-login-n">
              <button className="button-login-n" type="submit">
                Sign in
              </button>
              <a href="/registration" className="bn14">
                สมัครสมาชิก
              </a>
            </div>
          </form>
              </div>*/}


        <div className="container px-4 py-5 mx-auto">
          <form
            onSubmit={(e) => {
              login(e);
            }}

          >
            <div className="card card0">
              <div className="d-flex flex-lg-row flex-column-reverse">
                <div className="card card1d">
                  <div className="row justify-content-center my-auto">
                    <div className="col-md-8 col-10 my-5">
                      <div className="row justify-content-center px-3 mb-3"> <img id="logo" src="/images/logo.png"></img> </div>
                      <h3 className="mb-5 text-center heading">เข้าสู่ระบบบ TNC</h3>

                      <div className="form-group"> <label className="form-control-label text-muted">Email</label> <input
                        type="email"
                        className="form-control"
                        placeholder="Email"
                        onChange={(e) => {
                          setEmail(e.target.value);
                        }}
                        required
                      /> </div>
                      <div className="form-group"> <label className="form-control-label text-muted">Password</label> <input
                        type="password"
                        className="form-control"
                        placeholder="Password"
                        onChange={(e) => {
                          setPassword(e.target.value);
                        }}
                        required
                      /> </div>
                      <div className="row justify-content-center my-3 px-3"> <button className="btn-block btn-color" type="submit">Login </button></div>
                      <div className="bottom text-center mb-5"> 
                      <p className="p-loginstatus" style={{ color: "red" }}>
                        {loginStatus}
                      </p> 
                      <a href="/forgotpwd"><small className="text-muted">ลืมรหัสผ่าน</small></a> </div>
                      <div className="div-button-login-n">
                        <a href="/registration" className="bn14">สมัครสมาชิก</a>
                      </div>
                    </div>
                  </div>
                </div>
                <div className="card card2d">
                  <div className="my-auto mx-md-5 px-md-5 right">
                    <h3 className="text-white-hh3">ยินดีต้อนรับเข้าสู่ระบบสะสมคะแนน TNC</h3>
                    <small className="text-white">โอนสะดวก ง่าย รวดเร็ว และปลอดภัย</small>
                  </div>
                </div>
              </div>
            </div>
          </form>
        </div>



      </motion.div>
    </div>
  );
};
export default Login;
