import React, { useState } from "react";
import { Link, useHistory } from "react-router-dom";
import axios from "axios";
import { useEffect } from "react";
import Modal from "react-modal";
import "./Shopprofile.css";
import { motion } from "framer-motion";

const customStyles = {
  content: {
    width: "100%",
    top: "50%",
    left: "50%",
    right: "auto",
    bottom: "auto",
    marginRight: "-50%",
    transform: "translate(-50%, -50%)",
  },
};

function Shopprofile() {
  let subtitle;
  const [modalIsOpenid, setIsOpenid] = React.useState(false);
  const [modalIsOpenname, setIsOpenname] = React.useState(false);
  const [modalIsOpenphone, setIsOpenphone] = React.useState(false);
  const [modalIsOpenemail, setIsOpenemail] = React.useState(false);
  const [modalIsOpenPassword, setIsOpenPassword] = React.useState(false);

  function openModalid() {
    setIsOpenid(true);
  }

  function openModalname() {
    setIsOpenname(true);
  }

  function openModalphone() {
    setIsOpenphone(true);
  }

  function openModalemail() {
    setIsOpenemail(true);
  }

  function openModalpassword() {
    setIsOpenPassword(true);
  }

  function afterOpenModal() {
    // references are now sync'd and can be accessed.
    subtitle.style.color = "#000";
  }

  function closeModal() {
    setIsOpenid(false);
    setIsOpenname(false);
    setIsOpenphone(false);
    setIsOpenemail(false);
    setIsOpenPassword(false);
  }
  //----------------------------------------------
  const history = useHistory();
  const [id, setId] = useState();
  const [national_id, setNationalId] = useState("");
  const [name, setName] = useState("");
  const [phone, setPhone] = useState("");
  const [email, setEmail] = useState("");
  const [shoptype, setShoptype] = useState("");
  const [point, setPoint] = useState();
  const [shoppoint, setShopPoint] = useState();
  const [role, setRole] = useState("");
  const [statusID, setStatusID] = useState("");
  const [statusName, setStatusName] = useState("");
  const [statusPhone, setStatusPhone] = useState("");
  const [statusEmail, setStatusEmail] = useState("");
  const [statusPassword, setStatusPassword] = useState("");

  const [neweNational_id, setnewNationalId] = useState("");
  const [newname, setnewName] = useState("");
  const [newphone, setnewPhone] = useState("");
  const [newemail, setnewEmail] = useState("");
  const [oldPassword, setoldPassword] = useState("");
  const [newPassword, setnewPassword] = useState("");
  const [newConfirmPassword, setnewConfirmPassword] = useState("");

  axios.defaults.withCredentials = true;

  const idUpdate = () => {
    if (!neweNational_id) {
      setStatusID("เลขบัตรประจำตัวประชาชนไม่ถูกต้อง");
    } else {
      axios
        .put("/updateshopId", { national_id: neweNational_id, id: id })
        .then((response) => {
          if (response.data.message) {
            setStatusID(response.data.message);
          } else {
            alert("บันทึกสำเร็จ");
            setNationalId(response.data.user[0].national_id);
            history.go();
          }
        });
    }
  };

  const nameUpdate = () => {
    if (!newname) {
      setStatusName("ชื่อไม่ถูกต้อง");
    } else {
      axios
        .put("/updateshopName", { name: newname, id: id })
        .then((response) => {
          if (response.data.message) {
            setStatusName(response.data.message);
          } else {
            alert("บันทึกสำเร็จ");
            setName(response.data.user[0].name);
            history.go();
          }
        });
    }
  };
  const phoneUpdate = () => {
    if (!newphone) {
      setStatusPhone("เบอร์โทรไม่ถูกต้อง");
    } else {
      axios
        .put("/updateshopPhone", { phone: newphone, id: id })
        .then((response) => {
          if (response.data.message) {
            setStatusPhone(response.data.message);
          } else {
            alert("บันทึกสำเร็จ");
            setPhone(response.data.user[0].phone);
            history.go();
          }
        });
    }
  };

  const emailUpdate = () => {
    axios
      .put("/updateshopEmail", { email: newemail, id: id })
      .then((response) => {
        if (response.data.message) {
          setStatusEmail(response.data.message);
        } else {
          alert("บันทึกสำเร็จ");
          setEmail(response.data.user[0].email);
          history.go();
        }
      });
  };

  const passwordUpdate = (e) => {
    e.preventDefault();
    if (!oldPassword || !newPassword || !newConfirmPassword) {
      setStatusPassword("กรุณาป้อนรหัสผ่าน");
    } else if (newPassword !== newConfirmPassword) {
      setStatusPassword("รหัสผ่านใหม่ไม่ตรงกัน");
    } else {
      setStatusPassword("");
      axios
        .put("/updateshopPassword", {
          email: email,
          oldPassword: oldPassword,
          password: newPassword,
        })
        .then((response) => {
          if (response.data.message) {
            setStatusPassword(response.data.message);
          } else if (response.data.success) {
            alert(response.data.success);
            history.go();
          }
        });
    }
  };

  /* ********************************** */

  const pageTransition = {
    in: {
      opacity: 1,
      x: 0,
    },
    out: {
      opacity: 0,
      x: 100,
    },
  };

  /* ********************************** */

  useEffect(() => {
    axios.get("/login").then((response) => {
      if (response.data.loggedIn === true) {
        setId(response.data.user[0].id);
        setNationalId(response.data.user[0].national_id);
        setName(response.data.user[0].name);
        setPhone(response.data.user[0].phone);
        setEmail(response.data.user[0].email);
        setPoint(response.data.user[0].point);
        setShoptype(response.data.user[0].type);
        setShopPoint(response.data.user[0].shoppoint);
        setRole(response.data.user[0].role);
      }
      if (
        response.data.loggedIn === false ||
        response.data.user[0].role !== "shop"
      ) {
        return history.push("/login");
      }
    });
  }, []);

