import React from "react";
import { useEffect } from 'react';
import axios from 'axios';
import { useState } from 'react';
import './login_and_register.css';
import { Redirect } from "react-router";
import { Link } from "react-router-dom";
import { useHistory } from 'react-router';
import './Login.css';


export default function Login() {



  //----------------------------------------------------------
  const history = useHistory();
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const [loginStatus, setLoginSatus] = useState("");
  const [isLoggedIn, setIsLoggedIn] = useState("flase");

  axios.defaults.withCredentials = true;

  //login
  const login = () => {
    axios.post("/login", {
      email: email,
      password: password,
    })
      .then((response) => {
        if (response.data.message) {
          setLoginSatus(response.data.message);
        } else {
          window.location.replace('/');
        }
      });
  };

  useEffect(() => {
    axios.get("/login").then((response) => {
      if (response.data.loggedIn == true) {
        setIsLoggedIn("true");
      }
    });
  }, []);

  if (isLoggedIn == "true") {
    return (
      <Redirect to="/" />
    );
  }
  return (
    <div >


      <nav className="navbar navbar-light bg-light justify-content-between">
        <Link to="/"  ><i className="fas fa-angle-left"><a className="bl">ย้อนกลับ</a></i></Link>
      </nav>
      <div className="login-page">
      <div className="container bootstrap snippets bootdey">
        <div className="row">
          <div className="col-sm-6 col-md-4 col-md-offset-4">
            <h1 className="text-center login-title">เข้าสู่ระบบ</h1>
            <div className="account-wall">
              <img className="profile-img" src="https://bootdey.com/img/Content/avatar/avatar7.png"
                alt="" />
              <form className="form-signin">
                <input type="text" className="form-control" placeholder="Email" onChange={(e) => {
                  setEmail(e.target.value);
                }}
                  required />
                <input type="password" className="form-control" placeholder="Password" onChange={(e) => {
                  setPassword(e.target.value);
                }}
                  required />
                <a href="/registration" className="bn13">สมัครสมาชิก</a>
                <button onClick={login} className="bn29" type="submit">Sign in</button>


              </form>
            </div>

          </div>
        </div>
      </div>
</div>



      {/*<label for="user" className="label">Email</label>
          <input className="input" type="text"
            placeholder="Email"
            onChange={(e) => {
              setEmail(e.target.value);
            }}
            required />

          <label for="pass" className="label">Password</label>
          <input className="input" type="password"
            placeholder="Password"
            onChange={(e) => {
              setPassword(e.target.value);
            }}
            required />

          <div className="notification">
            <p className="notify">{loginStatus}</p>
          </div>

          <p>ยังไม่ได้เป็นสมาชิค?</p>
          <Link to='/registration'>สมัครสมาชิก</Link><br /><br />
          <input onClick={login} type="submit" className="button" value="LOGIN" />*/}

    </div>
  );
}
