import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { Buttonshop } from './Buttonshop';
import { useEffect } from 'react';
import axios from 'axios';
import './Calculatepointshop.css';


function Calculatepointshop() {
    const [click, setClick] = useState(false);
    const [button, setButton] = useState(true);


    const handleClick = () => setClick(!click);
    const closeMobileMenu = () => setClick(false);

    const showButton = () => {
        if (window.innerWidth <= 960) {
            setButton(false)
        } else {
            setButton(true)
        }
    };

    window.addEventListener('resize', showButton);
    //-----------------------------------------------------

    const [id, setId] = useState();
    const [shoppoint, setShopPoint] = useState();
    const [phone, setPhone] = useState("");
    const [destPhone, setdestPhone] = useState("");
    const [amount, setamount] = useState();
    const [transferStatus, setTransferStatus] = useState("");


    const calPoint = () => {

        if (destPhone == "") {
            setTransferStatus("เบอร์ปลายทางไม่ถูกต้อง");
        } else if (phone == destPhone) {
            setTransferStatus("เบอร์ปลายทางไม่ถูกต้อง");
        } else if (amount > shoppoint) {
            setTransferStatus("คะแนนไม่เพียงพอสำหรับการโอน กรุณาเติมคะแนน");
        } else if (amount < 1) {
            setTransferStatus("ยอดซื้อขั้นต่ำ 50-.");
        } else {
            axios.put("/calPointTransfer", {
                id: id,
                phone: phone,
                destphone: destPhone,
                amount: amount
            })
                .then((response) => {
                    setTransferStatus(response.data.message);
                    if (response.data.loggedIn == true) {
                        setShopPoint(response.data.user[0].shoppoint);
                        window.location.reload();
                    }
                });
        }

    }
    const logout = () => {
        axios.get("/logout");
        window.location.replace('/');
    }

    useEffect(() => {
        axios.get("/login").then((response) => {
            if (response.data.loggedIn == true) {
                setId(response.data.user[0].id);
                setShopPoint(response.data.user[0].shoppoint);
                setPhone(response.data.user[0].phone);
            }
            if (response.data.loggedIn == false || response.data.user[0].role != 'member') {
                return (window.location.replace('/'));
            }
        });
    }, []);

    return (
        <>
            <nav className="navbar">
                <div className="navbar-container">
                    <Link to="/" className="navbar-logo">
                        TNC
                    </Link>
                    <div className='menu-icon' onClick={handleClick}>
                        <i className={click ? 'fas fa-times' : 'fas fa-angle-down'} />

                    </div>


                    <ul className={click ? 'nav-menu active' : 'nav-menu'}>

                        <li className='nav-item'>
                            <Link className='nav-links' onClick={closeMobileMenu}>

                            </Link>
                        </li>

                        {button && <Buttonshop buttonStyle='btn--outline'>MY PROFILE</Buttonshop>}
                        <a onClick={logout} className="navbar-logo">
                            <i class='fas fa-sign-out-alt'></i>
                        </a>
                    </ul>


                </div>

            </nav>

            <div className='calculate'>

                <h1>คำนวณคะแนน</h1>
                <h1>shoppoint : {shoppoint}</h1>
                <label>กรอกเบอร์</label>
                <input
                    type="text"
                    onChange={(e) => {
                        setdestPhone(e.target.value);
                    }} />

                <label>กรอกราคาสินค้า</label>
                <input
                    type="text"
                    onChange={(e) => {
                        setamount(Math.floor(e.target.value / 50));
                    }} />
                {transferStatus}
                <button onClick={calPoint} >ยืนยัน</button>

            </div>

        </>

    );
}
export default Calculatepointshop;
