import React, { useEffect } from 'react'
import axios from 'axios'
import { useState } from 'react'
import { useHistory } from 'react-router-dom'
import './Admin.css'
import 'semantic-ui-css/semantic.min.css'
import { Icon } from 'semantic-ui-react'
import { Loader } from 'semantic-ui-react'
import moment from 'moment'
import { AnimatePresence, motion } from 'framer-motion'

import SideNav, {
  Toggle,
  Nav,
  NavItem,
  NavIcon,
  NavText,
} from '@trendmicro/react-sidenav'

// Be sure to include styles at some point, probably during your bootstraping
import '@trendmicro/react-sidenav/dist/react-sidenav.css'

export default function Admin() {
  const history = useHistory()
  const location = useHistory()
  const [active, setactive] = useState(true) //loader
  //-------------------------------------------
  const [name, setname] = useState('')
  const [point, setpoint] = useState()
  const [pendingpromotion, setpendingpromotion] = useState([])
  const [Txdata, setTxdata] = useState([])
  const [checkTx, setcheckTx] = useState([])
  const [checkTxid, setcheckTxid] = useState([])

  const [keys, setkeys] = useState('')
  axios.defaults.withCredentials = true
  //-------------------------------------------
  const logout = () => {
    axios.get('/logout')
    history.go()
    //history.push("/");
  }

  const acceptpromotion = (a, b, c, d, e, f, g, h) => {
    axios
      .post('/acceptpromotionpoint', {
        req_id: a,
        shop_name: b,
        product_name: c,
        product_code: d,
        amount_point: e,
        picture: f,
        category: g,
        expired: h,
      })
      .then((response) => {
        if (response.data.message) {
          alert(response.data.message)
          history.go()
        }
      })
  }

  const disacceptpromotion = (id) => {
    axios.put(`/disacceptpromotion/${id}`).then((response) => {
      if (response.data.message) {
        alert(response.data.message)
        history.go()
      }
    })
  }

  const checktransaction = () => {
    setTxdata([])
    setcheckTx([])
    axios.get('/checktransaction/' + keys).then((response) => {
      if (response.data) {
        setTxdata(response.data)
      } else if (response.data.message) {
        alert(response.data.message)
      }
    })
  }

  const checkidtransaction = (id) => {
    axios.get('/checkidtransaction/' + id).then((response) => {
      if (response) {
        setcheckTxid(response.data.data)
        console.log(response.data.data)
      }
    })
  }
  /* ********************************** */

  const pageTransition = {
    in: {
      opacity: 1,
      x: 0,
    },
    out: {
      opacity: 0,
      x: '-100vh',
    },
  }

  /* ********************************** */

  useEffect(() => {
    const timer = setTimeout(() => {
      setactive(false)
      axios.get('/login').then((response) => {
        if (
          response.data.loggedIn === true &&
          response.data.user[0].role === 'admin'
        ) {
          setname(response.data.user[0].name)
          setpoint(response.data.user[0].point)
          axios.get('/getpromotionrequest').then((response) => {
            if (response.data) {
              setpendingpromotion(response.data)
            }
          })
        } else {
          history.push('/login')
        }
      })
    }, 2000)
    return () => clearTimeout(timer)
  }, [])

  return (
    <div className="adminpage">
      <SideNav
        style={{ background: 'black' }}
        onSelect={(selected) => {
          // Add your code here
          const to = '/' + selected
          if (location.pathname !== to) {
            history.push(to)
          }
        }}
      >
        <SideNav.Toggle />
        <SideNav.Nav defaultSelected="home">
          <NavItem eventKey="">
            <NavIcon>
              <i className="fa fa-fw fa-home" style={{ fontSize: '1.75em' }} />
            </NavIcon>
            <NavText>Home</NavText>
          </NavItem>
          <NavItem onClick={logout}>
            <NavIcon>
              <Icon name="log out" style={{ fontSize: '1.75em' }} />
            </NavIcon>
            <NavText>Logout</NavText>
          </NavItem>
        </SideNav.Nav>
      </SideNav>

      <motion.div
        initial="out"
        animate="in"
        exit="out"
        variants={pageTransition}
      >
        <Loader active={active} />
        <div className="data-container">
          <h1 className="h1-admin">Admin</h1>
          <h2 className="h1-admin">Hi {name}</h2>
          <h2 className="h1-admin">point : {point}</h2>

          <div class="table-wrapper-scroll-y my-custom-scrollbar">
            <table class="table table-bordered table-striped mb-0">
              <thead>
                <tr>
                  <th className="th-table" scope="col">
                    request id
                  </th>
                  <th className="th-table" scope="col">
                    shop name
                  </th>
                  <th className="th-table" scope="col">
                    product name
                  </th>
                  <th className="th-table" scope="col">
                    product code
                  </th>
                  <th className="th-table" scope="col">
                    point
                  </th>
                  <th className="th-table" scope="col">
                    category
                  </th>
                  <th className="th-table" scope="col">
                    expired
                  </th>
                  <th className="th-table" scope="col">
                    ตัวเลือก
                  </th>
                </tr>
              </thead>

              {pendingpromotion.map((show) => {
                return (
                  <tbody>
                    <tr>
                      <td className="td-table">
                        <p className="h1-admin">{show.id}</p>
                      </td>
                      <td className="td-table">
                        <p className="h1-admin">{show.shop_name}</p>
                      </td>
                      <td className="td-table">
                        <p className="h1-admin">{show.product_name}</p>
                      </td>
                      <td className="td-table">
                        <p className="h1-admin">{show.product_code}</p>
                      </td>
                      <td className="td-table">
                        <p className="h1-admin">{show.amount_point}</p>
                      </td>
                      <td className="td-table">
                        <p className="h1-admin">{show.category}</p>
                      </td>
                      <td className="td-table">
                        <p className="h1-admin">
                          {moment(show.expired).format('DD/MM/YYYY')}
                        </p>
                      </td>
                      <td className="td-table">
                        <button
                          className="buttoncalculate-modal1"
                          onClick={() => {
                            acceptpromotion(
                              show.id,
                              show.shop_name,
                              show.product_name,
                              show.product_code,
                              show.amount_point,
                              show.picture,
                              show.category,
                              show.expired,
                            )
                          }}
                        >
                          อนุมัติ
                        </button>
                        <button
                          className="buttoncalculate-modal2"
                          onClick={() => {
                            disacceptpromotion(show.id)
                          }}
                        >
                          ไม่อนุมัติ
                        </button>
                      </td>
                    </tr>
                  </tbody>
                )
              })}
            </table>
          </div>
          <input
            placeholder="Tx-id หรือ เบอร์โทรศัพท์"
            onChange={(e) => {
              setkeys(e.target.value)
            }}
          ></input>
          <button onClick={checktransaction}>ค้นหา</button>

          {/* {Txdata.map((val) => {
            return (
              <div>
                <p
                  onClick={() => {
                    checkidtransaction(val.Tx_id)
                  }}
                >
                  Tx_id :{val.Tx_id}
                </p>
                <p>เวลา :{val.time}</p>
                <p>คนโอน :{val.transferor}</p>
                <p>คนรับ :{val.recipient}</p>
                <p>จำนวน :{val.amount}</p>
              </div>
            )
          })} */}

          <p>BY ID</p>
          <p>คนโอน{checkTx[0]}</p>
          <p>คนรับ{checkTx[1]}</p>
          <p>จำนวน{parseInt(checkTx[2], 16)}</p>

          {/***************************************************************** */}

          <div class="table-wrapper-scroll-y my-custom-scrollbar">
            <h1>ตรวจสอบ transaction</h1>
            <table class="table table-bordered table-striped mb-0">
              <thead>
                <tr>
                  <th className="th-table" scope="col">
                    รหัสอ้างอิง
                  </th>
                  <th className="th-table" scope="col">
                    เวลา
                  </th>
                  <th className="th-table" scope="col">
                    คนโอน
                  </th>
                  <th className="th-table" scope="col">
                    คนรับ
                  </th>
                  <th className="th-table" scope="col">
                    จำนวน
                  </th>
                </tr>
              </thead>
              {Txdata.map((val) => {
                return (
                  <tbody>
                    <tr>
                      <td className="td-table">
                        <p
                          className="h1-admin"
                          onClick={() => {
                            checkidtransaction(val.Tx_id)
                          }}
                        >
                          {val.Tx_id}
                        </p>
                      </td>
                      <td className="td-table">
                        <p className="h1-admin">
                          {moment(val.time).format('DD/MMMM/YYYY - LT')}
                        </p>
                      </td>
                      <td className="td-table">
                        <p className="h1-admin">{val.transferor}</p>
                      </td>
                      <td className="td-table">
                        <p className="h1-admin">{val.recipient}</p>
                      </td>
                      <td className="td-table">
                        <p className="h1-admin">{val.amount}</p>
                      </td>
                    </tr>
                  </tbody>
                )
              })}
            </table>
          </div>
        </div>
      </motion.div>
    </div>
  )
}
