import React, { useState } from "react";
import { Link } from "react-router-dom";
import { Buttonmember } from "./Buttonmember";
import { useEffect } from "react";
import axios from "axios";
import "./Mypromotionshop.css";
import { Buttonshop } from "./Buttonshop";

function Mypromotionshop() {
  //-----------------------------------------------------

  const [id, setId] = useState("");
  const [name, setName] = useState("");
  const [phone, setPhone] = useState("");
  const [email, setEmail] = useState("");
  const [point, setPoint] = useState();

  const [file, setFile] = useState("");
  const [UploadFile, setUploadFile] = useState("");
  const [picture, setPicture] = useState("");
  const [productname, setProductName] = useState("");
  const [amount, setAmount] = useState();

  const [mypromotion, setMypromotion] = useState([]);

  const logout = () => {
    axios.get("/logout");
    window.location.replace("/");
  };

  const addpromotion = () => {
    const formData = new FormData();
    formData.append("file", file);
    axios.post("/postpromotion", {
      picture: picture,
      shop_name: name,
      product_name: productname,
      amount: amount,
    });

    axios
      .post("/upload", formData, {
        headers: {
          "Content-Type": "multipart/form-data",
        },
      })
      .then((response) => {
        if (response) {
          const { fileName, filePath } = response.data;
          setUploadFile({ fileName, filePath });
        }
      });
  };

  useEffect(() => {
    axios.get("/login").then((response) => {
      if (response.data.loggedIn == true) {
        setId(response.data.user[0].id);
        setName(response.data.user[0].name);
        setPhone(response.data.user[0].phone);
        setEmail(response.data.user[0].email);
        setPoint(response.data.user[0].point);
      }
      if (
        response.data.loggedIn == false ||
        response.data.user[0].role != "shop"
      ) {
        return window.location.replace("/");
      }
    });

    axios.get("/getmypromotion").then((response) => {
      if (response) {
        setMypromotion(response.data);
      }
    });
  }, []);

  return (
    <div>
      <link
        href="https://maxcdn.bootstrapcdn.com/font-awesome/4.7.0/css/font-awesome.min.css"
        rel="stylesheet"
      />

      <nav class="navbar navbar-light bg-light justify-content-between">
        <Link to="/promotion-shop">
          <i className="fas fa-angle-left">
            <a className="bl">ย้อนกลับ</a>
          </i>
        </Link>
      </nav>

      <div className="add-promotion">
        <form onSubmit={addpromotion}>
          <input
            type="text"
            placeholder="ชื่อสินค้า"
            onChange={(e) => {
              setProductName(e.target.value);
            }}
            required
          />
          <input
            type="number"
            placeholder="จำนวนคะแนน"
            onChange={(e) => {
              setAmount(e.target.value);
            }}
            required
          />
          <input
            name="file"
            type="file"
            placeholder="รูปภาพสินค้า"
            onChange={(e) => {
              setFile(e.target.files[0]);
              setPicture("/upload/" + e.target.files[0].name);
            }}
          />
          <button className="btn btn-success" type="submit">
            Add
          </button>
        </form>
      </div>

      <div className="whos-speaking-area speakers pad100">
        <div className="container">
          <div className="row">
            <div className="col-lg-12">
              <div className="section-title text-center">
                <div className="title-text mb50">
                  <h2>โปรโมชั่นร้านค้า</h2>
                  <input type="text" placeholder="search" />{" "}
                  <i className="fas fa-search"></i>
                </div>
              </div>
            </div>
          </div>

          {mypromotion.map((val) => {
            return (
              <div className="row mb50">
                <div className="col-xl-3 col-lg-3 col-md-4 col-sm-12">
                  <div className="speakers xs-mb30">
                    <div className="spk-img">
                      <img
                        className="img-fluid"
                        src={val.picture}
                        alt="trainer-img"
                      />
                    </div>
                    <div className="spk-info" onClick="">
                      <h4>Point : {val.amount}</h4>
                      <h3>Product : {val.product_name}</h3>
                    </div>
                  </div>
                </div>
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
}
export default Mypromotionshop;
