import { useHistory } from "react-router-dom";
import axios from "axios";
import "./Productstockpage.css";
import "semantic-ui-css/semantic.min.css";
import { Button, Header, Icon, Modal } from "semantic-ui-react";
import { Loader } from "semantic-ui-react";
import { Link } from "react-router-dom";
import { motion } from "framer-motion";

function Productstockpage() {
  const [open, setOpen] = React.useState(false);
  const [openEdit, setOpenEdit] = React.useState(false);
  const [active, setactive] = useState(true);
  //-----------------------------------------------
  const history = useHistory();
  const [name, setName] = useState("");
  const [productlist, setproductlist] = useState([]);
  const [picture, setpicture] = useState("");
  const [product_id, setproduct_id] = useState();
  const [product_name, setproduct_name] = useState("");
  const [product_code, setproduct_code] = useState("");
  const [price, setprice] = useState("");
  const [category, setcategory] = useState("");
  //-----------------------------------------------
  const goAddstock = () => {
    history.push("/addstock");
  };

  const deleteproduct = () => {
    axios
      .post("/deleteproduct", { id: product_id, picture: picture })
      .then((response) => {
        if (response.data.message) {
          alert(response.data.message);
          history.go();
        }
      });
  };

  /* ********************************** */

  const pageTransition = {
    in: {
      opacity: 1,
      x: 0,
    },
    out: {
      opacity: 0,
      x: 100,
    },
  };

  /* ********************************** */

  useEffect(() => {
    const timer = setTimeout(() => {
      setactive(false);
      axios.get("/login").then((response) => {
        if (response.data.loggedIn === true) {
          setName(response.data.user[0].name);
          axios.get("/getproduct").then((response) => {
            if (response.data) {
              setproductlist(response.data);
            }
          });
        }
        if (
          response.data.loggedIn === false ||
          response.data.user[0].role !== "shop"
        ) {
          return history.push("/login");
        }
      });
    }, 1000);
    return () => clearTimeout(timer);
  }, []);

  return (
    <div className="ProductStockPage">
      <Modal
        onClose={() => setOpenEdit(false)}
        onOpen={() => setOpenEdit(true)}
        open={openEdit}
        style={{ width: "100%" }}
      >
        <motion.div initial={{ opacity: 0, y: 100 }} animate={{ opacity: 1, y: 0 }} exit={{ opacity: 0, y: 100 }}>
          <h2 className="modal-header-productstock">แก้ไขสินค้า</h2>
          <Modal.Content>

            <div className="div-modal-label-productstock">
              <label className="modal-label-productstock">ชื่อสินค้า</label>
            </div>
            <div className="div-productstock">
              <input
                className="modal-input-productstock"
                value={product_name}
                onChange={(e) => {
                  setproduct_name(e.target.value);
                }}
              />
            </div>
            <div className="div-modal-label-productstock">
              <label className="modal-label-productstock">รหัสสินค้า</label>
            </div>
            <div className="div-productstock">
              <input
                className="modal-input-productstock"
                value={product_code}
                onChange={(e) => {
                  setproduct_code(e.target.value);
                }}
              />
            </div>
            <div className="div-modal-label-productstock">
              <label className="modal-label-productstock">ราคา</label>
            </div>
            <div className="div-productstock">
              <input
                className="modal-input-productstock"
                value={price}
                type="number"
                onChange={(e) => {
                  setprice(e.target.value);
                }}
              />
            </div>
            <div className="div-modal-label-productstock-menu">
              <label className="modal-label-productstock">หมวดหมู่</label>
              {/*แต่งไว้หน้า addstock */}
              <select
                className="menuaddstock"
                onChange={(e) => {
                  setcategory(e.target.value);
                }}
                required
              >
                <option selected value="null" className="option-menuaddstock">
                  เลือก...
                </option>
                <option value="ร้านทั่วไป" className="option-menuaddstock">
                  ร้านทั่วไป
                </option>
                <option value="ร้านทั่วไป" className="option-menuaddstock">
                  อาหาร
                </option>
                <option value="ร้านทั่วไป" className="option-menuaddstock">
                  เครื่องดื่ม
                </option>
                <option value="ร้านทั่วไป" className="option-menuaddstock">
                  สุขภาพ/ความงาม
                </option>
                <option value="ร้านทั่วไป" className="option-menuaddstock">
                  เสื้อผ้า
                </option>
              </select>
            </div>
            {/*<div className="div-productstock">
              <input
                className="modal-input-productstock"
                value={category}
                onChange={(e) => {
                  setcategory(e.target.value);
                }}
              />
              </div>*/}

          </Modal.Content>
        </motion.div>


        <Modal.Actions>
          <motion.div initial={{ opacity: 0, y: 100 }} animate={{ opacity: 1, y: 0 }} exit={{ opacity: 0, y: 100 }}>
            <Button
              className="button1-productstock"
              color="red"
              onClick={() => {
                setOpenEdit(false);
                setOpen(false);
              }}
            >
              ยกเลิก
            </Button>
            <Button
              className="button1-productstock"
              content="บันทึก"
              labelPosition="right"
              icon="checkmark"
              onClick={() => setOpenEdit(false)}
              positive
            />
          </motion.div>
        </Modal.Actions>

      </Modal>

      <Modal
        basic
        onClose={() => setOpen(false)}
        onOpen={() => setOpen(true)}
        open={open}
        size="small"
        style={{ width: "100%", top: "40%" }}
      >
        <Header icon>
          <motion.div initial={{ opacity: 0, y: 100 }} animate={{ opacity: 1, y: 0 }} exit={{ opacity: 0, y: 100 }}>
            <div className="buttoniconedit">
              <Button color="green" inverted onClick={() => setOpenEdit(true)}>
                <div className="iconedit">
                  <Icon name="edit" />
                </div>
                <p>แก้ไข</p>
              </Button>
            </div>
            <div className="buttoniconedit">
              <Button
                className="buttoniconedit"
                color="red"
                inverted
                onClick={() => deleteproduct()}
              >
                <div className="iconedit">
                  <Icon name="trash" />
                </div>
                <p>ลบ</p>
              </Button>
            </div>
            <div className="buttoniconedit">
              <Button
                className="buttoniconedit"
                color="red"
                inverted
                onClick={() => setOpen(false)}
              >
                <div className="iconedit">
                  <Icon name="cancel" />
                </div>
                <p>ยกเลิก</p>
              </Button>
            </div>
          </motion.div>
        </Header>
      </Modal>

      <nav className="navbar navbar-light bg-light justify-content-between">
        <Link className="buttonblack" to="/">
          <i className="fas fa-angle-left">
            <a className="bl">ย้อนกลับ</a>
          </i>
        </Link>
        <div className="div-img-logo2">
          <img className="img-logoo" src="/images/logo.png" />
        </div>
      </nav>

      <motion.div
        initial="out"
        animate="in"
        exit="out"
        variants={pageTransition}
      >
        {/* แต่งไว้หน้า promotion */}
        <div className="toppic-promotin">
          <h2 className="h2promotionmember">สินค้าของคุณ</h2>
        </div>
        <div className="shopbox-promotinnnnn">
          <button className="button-cotainerpromotionnnn" onClick={goAddstock}>
            <i className="fas fa-cart-plus"></i>เพิ่มสินค้าของคุณ
          </button>
        </div>

        <Loader active={active} />
        {/* แต่งไว้หน้า promotion */}
        <div className="top-productstck">
          <div className="container-productstck">
            {productlist.map((product) => {
              return (
                <div
                  className="shopbox-productstck"
                  onClick={() => {
                    setproduct_id(product.id);
                    setpicture(product.picture);
                    setproduct_name(product.product_name);
                    setproduct_code(product.product_code);
                    setprice(product.price);
                    setcategory(product.category);
                    setOpen(true);
                  }}
                >
                  {/*shopbox-img แต่งไว้ที่ promotionshop*/}
                  <div className="shopbox-img">
                    <img className="img-fluid-shopbox" src={product.picture} style={{ height: "100px" }} />
                  </div>

                  <div className="boxproductstck">
                    <p className="p-productstck">
                      ชื่อสินค้า : {product.product_name}
                    </p>
                    <p className="p-productstck">
                      รหัสสินค้า : {product.product_code}
                    </p>
                    <p className="p-productstck">ราคา : {product.price}</p>
                    <p className="p-productstck">
                      หมวดหมู่ : {product.category}
                    </p>
                  </div>
                </div>
              );
            })}
          </div>
        </div>
      </motion.div>
    </div>
  );
}
export default Productstockpage;
