import React from "react";
import { useEffect } from "react";
import axios from "axios";
import { useState } from "react";
import "./login_and_register.css";
import { Link, useHistory } from "react-router-dom";
import { Redirect } from "react-router";
import "./Registeration.css";
import { AnimatePresence, motion } from "framer-motion";

export default function Registeration() {
  var x;
  var y;
  var z;

  function shopp() {
    x = document.getElementById("memberregisterr").style.left = "-400px";
    y = document.getElementById("shopregisterr").style.left = "35px";
    z = document.getElementById("buttonregis").style.left = "110px";
  }
  function memberr() {
    x = document.getElementById("memberregisterr").style.left = "35px";
    y = document.getElementById("shopregisterr").style.left = "380px";
    z = document.getElementById("buttonregis").style.left = "0px";
  }

  //----------------------------------------------------------
  const history = useHistory();
  const [nameReg, setNameReg] = useState("");
  const [phoneReg, setPhoneReg] = useState("");
  const [emailReg, setEmailReg] = useState("");
  const [passwordReg, setPasswordReg] = useState("");
  const [typeReg, setTypeReg] = useState("");

  const [memberregisterStatus, setmemberRegisterStatus] = useState("");
  const [shopregisterStatus, setshopRegisterStatus] = useState("");
  const [isLoggedIn, setIsLoggedIn] = useState("flase");

  axios.defaults.withCredentials = true;
  //register
  const memberregister = (e) => {
    e;
    if (!nameReg || !phoneReg || !emailReg || !passwordReg) {
    } else {
      axios
        .post("/memberregister", {
          name: nameReg,
          phone: phoneReg,
          email: emailReg,
          password: passwordReg,
        })
        .then((response) => {
          if (response.data.message) {
            setmemberRegisterStatus(response.data.message);
          } else {
            alert(response.data.reg_success);
            history.push("/login");
          }
        });
    }
  };
  const shopregister = (e) => {
    e;
    if (
      !nameReg ||
      !phoneReg ||
      !emailReg ||
      !passwordReg ||
      !typeReg ||
      typeReg === "null"
    ) {
    } else {
      axios
        .post("/shopregister", {
          name: nameReg,
          phone: phoneReg,
          email: emailReg,
          password: passwordReg,
          type: typeReg,
        })
        .then((response) => {
          if (response.data.message) {
            setshopRegisterStatus(response.data.message);
          } else {
            alert(response.data.reg_success);
            history.push("/login");
          }
        });
    }
  };

  /* ********************************** */

  const pageTransition = {

    in: {
      opacity: 1,
      x: 0,

    },
    out: {
      opacity: 0,
      x: 100,

    }
  };


  /* ********************************** */

  useEffect(() => {
    axios.get("/login").then((response) => {
      if (response.data.loggedIn == true) {
        setIsLoggedIn("true");
      }
    });
  }, []);

  if (isLoggedIn == "true") {
    return <Redirect to="/" />;
  }

  return (
    <div>
      <nav className="navbar navbar-light bg-light justify-content-between">
        <Link className="buttonblack" to="/home">
          <i className="fas fa-angle-left">
            <a className="bl">ย้อนกลับ</a>
          </i>
        </Link>
      </nav>

      <motion.div initial="out" animate="in" exit="out" variants={pageTransition}>
      <div className="register-page">
        <div className="register-con">
          <div className="button-register">
            <div id="buttonregis"></div>
            <button
              type="button "
              className="button-member"
              onClick={() => memberr()}
            >
              ลูกค้า
            </button>
            <button
              type="button "
              className="button-shop"
              onClick={() => shopp()}
            >
              ร้านค้า
            </button>
          </div>

          <form
            onSubmit={(e) => memberregister(e.preventDefault())}
            id="memberregisterr"
            className="form-register"
          >
            <div className="member-register">
              <div className="group-member-register-name">
                <input
                  className="member-register-name"
                  type="text"
                  placeholder="Name"
                  onChange={(e) => {
                    setNameReg(e.target.value);
                  }}
                  required
                />
              </div>
              <div className="group-member-register-phone">
                <input
                  className="member-register-name"
                  type="text"
                  placeholder="Phone"
                  onChange={(e) => {
                    setPhoneReg(e.target.value);
                  }}
                  required
                />
              </div>
              <div className="group-member-register-email">
                <input
                  className="member-register-name"
                  type="email"
                  placeholder="Email"
                  onChange={(e) => {
                    setEmailReg(e.target.value);
                  }}
                  required
                />
              </div>
              <div className="group-member-register-password">
                <input
                  className="member-register-name"
                  type="password"
                  placeholder="Password"
                  onChange={(e) => {
                    setPasswordReg(e.target.value);
                  }}
                  required
                />
              </div>
            </div>
            <p className="p-member-register-name" style={{ color: "white" }}>
              {memberregisterStatus}
            </p>
            <button className="submit-member">สมัครสมาชิก</button>
          </form>

          <form
            onSubmit={(e) => shopregister(e.preventDefault())}
            id="shopregisterr"
            className="form-register"
          >
            <div className="shop-register">
              <div className="group-shop-register-name">
                <input
                  className="shop-register-name"
                  type="text"
                  placeholder="Name"
                  onChange={(e) => {
                    setNameReg(e.target.value.toLowerCase());
                  }}
                  required
                />
              </div>
              <div className="group-shop-register-phone">
                <input
                  className="shop-register-name"
                  type="text"
                  placeholder="Phone"
                  onChange={(e) => {
                    setPhoneReg(e.target.value);
                  }}
                  required
                />
              </div>
              <div className="group-shop-register-email">
                <input
                  className="shop-register-name"
                  type="email"
                  placeholder="Email"
                  onChange={(e) => {
                    setEmailReg(e.target.value);
                  }}
                  required
                />
              </div>
              <div className="group-shop-register-password">
                <input
                  className="shop-register-name"
                  type="password"
                  placeholder="Password"
                  onChange={(e) => {
                    setPasswordReg(e.target.value);
                  }}
                  required
                />
              </div>
              <div className="group-shop-register-shop-type">
                <label className="labeltype">ประเภทร้านค้า</label>
                <select
                  className="menutypeshop"
                  onChange={(e) => {
                    setTypeReg(e.target.value);
                  }}
                  required
                >
                  <option selected value="null" className="option-register">
                    เลือก...
                  </option>
                  <option value="ร้านทั่วไป" className="option-register">
                    ร้านทั่วไป
                  </option>
                  <option
                    value="ร้านอาหาร/เครื่องดื่ม"
                    className="option-register"
                  >
                    ร้านอาหาร/เครื่องดื่ม
                  </option>
                  <option value="สุขภาพ/ความงาม" className="option-register">
                    สุขภาพ/ความงาม
                  </option>
                  <option value="เสื้อผ้า" className="option-register">
                    เสื้อผ้า
                  </option>
                </select>
              </div>
            </div>
            <p className="p-shop-register-name" style={{ color: "white" }}>
              {shopregisterStatus}
            </p>
            <button className="submit-shop">สมัครสมาชิก</button>
          </form>
        </div>
      </div>
      </motion.div>
    </div>
  );
}
