import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { Buttonmember } from './Buttonmember';
import { useEffect } from 'react';
import axios from 'axios';
import './Transferpoint.css';



function Transferpoint() {
    const [click, setClick] = useState(false);
    const [button, setButton] = useState(true);


    const handleClick = () => setClick(!click);
    const closeMobileMenu = () => setClick(false);

    const showButton = () => {
        if (window.innerWidth <= 960) {
            setButton(false)
        } else {
            setButton(true)
        }
    };

    window.addEventListener('resize', showButton);
    //-------------------------------------------------------
    axios.defaults.withCredentials = true;

    const [id, setId] = useState();
    const [point, setPoint] = useState();
    const [phone, setPhone] = useState("");
    const [destPhone, setdestPhone] = useState("");
    const [amount, setamount] = useState();

    const logout = () => {
        axios.get("/logout");
        window.location.replace('/');
    }
    const transferSubmit = () => {
        if (destPhone == "") {
            alert("เบอร์ปลายทางไม่ถูกต้อง");
        } else if (phone == destPhone) {
            alert("เบอร์ปลายทางไม่ถูกต้อง");
        } else if (amount > point) {
            alert("คะแนนไม่เพียงพอสำหรับการโอน");
        } else if (amount < 1) {
            alert("คะแนนไม่ถูกต้อง");
        } else {
            axios.put("/memberPointTransfer", {
                id: id,
                phone: phone,
                destphone: destPhone,
                amount: amount
            })
                .then((response) => {
                    if (response.data.message) {
                        alert(response.data.message);
                    }
                    //setTransferStatus(response.data.message);
                    else if (response.data.loggedIn == true) {
                        alert("โอนคะแนนสำเร็จ");
                        setPoint(response.data.user[0].point);
                    }
                });
        }
    }

    useEffect(() => {
        axios.get("/login").then((response) => {
            if (response.data.loggedIn == true) {
                setId(response.data.user[0].id);
                setPoint(response.data.user[0].point);
                setPhone(response.data.user[0].phone);
            }
            if(response.data.loggedIn == false || response.data.user[0].role != 'member'){
                return (window.location.replace('/'));
            }
        });
    }, []);

    return (
        <>
            <nav className="navbar">
                <div className="navbar-container">
                    <Link to="/" className="navbar-logo">
                        TNC
                    </Link>
                    <div className='menu-icon' onClick={handleClick}>
                        <i className={click ? 'fas fa-times' : 'fas fa-angle-down'} />

                    </div>


                    <ul className={click ? 'nav-menu active' : 'nav-menu'}>

                        <li className='nav-item'>
                            <p className='nav-links' onClick={closeMobileMenu}></p>
                        </li>

                        {button && <Buttonmember buttonStyle='btn--outline'>MY PROFILE</Buttonmember>}
                        <p onClick={logout} className="navbar-logo">
                            <i className='fas fa-sign-out-alt'></i>
                        </p>
                    </ul>


                </div>

            </nav>

            <div className='transferpoint'>
                <h1>phone : {phone}</h1>
                <h1>โอนคะแนน</h1>
                <h2>Point : {point}</h2>
                <label>กรอกเบอร์</label>
                <input
                    type="text"
                    onChange={(e) => {
                        setdestPhone(e.target.value);
                    }} />

                <label>กรอกจำนวนคะแนน</label>
                <input
                    type="number" min="0"
                    onChange={(e) => {
                        setamount(e.target.value);
                    }}
                />
                <button onClick={transferSubmit}>ยืนยัน</button>

            </div>

        </>

    );
}
export default Transferpoint;
