import React, { useState } from "react";
import { Link } from "react-router-dom";
import { useEffect } from "react";
import { useHistory } from "react-router";
import axios from "axios";
import Modal from "react-modal";
import { Loader } from "semantic-ui-react";
import "./Promotiondetails.css";
import { AnimatePresence, motion } from "framer-motion";

const customStyles = {
  content: {
    top: "50%",
    left: "50%",
    right: "auto",
    bottom: "auto",
    marginRight: "-50%",
    transform: "translate(-50%, -50%)",
  },
};

function Promotiondetails(shop) {
  let subtitle;
  const [modalIsOpenModal, setIsOpenModal] = React.useState(false);

  function openModal() {
    setIsOpenModal(true);
  }

  function afterOpenModal() {
    // references are now sync'd and can be accessed.
    subtitle.style.color = "#000";
  }

  function closeModal() {
    setIsOpenModal(false);
  }
  //-----------------------------------------------------
  const history = useHistory();
  const setshop = shop;
  const [phone, setPhone] = useState("");
  const [shopname, setshopname] = useState("");
  const [point, setPoint] = useState();

  const [amount, setamount] = useState();
  const [product_name, setProductName] = useState("");
  const [promotionlist, setPromotionList] = useState([]);
  const [promotionpointlist, setPromotionPointList] = useState([]);

  const [active, setactive] = useState(true); //loader
  //-----------------------------------------------------

  const showpromotion = (shopname) => {
    axios
      .post("/getpromotionpoint", { shop_name: shopname })
      .then((response) => {
        if (response) {
          setPromotionPointList(response.data);
        }
      });
    axios
      .post("/selectshop", {
        shop_name: shopname,
      })
      .then((response) => {
        setPromotionList(response.data);
      });
  };

  const selectpromotion = (id) => {
    axios
      .post("/selectpromotion", {
        id: id,
      })
      .then((response) => {
        if (response) {
          setProductName(response.data[0].product_name);
          setamount(response.data[0].amount);
          setshopname(response.data[0].shop_name);
          openModal();
        }
      });
  };

  const submitpromotion = () => {
    if (amount > point) {
      alert("คะแนนไม่เพียงพอ");
    } else {
      axios
        .post("/promotion/exchange", {
          shopname: shopname,
          amount: amount,
          phone: phone,
        })
        .then((response) => {
          if (response.data) {
            alert(response.data.status);
            history.go();
          }
        });
    }
  };

  /* ********************************** */

  const pageTransition = {

    in: {
      opacity: 1,
      x: 0,

    },
    out: {
      opacity: 0,
      x: 100,

    }
  };


  /* ********************************** */

  useEffect(() => {
    const timer = setTimeout(() => {
      setactive(false);
      axios.get("/login").then((response) => {
        if (response.data.loggedIn === true) {
          if (response.data.user[0].role === "member") {
            axios.get("/member").then((response) => {
              if (response) {
                setPoint(response.data.user[0].point);
              }
            });
          } else if (response.data.user[0].role === "shop") {
            axios.get("/shop").then((response) => {
              if (response) {
                setPoint(response.data.user[0].point);
              }
            });
          }
          setPhone(response.data.user[0].phone);
          showpromotion(setshop.match.params.shopname);
        } else if (
          response.data.loggedIn === false ||
          !response.data.user[0].role
        ) {
          return history.push("/login");
        }
      });
    }, 1000);
    return () => clearTimeout(timer);
  }, []);

  return (
    <div>
      <link
        href="https://maxcdn.bootstrapcdn.com/font-awesome/4.7.0/css/font-awesome.min.css"
        rel="stylesheet"
      />

      <div className="promotiondetails">
        <Modal
          isOpen={modalIsOpenModal}
          onAfterOpen={afterOpenModal}
          onRequestClose={closeModal}
          style={customStyles}
          contentLabel="Example Modal"
        >
           <motion.div initial="out" animate="in" exit="out" variants={pageTransition}>
          <div className="modalpromotiondetails">
            <h2
              className="h2h2h2promotiondetails"
              ref={(_subtitle) => (subtitle = _subtitle)}
            >
              <p>ชื่อสินค้า : {product_name}</p>
            </h2>
            <h3 className="h3h3h3promotiondetails">
              <p>จำนวนคะแนน : {amount}</p>
            </h3>
            <div>
              <button
                className="buttoncalculate-modal1"
                onClick={submitpromotion}
              >
                ใช้สิทธิ
              </button>
              <button className="buttoncalculate-modal2" onClick={closeModal}>
                ยกเลิก
              </button>
            </div>
          </div>
          </motion.div>
        </Modal>
      </div>

      <nav className="navbar navbar-light bg-light justify-content-between">
        <Link
          className="buttonblack"
          onClick={() => {
            history.goBack();
          }}
        >
          <i className="fas fa-angle-left">
            <a className="bl">ย้อนกลับ</a>
          </i>
        </Link>
        <div className="div-img-logo2">
          <img className="img-logoo" src="/images/logo.png" />
        </div>
      </nav>

      <motion.div initial="out" animate="in" exit="out" variants={pageTransition}>
      <div className="top-promotion">
        <div className="divh1promotiondetails1">
          <h1 className="h1promotiondetails1">โปรโมชั่นคะแนน</h1></div>
        <div className="container-promotion">

          <Loader active={active} />
          {promotionpointlist.map((val) => {
            return (
              <div className="shopbox-promotin">
                {/*shopbox-img แต่งไว้ที่ promotionshop*/}
                <div className="shopbox-img">
                  <img
                    className="img-fluid-shopbox"
                    src={val.picture}
                    style={{height : "100px"}}
                    alt="trainer-img"
                  />
                </div>

                <div className="boxpromotion">
                  <p>{val.product_name}</p>
                  <p>ได้รับ : {val.amount_point} คะแนน</p>
                </div>
              </div>
            );
          })}
        </div>
        <div className="divh1promotiondetails2">
          <h1 className="h1promotiondetails2">โปรโมชั่นแลกสินค้า</h1></div>
        <div className="container-promotion">

          <Loader active={active} />
          {promotionlist.map((val) => {
            return (
              <div
                className="shopbox-promotin"
                onClick={() => {
                  selectpromotion(val.id);
                }}
              >
                {/*shopbox-img แต่งไว้ที่ promotionshop*/}
                <div className="shopbox-img">
                  <img
                    className="img-fluid-shopbox"
                    src={val.picture}
                    style={{height : "100px"}}
                    alt="trainer-img"
                  />
                </div>

                <div className="boxpromotion">
                  <p className="p-promotindetail">ชื่อสินค้า : {val.product_name}</p>
                  <p className="p-promotindetail">คะแนนที่ใช้ : {val.amount}</p>
                </div>
              </div>
            );
          })}
        </div>
      </div>
      </motion.div>
    </div>
  );
}
export default Promotiondetails;
