import React from 'react';
import './HeroSection.css';
import { Buttonregister } from './Buttonregister';

function Herosection() {
    return (
        <div className='hero-container'>
            <img src="images/home.jpg" />
            <p>สะสมคะแนนของคุณได้เลย <i className="fas fa-angle-double-down"></i></p>
            <div className="hero-btns">
                <Buttonregister className='btns' buttonStyle='btn--outline'
                  buttonSize='btn--large'>สมัครสมาชิก<i className="fas fa-angle-right"></i></Buttonregister>
            </div>
        </div>
    )
}

export default Herosection;
