import React from 'react';
import {BrowserRouter as Router,Redirect,Route} from "react-router-dom";
import "./App.css";
import axios from 'axios';
import { useEffect } from 'react';
import { useState } from 'react';

import Registeration from './pages/Registeration';
import Login from './pages/Login';
import Main from './pages/Main';
import ProtectedRoute from './components/ProtectedRoute';

import Transferpoint from './components/Transferpoint';
import Transaction from './components/Transaction';
import Promotion from './components/Promotion';
import Memberprofile from './components/Memberprofile';

import Transferpointshop from './components/Transferpointshop';
import Calculatepointshop from './components/Calculatepointshop';
import Transactionshop from './components/Transactionshop';
import Promotionshop from './components/Promotionshop';
import Topuppointshop from './components/Topuppointshop';
import Shopprofile from './components/Shopprofile';

axios.defaults.baseURL = "http://localhost:3001";


function App() {

  axios.defaults.withCredentials = true;
  const [Auth,setAuth] = useState();
  useEffect(() => {
    axios.get("/login").then((response) => {
        if (response.data.loggedIn == true) {
            setAuth(response.data.loggedIn);
        }
        if (response.data.loggedIn == false) {
            setAuth(response.data.loggedIn);
        }
    });
}, []);
  return (
    <Router>
      <Route path="/" exact render={(props) => <Main />} /> 
      <Route path="/registration" exact render={(props) => <Registeration/>} />
      <Route path="/login" exact render={(props) => <Login />} />

      <ProtectedRoute path="/transfer-point"  Auth = {Auth} exact render={(props) => <Transferpoint />} />
      <ProtectedRoute path="/transaction" Auth = {Auth} exact render={(props) => <Transaction />} />
      <ProtectedRoute path="/promotion" Auth = {Auth} exact render={(props) => <Promotion />} />
      <ProtectedRoute path="/memberprofile" Auth = {Auth} exact render={(props) => <Memberprofile />} />

      <ProtectedRoute path="/transfer-point-shop" Auth = {Auth} exact render={(props) => <Transferpointshop />} />
      <ProtectedRoute path="/calculate-point-shop" Auth = {Auth} exact render={(props) => <Calculatepointshop />} />
      <ProtectedRoute path="/transaction-shop" Auth = {Auth} exact render={(props) => <Transactionshop />} />
      <ProtectedRoute path="/promotion-shop" Auth = {Auth} exact render={(props) => <Promotionshop />} />
      <ProtectedRoute path="/top-up-point-shop" Auth = {Auth} exact render={(props) => <Topuppointshop />} />
      <ProtectedRoute path="/shopprofile" Auth = {Auth} exact render={(props) => <Shopprofile />} />

    </Router>
  );
}

export default App;
