import React, { useState } from 'react';
import { Router } from 'react-router-dom';
import { Link } from 'react-router-dom';
import { Buttonshop } from './Buttonshop';
import axios from 'axios';
import { useEffect } from 'react';
import './Menushop.css';


function Menushop() {
    
    //--------------------------------------------------
    const [point, setPoint] = useState();

    const logout = () => {
        axios.get("/logout");
        window.location.replace('/');
    }
    const refresh = () => {
        axios.get('/shop');
        window.location.replace('/');
    }

    useEffect(() => {
        axios.get("/login").then((response) => {
            if (response.data.loggedIn == true) {
                setPoint(response.data.user[0].point);
            }
            if (response.data.loggedIn == false || response.data.user[0].role != 'shop') {
                return (window.location.replace('/'));
            }
        });
    }, []);

    return (
        <>
            <nav class="navbar navbar-light bg-light justify-content-between">
               <a className="nav-tnc" ><i className="fas fa-braille"></i>T N C</a>
                <form class="form-inline">
                    <button to=""className="bn632-hover bn24">MY PROFILE</button>
                     <button onClick={logout} className="bn632-hover bn24">ออกจากระบบ</button>
                        
                </form>
            </nav>
            
            <div className="main-menu">

                <h1>Point : {point}</h1>
                <button onClick={refresh}>refresh</button>

                <Link to="/transfer-point-shop" className="menu">
                    <i class='fas fa-exchange-alt'></i>
                    โอนคะแนน
                </Link>

                <Link to="/calculate-point-shop" className="menu">
                    <i class='fas fa-square-root-alt'></i>
                    คำนวณคะแนน
                </Link>

                <Link to="/transaction-shop" className="menu">
                    <i class='fas fa-redo-alt'></i>
                    รายการย้อนหลัง
                </Link>

                <Link to="/promotion-shop" className="menu">
                    <i class='fas fa-tag'></i>
                    เพิ่มโปรโมชั่น
                </Link>

                <Link to="/top-up-point-shop" className="menu">
                    <i class='fas fa-money-bill-alt'></i>
                    เติมคะแนน
                </Link>

            </div>

        </>

    );
}
export default Menushop;
