import React, { useState } from 'react';
import { Link, Redirect } from 'react-router-dom';
import { useEffect } from 'react';
import Modal from 'react-modal';
import axios from 'axios';

import './Memberprofile.css';

const customStyles = {
  content: {
    top: '50%',
    left: '50%',
    right: 'auto',
    bottom: 'auto',
    marginRight: '-50%',
    transform: 'translate(-50%, -50%)',
  },
};

function Memberprofile() {
  const [click, setClick] = useState(false);


  const handleClick = () => setClick(!click);
  const closeMobileMenu = () => setClick(false);

  //----------------------------------------------------------
  let subtitle;
  const [modalIsOpen, setIsOpen] = React.useState(false);

  function openModal() {
    setIsOpen(true);
  }

  function afterOpenModal() {
    // references are now sync'd and can be accessed.
    subtitle.style.color = '#f00';
  }

  function closeModal() {
    setIsOpen(false);
  }

  const [id, setId] = useState();
  const [name, setName] = useState("");
  const [phone, setPhone] = useState("");
  const [email, setEmail] = useState("");
  const [point, setPoint] = useState();

  const [statusName, setStatusName] = useState("");
  const [statusPhone, setStatusPhone] = useState("");

  const [newname, setnewName] = useState("");
  const [newphone, setnewPhone] = useState("");

  axios.defaults.withCredentials = true;
  const logout = () => {
    axios.get("/logout");
    window.location.replace('/');
  }
  const refresh = () => {
    axios.get("/member");
    window.location.replace('/memberprofile');
  }
  const nameUpdate = () => {
    if (newname == "") {
      setStatusName("ชื่อไม่ถูกต้อง");
    } else {
      axios.put("/updatememberName", { name: newname, id: id }).then((response) => {
        setStatusName("บันทึกสำเร็จ");
        return setName(response.data.user[0].name);
      });
    }

  }
  const phoneUpdate = () => {
    if (newphone == "") {
      setStatusPhone("เบอร์โทรไม่ถูกต้อง");
    } else {
      axios.put("/updatememberPhone", { phone: newphone, id: id }).then((response) => {
        if (response.data.message) {
          return setStatusPhone(response.data.message);
        }
        setStatusPhone("บันทึกสำเร็จ");
        setPhone(response.data.user[0].phone);
      });
    }
  }

  useEffect(() => {
    axios.get("/login").then((response) => {
      if (response.data.loggedIn == true) {
        setId(response.data.user[0].id);
        setName(response.data.user[0].name);
        setPhone(response.data.user[0].phone);
        setEmail(response.data.user[0].email);
        setPoint(response.data.user[0].point);
      }
    });
  }, []);
  return (
    <>
      <nav className="navbar">
        <div className="navbar-container">
          <Link to="/" className="navbar-logo">
            TNC
          </Link>
          <div className='menu-icon' onClick={handleClick}>
            <i className={click ? 'fas fa-times' : 'fas fa-angle-down'} />

          </div>


          <ul className={click ? 'nav-menu active' : 'nav-menu'}>

            <li className='nav-item'>
              <p className='nav-links' onClick={closeMobileMenu}></p>
            </li>

            <p onClick={logout} className="navbar-logo">
              <i className='fas fa-sign-out-alt'></i>
            </p>
          </ul>


        </div>

      </nav>

      <div>
        <button onClick={openModal}>Open Modal</button>
        <Modal
          isOpen={modalIsOpen}
          onAfterOpen={afterOpenModal}
          onRequestClose={closeModal}
          style={customStyles}
          contentLabel="Example Modal"
        >
          <h2 ref={(_subtitle) => (subtitle = _subtitle)}>Hello</h2>
          <button onClick={closeModal && openModal}>close</button>
          <div>I am a modal</div>
          <form>
            <input />
            <button>tab navigation</button>
            <button>stays</button>
            <button>inside</button>
            <button>the modal</button>
          </form>
        </Modal>
      </div>

      <div>
        <h1>member</h1>
        <p>Hi  {name} </p>
        <p>Phone : {phone} <br />Email : {email} <br />Point :  {point} <button onClick={refresh}>refresh</button></p>
        <br />
        <input type="text" placeholder="name"
          onChange={(e) => { setnewName(e.target.value) }
          }></input>
        {statusName}
        <button className="btn" onClick={nameUpdate}>update</button>

        <input type="text" placeholder="Phone"
          onChange={(e) => { setnewPhone(e.target.value) }
          }></input>
        {statusPhone}
        <button className="btn" onClick={phoneUpdate}>update</button>

        <br /><button className="logout" onClick={logout}>Logout</button>
      </div>

    </>

  );
}
export default Memberprofile;
