import React, { useState } from 'react';
import { Link, Redirect } from 'react-router-dom';
import { useEffect } from 'react';
import Modal from 'react-modal';
import axios from 'axios';

import './Memberprofile.css';

const customStyles = {
  content: {
    top: '50%',
    left: '50%',
    right: 'auto',
    bottom: 'auto',
    marginRight: '-50%',
    transform: 'translate(-50%, -50%)',
  },
};

function Memberprofile() {
  const [click, setClick] = useState(false);


  const handleClick = () => setClick(!click);
  const closeMobileMenu = () => setClick(false);

  //----------------------------------------------------------
  let subtitle;
  const [modalIsOpenid, setIsOpenid] = React.useState(false);
  const [modalIsOpenname, setIsOpenname] = React.useState(false);
  const [modalIsOpenphone, setIsOpenphone] = React.useState(false);
  const [modalIsOpenemail, setIsOpenemail] = React.useState(false);

  function openModalid() {
    setIsOpenid(true);
  }

  function openModalname() {
    setIsOpenname(true);
  }

  function openModalphone() {
    setIsOpenphone(true);
  }

  function openModalemail() {
    setIsOpenemail(true);
  }

  function afterOpenModal() {
    // references are now sync'd and can be accessed.
    subtitle.style.color = '#f00';
  }

  function closeModal() {
    setIsOpenid(false);
    setIsOpenname(false);
    setIsOpenphone(false);
    setIsOpenemail(false);
  }


  const [id, setId] = useState();
  const [name, setName] = useState("");
  const [phone, setPhone] = useState("");
  const [email, setEmail] = useState("");
  const [point, setPoint] = useState();

  onst[neweid, setnewId] = useState("");
  const [newname, setnewName] = useState("");
  const [newphone, setnewPhone] = useState("");
  const [newemail, setnewEmail] = useState("");

  axios.defaults.withCredentials = true;
  const logout = () => {
    axios.get("/logout");
    window.location.replace('/');
  }
  const refresh = () => {
    axios.get("/member");
    window.location.replace('/memberprofile');
  }

  const idUpdate = () => {
    if (newphone == "") {
      alert("เลขบัตรไม่ถูกต้อง");
    } else {
      axios.put("/updatememberPhone", { phone: newphone, id: id }).then((response) => {
        if (response.data.message) {
          return alert(response.data.message);
        }
        alert("บันทึกสำเร็จ");
        setPhone(response.data.user[0].phone);
      });
    }
  }

  const nameUpdate = () => {
    if (newname == "") {
      alert("ชื่อไม่ถูกต้อง");
    } else {
      axios.put("/updatememberName", { name: newname, id: id }).then((response) => {
        alert("บันทึกสำเร็จ");
        return setName(response.data.user[0].name);
      });
    }

  }
  const phoneUpdate = () => {
    if (newphone == "") {
      alert("เบอร์โทรไม่ถูกต้อง");
    } else {
      axios.put("/updatememberPhone", { phone: newphone, id: id }).then((response) => {
        if (response.data.message) {
          return alert(response.data.message);
        }
        alert("บันทึกสำเร็จ");
        setPhone(response.data.user[0].phone);
      });
    }
  }

  const emailUpdate = () => {
    if (newphone == "") {
      alert("อีเมลไม่ถูกต้อง");
    } else {
      axios.put("/updatememberEmail", { phone: newphone, id: id }).then((response) => {
        if (response.data.message) {
          return alert(response.data.message);
        }
        alert("บันทึกสำเร็จ");
        setPhone(response.data.user[0].phone);
      });
    }
  }

  useEffect(() => {
    axios.get("/login").then((response) => {
      if (response.data.loggedIn == true) {
        setId(response.data.user[0].id);
        setName(response.data.user[0].name);
        setPhone(response.data.user[0].phone);
        setEmail(response.data.user[0].email);
        setPoint(response.data.user[0].point);
      }
      if (response.data.loggedIn == false || response.data.user[0].role != 'member') {
        return (window.location.replace('/'));
      }
    });
  }, []);
  return (
    <>
      <nav className="navbar">
        <div className="navbar-container">
          <Link to="/" className="navbar-logo">
            TNC
          </Link>
          <div className='menu-icon' onClick={handleClick}>
            <i className={click ? 'fas fa-times' : 'fas fa-angle-down'} />

          </div>


          <ul className={click ? 'nav-menu active' : 'nav-menu'}>

            <li className='nav-item'>
              <p className='nav-links' onClick={closeMobileMenu}></p>
            </li>

            <p onClick={logout} className="navbar-logo">
              <i className='fas fa-sign-out-alt'></i>
            </p>
          </ul>


        </div>

      </nav>
      <div>
        <Modal
          isOpen={modalIsOpenid}
          onAfterOpen={afterOpenModal}
          onRequestClose={closeModal}
          style={customStyles}
          contentLabel="Example Modal"
        >
          <h2 ref={(_subtitle) => (subtitle = _subtitle)}>เลขบัตรประจำตัวประชาชน</h2>
          <form>
            <input type="text" placeholder="id"
              onChange={(e) => { setnewId(e.target.value) }
              }></input>
            <button className="btn" onClick={nameUpdate}>update</button>
          </form>
        </Modal>
      </div>

      <div>
        <Modal
          isOpen={modalIsOpenid}
          onAfterOpen={afterOpenModal}
          onRequestClose={closeModal}
          style={customStyles}
          contentLabel="Example Modal"
        >
          <h2 ref={(_subtitle) => (subtitle = _subtitle)}>เลขบัตรประจำตัวประชาชน</h2>
          <form>
            <input type="text" placeholder="id"
              onChange={(e) => { setnewId(e.target.value) }
              }></input>
            <button className="btn" onClick={idUpdate}>update</button>
          </form>
        </Modal>
      </div>

      <div>
        <Modal
          isOpen={modalIsOpenname}
          onAfterOpen={afterOpenModal}
          onRequestClose={closeModal}
          style={customStyles}
          contentLabel="Example Modal"
        >
          <h2 ref={(_subtitle) => (subtitle = _subtitle)}>ชื่อ-สกุล</h2>
          <form>
            <input type="text" placeholder="name"
              onChange={(e) => { setnewName(e.target.value) }
              }></input>
            <button className="btn" onClick={nameUpdate}>update</button>
          </form>
        </Modal>
      </div>

      <div>
        <Modal
          isOpen={modalIsOpenphone}
          onAfterOpen={afterOpenModal}
          onRequestClose={closeModal}
          style={customStyles}
          contentLabel="Example Modal"
        >
          <h2 ref={(_subtitle) => (subtitle = _subtitle)}>เบอร์โทรศัพท์</h2>
          <form>
            <input type="text" placeholder="phone"
              onChange={(e) => { setnewPhone(e.target.value) }
              }></input>
            <button className="btn" onClick={phoneUpdate}>update</button>
          </form>
        </Modal>
      </div>

      <div>
        <Modal
          isOpen={modalIsOpenemail}
          onAfterOpen={afterOpenModal}
          onRequestClose={closeModal}
          style={customStyles}
          contentLabel="Example Modal"
        >
          <h2 ref={(_subtitle) => (subtitle = _subtitle)}>อีเมล์</h2>
          <form>
            <input type="email" placeholder="email"
              onChange={(e) => { setnewEmail(e.target.value) }
              }></input>
            <button className="btn" onClick={emailUpdate}>update</button>
          </form>
        </Modal>
      </div>




      <div className="container bootstrap snippets bootdey">
        <div className="panel-body inf-content">
          <div className="row">
            <div className="col-md-4">
              <img alt="" title="" className="img-circle img-thumbnail isTooltip" src="https://bootdey.com/img/Content/avatar/avatar7.png" data-original-title="Usuario" />
              <ul title="Ratings" className="list-inline ratings text-center">
                <li><a href="#"><span className="glyphicon glyphicon-star"></span></a></li>
                <li><a href="#"><span className="glyphicon glyphicon-star"></span></a></li>
                <li><a href="#"><span className="glyphicon glyphicon-star"></span></a></li>
                <li><a href="#"><span className="glyphicon glyphicon-star"></span></a></li>
                <li><a href="#"><span className="glyphicon glyphicon-star"></span></a></li>
              </ul>
            </div>
            <div className="col-md-6">
              <strong>ข้อมูลส่วนตัว</strong><br />
              <div className="table-responsive">
                <table className="table table-user-information">
                  <tbody>
                    <tr>
                      <td>
                        <strong>
                          <span className="glyphicon glyphicon-asterisk text-primary"></span>
                          เลขบัตรประจำตัวประชาชน
                        </strong>
                      </td>
                      <td className="text-primary">
                        รอเพิ่ม <i onClick={openModalid} className="fas fa-pen"></i>
                      </td>
                    </tr>
                    <tr>
                      <td>
                        <strong>
                          <span className="glyphicon glyphicon-user  text-primary"></span>
                          ชื่อ-สกุล
                        </strong>
                      </td>
                      <td className="text-primary">
                        {name} <i onClick={openModalname} className="fas fa-pen"></i>
                      </td>
                    </tr>

                    <tr>
                      <td>
                        <strong>
                          <span className="glyphicon glyphicon-bookmark text-primary"></span>
                          เบอร์โทรศัพท์
                        </strong>
                      </td>
                      <td className="text-primary">
                        {phone} <i onClick={openModalphone} className="fas fa-pen"></i>
                      </td>
                    </tr>



                    <tr>
                      <td>
                        <strong>
                          <span className="glyphicon glyphicon-envelope text-primary"></span>
                          อีเมล์
                        </strong>
                      </td>
                      <td className="text-primary">
                        {email} <i onClick={openModalemail} className="fas fa-pen"></i>
                      </td>
                    </tr>

                    <tr>
                      <td>
                        <strong>
                          <span className="glyphicon glyphicon-envelope text-primary"></span>
                          คะแนน
                        </strong>
                      </td>
                      <td className="text-primary">
                        {point}
                      </td>
                    </tr>


                    <tr>
                      <td>
                        <strong>
                          <span className="glyphicon glyphicon-eye-open text-primary"></span>
                          สถานะ
                        </strong>
                      </td>
                      <td className="text-primary">
                        รอเพิ่ม
                      </td>
                    </tr>
                  </tbody>
                </table>
              </div>
            </div>
          </div>
        </div>
      </div>


    </>

  );
}
export default Memberprofile;
