import React from "react";
import { useEffect } from 'react';
import axios from 'axios';
import { useState } from 'react';
import './login_and_register.css';
import { Link } from "react-router-dom";
import { Redirect } from "react-router";
import './Registeration.css';

export default function Registeration() {





  //----------------------------------------------------------
  const [nameReg, setNameReg] = useState("");
  const [phoneReg, setPhoneReg] = useState("");
  const [emailReg, setEmailReg] = useState("");
  const [passwordReg, setPasswordReg] = useState("");
  const [typeReg, setTypeReg] = useState("");

  const [memberregisterStatus, setmemberRegisterStatus] = useState("");
  const [shopregisterStatus, setshopRegisterStatus] = useState("");
  const [isLoggedIn, setIsLoggedIn] = useState("flase");

  axios.defaults.withCredentials = true;
  //register
  const memberregister = () => {
    axios
      .post("/memberregister", {
        name: nameReg,
        phone: phoneReg,
        email: emailReg,
        password: passwordReg,
      })
      .then((response) => {
        alert(response.data.message);
        setmemberRegisterStatus(response.data.message);
      });
  };
  const shopregister = () => {
    axios
      .post("/shopregister", {
        name: nameReg,
        phone: phoneReg,
        email: emailReg,
        password: passwordReg,
        type: typeReg
      })
      .then((response) => {
        alert(response.data.message);
        setshopRegisterStatus(response.data.message);
      });
  };

  useEffect(() => {
    axios.get("/login").then((response) => {
      if (response.data.loggedIn == true) {
        setIsLoggedIn("true");
      }
    });
  }, []);

  if (isLoggedIn == "true") {
    return (
      <Redirect to="/" />
    );
  }
  return (
    <>
      <div className="navbar navbar-default navbar-static-top">
        <div className="container bootstrap snippets bootdey">
          <div className="navbar-header">

            <Link to="/"  ><i className="fas fa-angle-left"><a className="bl">ย้อนกลับ</a></i><i className="fas fa-braille" ></i>T N C</Link>

          </div>

        </div>
      </div>


      <div className="login-wrap">
        <div className="login-html">
          <input id="tab-1" type="radio" name="tab" className="sign-in" checked /><label for="tab-1" className="tab">ลูกค้า</label>
          <input id="tab-2" type="radio" name="tab" className="sign-up" /><label for="tab-2" className="tab">ร้านค้า</label>
          <div className="login-form">
            <form onSubmit={memberregister}>
              <div className="sign-in-htm">
                <div className="group">
                  <label for="user" className="label">Name</label>
                  <input className="input" type="text"
                    placeholder="Name"
                    onChange={(e) => {
                      setNameReg(e.target.value);
                    }}
                    required />
                </div>
                <div className="group">
                  <label for="pass" className="label">Phone</label>
                  <input className="input" type="text"
                    placeholder="Phone"
                    onChange={(e) => {
                      setPhoneReg(e.target.value);
                    }}
                    required />
                </div>
                <div className="group">
                  <label for="pass" className="label">Email</label>
                  <input className="input" type="email"
                    placeholder="Email"
                    onChange={(e) => {
                      setEmailReg(e.target.value);
                    }}
                    required />
                </div>
                <div className="group">
                  <label for="pass" className="label">Password</label>
                  <input className="input" type="password"
                    placeholder="Password"
                    onChange={(e) => {
                      setPasswordReg(e.target.value);
                    }}
                    required />
                </div>

                <div className="notification">
                  <p className="notify">{memberregisterStatus}</p>
                </div>

                <div className="hr"></div>
                <div className="foot-lnk">
                  <div className="group">
                    <input type="submit" className="button" value=" Register" />
                  </div>
                </div>
              </div>
            </form>

            <div className="sign-up-htm">
              <div className="group">
                <label for="user" className="label">Name</label>
                <input className="input" type="text"
                  placeholder="Name"
                  onChange={(e) => {
                    setNameReg(e.target.value);
                  }}
                  required />
              </div>
              <div className="group">
                <label for="pass" className="label">Phone</label>
                <input className="input" type="text"
                  placeholder="Phone"
                  onChange={(e) => {
                    setPhoneReg(e.target.value);
                  }}
                  required />
              </div>
              <div className="group">
                <label for="pass" className="label">Email</label>
                <input className="input" type="email"
                  placeholder="Email"
                  onChange={(e) => {
                    setEmailReg(e.target.value);
                  }}
                  required />
              </div>
              <div className="group">
                <label for="pass" className="label">Password</label>
                <input className="input" type="password"
                  placeholder="Password"
                  onChange={(e) => {
                    setPasswordReg(e.target.value);
                  }}
                  required />
              </div>
              <div className="group">
                <label for="pass" className="label">Shop Type</label>
                <input className="input" type="text"
                  placeholder="Shop Type"
                  onChange={(e) => {
                    setTypeReg(e.target.value);
                  }}
                  required />
              </div>

              <div className="notification">
                <p className="notify">{shopregisterStatus}</p>
              </div>

              <div className="hr"></div>
              <div className="foot-lnk">
                <div className="group">
                  <input type="submit" className="button" value=" Register" />
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>


    </>
  );
}
