import React, { useState } from "react";
import { Link } from "react-router-dom";
import { useHistory } from "react-router";
import axios from "axios";
import { useEffect } from "react";
import "./Promotionshop.css";
import { Loader } from "semantic-ui-react";
import { motion } from "framer-motion";

function Promotionshop() {
  //-----------------------------------------------
  const [active, setactive] = useState(true);
  //-----------------------------------------------
  const history = useHistory();
  const [shoplist, setShoplist] = useState([]);
  const [filteredShop, setFilteredShop] = useState(shoplist);
  //--------------search shop --------------//
  const shopSearch = (event) => {
    let value = event.toLowerCase();
    let result = [];
    result = shoplist.filter((data) => {
      return data.shop_name.search(value) != -1;
    });
    setFilteredShop(result);
  };
  //-----------------------------------------------

  const getpromotion = (shopname) => {
    history.push("/promotiondetails/" + shopname);
  };

  const gotomyshop = () => {
    history.push("/myshop");
  };

  /* ********************************** */

  const pageTransition = {
    in: {
      opacity: 1,
      x: 0,
    },
    out: {
      opacity: 0,
      x: 100,
    },
  };

  /* ********************************** */

  useEffect(() => {
    const timer = setTimeout(() => {
      setactive(false);
      axios.get("/login").then((response) => {
        if (response.data.loggedIn === true) {
          axios.get("/getshoplistpromotion").then((response) => {
            if (response) {
              console.log(response.data);
              setShoplist(response.data);
              setFilteredShop(response.data);
            }
          });
        }
        if (
          response.data.loggedIn === false ||
          response.data.user[0].role !== "shop"
        ) {
          return history.push("/login");
        }
      });
    }, 500);
    return () => clearTimeout(timer);
  }, []);

  return (
    <div>
      <nav className="navbar navbar-light bg-light justify-content-between">
        <Link className="buttonblack" to="/">
          <i className="fas fa-angle-left">
            <a className="bl">ย้อนกลับ</a>
          </i>
        </Link>
        <div className="div-img-logo2">
          <img className="img-logoo" src="/images/logo.png" />
        </div>
      </nav>

      {/* แต่งไว้หน้า promotion */}
      <div className="toppic-promotin">
        <h2 className="h2promotionmember">โปรโมชั่นร้านค้า</h2>
      </div>

      <div
        className="shopbox-promotinnnnn"
        onClick={() => {
          gotomyshop();
        }}
      >
        <div>
          <button className="button-cotainerpromotionnnn">
            <i className="fas fa-store"></i>ร้านค้าของฉัน
          </button>
        </div>
      </div>

      {/* แต่งไว้หน้า promotion */}
      <div className="divinsearch-1">
        <input
          className="insearch-divinsearch"
          type="text"
          placeholder="ค้นหาร้านค้า"
          onChange={(event) => shopSearch(event.target.value)}
        />
        <i className="fas fa-search"></i>
      </div>

      <motion.div
        initial="out"
        animate="in"
        exit="out"
        variants={pageTransition}
      >
        <Loader active={active} />
        {/* แต่งไว้หน้า promotion */}
        <div className="top-promotion">
          <div className="container-promotion">
            {filteredShop.map((val) => {
              return (
                <div
                  className="shopbox-promotin"
                  onClick={() => {
                    getpromotion(val.shop_name);
                  }}
                >
                  <div className="divimg-promotin">
                    <img
                      className="img-promotin"
                      src="images/shop.png"
                      style={{ height: "100px" }}
                    ></img>
                  </div>
                  <div className="boxpromotion">
                    <p className="p-promotin">ร้าน : {val.shop_name}</p>
                  </div>
                </div>
              );
            })}
          </div>
        </div>
      </motion.div>
    </div>
  );
}
export default Promotionshop;
