import React, { useState } from 'react';
import { Link, Route } from 'react-router-dom';
import axios from 'axios';
import { useHistory } from 'react-router';
import { useEffect } from 'react';
import './Menumember.css';

import Memberprofile from './Memberprofile';


function Menumember() {


    //----------------------------------------------------------
    const history = useHistory();
    const [point, setPoint] = useState();

    const logout = () => {
        axios.get("/logout");
        window.location.replace('/');
    }

    const refresh = () => {
        axios.get('/member');
        window.location.reload();
    }

    const profilepage = () => {
        history.push('/memberprofile')
    }

    const transferpage = () => {
        history.push('/transfer-point');
    }

    const transactionpage = () => {
        history.push('/transaction');
    }
    const promotionpage = () => {
        history.push('/promotion');
    }

    useEffect(() => {
        axios.get("/login").then((response) => {
            if (response.data.loggedIn == true) {
                setPoint(response.data.user[0].point);
            }
            if (response.data.loggedIn == false || response.data.user[0].role != 'member') {
                return (window.location.replace('/'));
            }
        });
    }, []);
    return (
        <>
            <link rel="stylesheet" type="text/css" href="//netdna.bootstrapcdn.com/font-awesome/4.1.0/css/font-awesome.min.css" />
            <link href="//maxcdn.bootstrapcdn.com/font-awesome/4.2.0/css/font-awesome.min.css" rel="stylesheet"></link>

            <nav class="navbar navbar-light bg-light justify-content-between">
                <a className="nav-tnc" ><i className="fas fa-braille"></i>T N C</a>
                <form class="form-inline">
                    <button onClick={logout} className="bn632-hover bn24">ออกจากระบบ</button>
                </form>

            </nav>

            <div className="head-frame">
                <div className="data-frame">
                    <div className="bg-circle">
                        <p className="point_acc">คะแนนสะสม</p>
                        <p className="point">{point}</p>
                        <i onClick={refresh} className="fas fa-sync-alt"></i>
                    </div>
                    <div className="profile-button">
                        <a onClick={profilepage} className="bn39"><span className="bn39span">ข้อมูลของฉัน</span></a>
                    </div>
                </div>
            </div>
            <hr />



            <div className="container bootstrap snippets bootdey">
                <div className="row margin-bottom-10">

                    <div className="col-md-4 col-sm-6">
                        <div onClick={transferpage} className="servive-block servive-block-yellow">
                            <i className='fas fa-exchange-alt'></i>
                            <h2 className="heading-md">โอนคะแนน</h2>
                        </div>
                    </div>
                    <div onClick={transactionpage} className="col-md-4 col-sm-6">
                        <div className="servive-block servive-block-purple">
                            <i className='fas fa-redo-alt'></i>
                            <h2 className="heading-md">รายการย้อนหลัง</h2>
                        </div>
                    </div>
                    <div onClick={promotionpage} className="col-md-4 col-sm-6">
                        <div className="servive-block rounded servive-block-aqua">
                            <i className='fas fa-tag'></i>
                            <h2 class="heading-md"> โปรโมชั่น</h2>
                        </div>
                    </div>
                </div>
            </div>


        </>

    );
}
export default Menumember;
