import React, { useState } from "react";
import { Link, useHistory } from "react-router-dom";
import Modal from "react-modal";
import axios from "axios";
import { Loader } from "semantic-ui-react";
import { useEffect } from "react";
import "./Transferpointshop.css";
import { AnimatePresence, motion } from "framer-motion";

const customStyles = {
  content: {
    top: "50%",
    left: "50%",
    right: "auto",
    bottom: "auto",
    marginRight: "-50%",
    transform: "translate(-50%, -50%)",
  },
};

function Transferpointshop() {
  let subtitle;
  const [modalIsOpenCheck, setIsOpenCheck] = useState(false);
  const [modalIsOpenConfirm, setIsOpenConfirm] = useState(false);
  const [destname, setDestName] = useState("");

  const [active, setactive] = useState(false); //loader

  function openModalCheck() {
    axios
      .post("/transfer/check", {
        phone: destPhone,
      })
      .then((response) => {
        if (response.data.message) {
          alert(response.data.message);
        } else if (response.data) {
          setDestName(response.data[0].name);
          setIsOpenCheck(true);
        }
      });
  }

  function openModalConfirm() {
    setactive(true);
    const timer = setTimeout(() => {
    axios
      .put("/shopPointTransfer", {
        id: id,
        phone: phone,
        destphone: destPhone,
        amount: amount,
      })
      .then((response) => {
        if (response.data.message) {
          alert(response.data.message);
        } else if (response.data.loggedIn === true) {
          setIsOpenConfirm(true);
          setPoint(response.data.user[0].point);
        }
      });
      setactive(false);
    }, 4000);
    return () => clearTimeout(timer);
  }

  function afterOpenModal() {
    // references are now sync'd and can be accessed.
    subtitle.style.color = "black";
  }

  function closeModal() {
    setIsOpenCheck(false);
    setIsOpenConfirm(false);
  }
  //---------------------------------------------------
  axios.defaults.withCredentials = true;

  const history = useHistory();
  const [id, setId] = useState();
  const [point, setPoint] = useState();
  const [name, setName] = useState("");
  const [phone, setPhone] = useState("");
  const [destPhone, setdestPhone] = useState("");
  const [amount, setamount] = useState();
  const [status, setStatus] = useState("");

  const checkinput = () => {
    if (!destPhone) {
      setStatus("เบอร์ปลายทางไม่ถูกต้อง");
    } else if (phone === destPhone) {
      setStatus("เบอร์ปลายทางไม่ถูกต้อง");
    } else if (!amount) {
      setStatus("โปรดป้อนจำนวนคะแนน");
    } else if (amount > point) {
      setStatus("คะแนนไม่เพียงพอสำหรับการโอน");
    } else if (amount < 1) {
      setStatus("คะแนนไม่ถูกต้อง");
    } else {
      setStatus("");
      openModalCheck();
    }
  };

  /* ********************************** */

  const pageTransition = {
    in: {
      opacity: 1,
      x: 0,
    },
    out: {
      opacity: 0,
      x: 100,
    },
  };

  /* ********************************** */
  useEffect(() => {
    axios.get("/login").then((response) => {
      if (response.data.loggedIn === true) {
        setId(response.data.user[0].id);
        setName(response.data.user[0].name);
        setPoint(response.data.user[0].point);
        setPhone(response.data.user[0].phone);
      }
      if (
        response.data.loggedIn === false ||
        response.data.user[0].role !== "shop"
      ) {
        return history.push("/login");
      }
    });
  }, []);

  return (
    <div>
      <div className="transferpointshop">
        <Modal
          isOpen={modalIsOpenCheck}
          onAfterOpen={afterOpenModal}
          onRequestClose={closeModal}
          style={customStyles}
          contentLabel="Example Modal"
        >
          <motion.div initial={{ opacity: 0, y: 100 }} animate={{ opacity: 1, y: 0 }} exit={{ opacity: 0, y: 100 }}>
            <div className="div-transferpointshop">
              <h2
                className="h2transferpointshop"
                ref={(_subtitle) => (subtitle = _subtitle)}
              >
                ตรวจสอบข้อมูล
              </h2>


              <h3 className="h3transfershop">จาก : {name}</h3>

              <h3 className="h3transfershop">โอนคะแนนให้ : {destname}</h3>

              <h3 className="h3transfershop">จำนวนคะแนน : {amount} คะแนน</h3>

              <div>
                <button
                  className="buttoncalculate-modal1"
                  onClick={openModalConfirm}
                >
                  ยืนยัน
                </button>
                <button className="buttoncalculate-modal2" onClick={closeModal}>
                  ยกเลิก
                </button>
              </div>
            </div>
          </motion.div>
        </Modal>
      </div>

      <div className="div-transferpointshop">
        <Loader active={active} />
        <Modal
          isOpen={modalIsOpenConfirm}
          onAfterOpen={afterOpenModal}
          onRequestClose={closeModal}
          style={customStyles}
          contentLabel="Example Modal"
        >
          <motion.div initial={{ opacity: 0, y: 100 }} animate={{ opacity: 1, y: 0 }} exit={{ opacity: 0, y: 100 }}>
            <h2
              className="h2h2transferpointshop"
              ref={(_subtitle) => (subtitle = _subtitle)}
            >
              <i className="fas fa-check-circle"></i>โอนคะแนนสำเร็จ
            </h2><hr />
            {/*แต่งไว้หน้า transferpoint */}
            <h2 className="h2modaltransfershop">ผู้โอน : {name}</h2>
            <img className="img-down" src="images/down.png"></img>
            <h2 className="h2modaltransfershop">ผู้รับ : {destname}</h2><hr />
            <h2 className="h2modaltransfershop-amount">จำนวนคะแนน : {amount} คะแนน</h2>

            <div className="div-buttontranfer-point-1">
              <button className="buttontranfer-point-1" onClick={closeModal}>
                กลับหน้าหลัก
              </button>
            </div>
          </motion.div>
        </Modal>
      </div>

      <nav className="navbar navbar-light bg-light justify-content-between">
        <Link className="buttonblack" to="/">
          <i className="fas fa-angle-left">
            <a className="bl">ย้อนกลับ</a>
          </i>
        </Link>
        <div className="div-img-logo2">
          <img className="img-logoo" src="/images/logo.png" />
        </div>
      </nav>

      <motion.div
        initial="out"
        animate="in"
        exit="out"
        variants={pageTransition}
      >
        {/* แต่งไว้หน้า tranfermember */}
        <div className="col-lg-6-card-tranfermember">
          <form className="card-tranfermember">
            <div className="card-details">
              <i className="fas fa-comments-dollar"></i>
              <h3 className="title-tranfermember">โอนคะแนน</h3>
              <div className="row-card-tranfermember">
                <div className="form-group col-sm-7">
                  <div className="inner-addon right-addon">
                    <label for="card-holder" className="card-holder-n">
                      เบอร์โทรศัพท์
                    </label>

                    <input
                      id="card-holder"
                      type="text"
                      className="input-input1-transferpoint"
                      placeholder="09xxxxxxxx"
                      aria-label="Phone"
                      aria-describedby="basic-addon1"
                      onChange={(e) => {
                        setdestPhone(e.target.value);
                      }}
                      required
                    />
                  </div>
                </div>

                <div className="form-group col-sm-7">
                  <div className="inner-addon right-addon">
                    <label for="card-number" className="card-holder-n">
                      จำนวนคะแนน
                    </label>

                    <input
                      id="card-number"
                      type="number"
                      className="input-input1-transferpoint"
                      placeholder="0"
                      aria-label="amount"
                      aria-describedby="basic-addon1"
                      onChange={(e) => {
                        setamount(e.target.value);
                      }}
                      required
                    />
                  </div>
                </div>

                <div className="form-group-btn-block-m">
                  <p className="p-form-group-btn" style={{ color: "#af0527" }}>
                    {status}
                  </p>
                  <button
                    type="button"
                    className="btn-block-m"
                    onClick={checkinput}
                  >
                    ตรวจสอบข้อมูล
                  </button>
                </div>
              </div>
            </div>
          </form>
        </div>
        {/*<div className='transferpointshop'>

                <h1>โอนคะแนน</h1>
                <h1>phone : {phone}</h1>
                <h2>Point : {point}</h2>
                <h1>โอนคะแนน</h1>
                <label>กรอกเบอร์</label>
                <input
                    type="text"
                    onChange={(e) => {
                        setdestPhone(e.target.value);
                    }} />

                <label>กรอกจำนวนคะแนน</label>
                <input
                    type="number" min="0"
                    onChange={(e) => {
                        setamount(e.target.value);
                    }}
                />
                <button onClick={transferSubmit}>ยืนยัน</button>

                </div>*/}
      </motion.div>
    </div>
  );
}
export default Transferpointshop;
