import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import { useEffect } from 'react';
import './Shopprofile.css';


function Shopprofile() {

  //----------------------------------------------
  const [id, setId] = useState();
  const [name, setName] = useState("");
  const [phone, setPhone] = useState("");
  const [email, setEmail] = useState("");
  const [point, setPoint] = useState();

  const [statusName, setStatusName] = useState("");
  const [statusPhone, setStatusPhone] = useState("");

  const [newname, setnewName] = useState("");
  const [newphone, setnewPhone] = useState("");

  axios.defaults.withCredentials = true;
  const logout = () => {
    axios.get("/logout");
    window.location.replace('/');
  }
  const refresh = () => {
    axios.get("/shop");
    window.location.replace('/shopprofile');
  }
  const nameUpdate = () => {
    if (newname == "") {
      setStatusName("ชื่อไม่ถูกต้อง");
    } else {
      axios.put("/updateshopName", { name: newname, id: id }).then((response) => {
        setStatusName("บันทึกสำเร็จ");
        return setName(response.data.user[0].name);
      });
    }
  }
  const phoneUpdate = () => {
    if (newphone == "") {
      setStatusPhone("เบอร์โทรไม่ถูกต้อง");
    } else {
      axios.put("/updateshopPhone", { phone: newphone, id: id }).then((response) => {
        if (response.data.message) {
          return setStatusPhone(response.data.message);
        }
        setStatusPhone("บันทึกสำเร็จ");
        setPhone(response.data.user[0].phone);
      });
    }
  }

  useEffect(() => {
    axios.get("/login").then((response) => {
      if (response.data.loggedIn == true) {
        setId(response.data.user[0].id);
        setName(response.data.user[0].name);
        setPhone(response.data.user[0].phone);
        setEmail(response.data.user[0].email);
        setPoint(response.data.user[0].point);
      }
      if (response.data.loggedIn == false || response.data.user[0].role != 'shop') {
        return (window.location.replace('/'));
      }
    });
  }, []);

  return (
    <>
      <nav class="navbar navbar-light bg-light justify-content-between">
        <Link to="/"  ><i className="fas fa-angle-left"><a className="bl">ย้อนกลับ</a></i></Link>
      </nav>

      <div className="container bootstrap snippets bootdey">
        <div className="panel-body inf-content">
          <div className="row">
            <div className="col-md-4">
              <img alt="" title="" className="img-circle img-thumbnail isTooltip" src="https://bootdey.com/img/Content/avatar/avatar7.png" data-original-title="Usuario" />
              <ul title="Ratings" className="list-inline ratings text-center">
                <li><a href="#"><span className="glyphicon glyphicon-star"></span></a></li>
                <li><a href="#"><span className="glyphicon glyphicon-star"></span></a></li>
                <li><a href="#"><span className="glyphicon glyphicon-star"></span></a></li>
                <li><a href="#"><span className="glyphicon glyphicon-star"></span></a></li>
                <li><a href="#"><span className="glyphicon glyphicon-star"></span></a></li>
              </ul>
            </div>
            <div className="col-md-6">
              <strong>ข้อมูลส่วนตัว</strong><br />
              <div className="table-responsive">
                <table className="table table-user-information">
                  <tbody>
                    <tr>
                      <td>
                        <strong>
                          <span className="glyphicon glyphicon-asterisk text-primary"></span>
                          เลขบัตรประจำตัวประชาชน
                        </strong>
                      </td>
                      <td className="text-primary">
                        {national_id} <i onClick={openModalid} className="fas fa-pen"></i>
                      </td>
                    </tr>
                    <tr>
                      <td>
                        <strong>
                          <span className="glyphicon glyphicon-user  text-primary"></span>
                          ชื่อ-สกุล
                        </strong>
                      </td>
                      <td className="text-primary">
                        {name} <i onClick={openModalname} className="fas fa-pen"></i>
                      </td>
                    </tr>

                    <tr>
                      <td>
                        <strong>
                          <span className="glyphicon glyphicon-bookmark text-primary"></span>
                          เบอร์โทรศัพท์
                        </strong>
                      </td>
                      <td className="text-primary">
                        {phone} <i onClick={openModalphone} className="fas fa-pen"></i>
                      </td>
                    </tr>



                    <tr>
                      <td>
                        <strong>
                          <span className="glyphicon glyphicon-envelope text-primary"></span>
                          อีเมล์
                        </strong>
                      </td>
                      <td className="text-primary">
                        {email} <i onClick={openModalemail} className="fas fa-pen"></i>
                      </td>
                    </tr>

                    <tr>
                      <td>
                        <strong>
                          <span className="glyphicon glyphicon-envelope text-primary"></span>
                          คะแนน
                        </strong>
                      </td>
                      <td className="text-primary">
                        {point}
                      </td>
                    </tr>


                    <tr>
                      <td>
                        <strong>
                          <span className="glyphicon glyphicon-eye-open text-primary"></span>
                          สถานะ
                        </strong>
                      </td>
                      <td className="text-primary">
                        {role}
                      </td>
                    </tr>
                  </tbody>
                </table>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/*}
      <div >
        <h1>Shop</h1>
        <p>Hi  {name} </p>
        <p>Phone : {phone} <br />Email : {email} <br />Point :  {point} <button onClick={refresh}>refresh</button></p>
        <br />
        <input type="text" placeholder="name"
          onChange={(e) => { setnewName(e.target.value) }
          }></input>
        {statusName}
        <button className="btn" onClick={nameUpdate}>update</button>

        <input type="text" placeholder="Phone"
          onChange={(e) => { setnewPhone(e.target.value) }
          }></input>
        {statusPhone}
        <button className="btn" onClick={phoneUpdate}>update</button>

        <br /><button className="logout" onClick={logout}>Logout</button>
        </div>*/}

    </>

  );
}
export default Shopprofile;
