import React, { useEffect } from 'react'
import axios from 'axios'
import { useState } from 'react'
import { useHistory } from 'react-router-dom'
import './Admin.css'
import 'semantic-ui-css/semantic.min.css'
import { Icon } from 'semantic-ui-react'
import { Loader } from 'semantic-ui-react'
import moment from 'moment'
import { AnimatePresence, motion } from 'framer-motion'
import Modal from 'react-modal'

// Be sure to include styles at some point, probably during your bootstraping
import '@trendmicro/react-sidenav/dist/react-sidenav.css'

const customStyles = {
  content: {
    width: '100%',
    top: '50%',
    left: '50%',
    right: 'auto',
    bottom: 'auto',
    marginRight: '-50%',
    transform: 'translate(-50%, -50%)',
  },
}

export default function Admin() {
  const history = useHistory()
  const location = useHistory()
  const [active, setactive] = useState(true) //loader
  //-------------------------------------------
  const [name, setname] = useState('')
  const [point, setpoint] = useState()
  const [pendingpromotion, setpendingpromotion] = useState([])

  const [system_coin, setSystemCoin] = useState()
  const [err_system_coin, setErrSystemCoin] = useState()

  const [Txdata, setTxdata] = useState([])
  const [checkTxid, setcheckTxid] = useState({})

  const [checkTx_error, setcheckTx_error] = useState([])
  const [member, setmember] = useState([])
  const [shop, setshop] = useState([])

  const [member_error, setmember_error] = useState([])
  const [shop_error, setshop_error] = useState([])

  const [keys, setkeys] = useState('')
  axios.defaults.withCredentials = true
  //-------------------------------------------
  let subtitle
  const [modalIsOpen, setIsOpen] = React.useState(false)

  function openModal() {
    setIsOpen(true)
  }

  function afterOpenModal() {
    // references are now sync'd and can be accessed.
    // subtitle.style.color = '#000'
  }

  function closeModal() {
    setIsOpen(false)
  }

  const logout = () => {
    axios.get('/logout')
    history.go()
    //history.push("/");
  }

  const homepage = () => {
    history.go()
  }

  const acceptpromotion = (a, b, c, d, e, f, g, h) => {
    axios
      .post('/acceptpromotionpoint', {
        req_id: a,
        shop_name: b,
        product_name: c,
        product_code: d,
        amount_point: e,
        picture: f,
        category: g,
        expired: h,
      })
      .then((response) => {
        if (response.data.message) {
          alert(response.data.message)
          history.go()
        }
      })
  }

  const disacceptpromotion = (id) => {
    axios.put(`/disacceptpromotion/${id}`).then((response) => {
      if (response.data.message) {
        alert(response.data.message)
        history.go()
      }
    })
  }

  const checktransaction = () => {
    axios.get('/checktransaction/' + keys).then((response) => {
      if (response.data.success) {
        setTxdata(response.data.success)
      } else if (response.data.message) {
        setTxdata([])
        alert('ไม่มีทรานแซคชันในระบบ')
      }
    })
  }

  const checkidtransaction = (id) => {
    axios.get('/checkidtransaction/' + id).then((response) => {
      if (response) {
        setcheckTxid(response.data)
        console.log(checkTxid)
      }
    })
  }

  const refreshtransaction = () => {
    axios.get('/refreshTx').then((response) => {
      if (response.data.success) {
        setTxdata(response.data.success)
      }
    })
  }
  //check err member and shop
  const checkErrorMember = () => {
    axios.get('/checkerror').then((response) => {
      if (response.data.Tx_check_error) {
        setcheckTx_error(response.data.Tx_check_error)
      }
    })
    axios.get('/getusermember').then((response) => {
      if (response.data.member) {
        setmember(response.data.member)
      }
    })

    checkErrorUserMember()
  }

  const checkErrorUserMember = () => {
    var withdraw
    var deposit
    if (member) {
      for (var i = 0; i < `${member.length}`; i++) {
        withdraw = 0
        deposit = 0
        for (var j = 0; j < `${checkTx_error.length}`; j++) {
          // console.log(member[i].phone + ' ' + checkTx_error[j].transferor)
          if (member[i].phone === checkTx_error[j].transferor) {
            withdraw += checkTx_error[j].amount
          } else if (member[i].phone === checkTx_error[j].recipient) {
            deposit += checkTx_error[j].amount
          }
        }
        // console.log(member[i].point)
        // console.log(deposit - withdraw)
        //check คะแนนใน block เทียบกับ DB ของ member
        if (deposit - withdraw !== member[i].point) {
          setmember_error([
            {
              name: member[i].name,
              phone: member[i].phone,
              pointDB: member[i].point,
              pointBC: deposit - withdraw,
              role: member[i].role,
            },
            ...member_error,
          ])
        }
      }
    }
  }

  const checkErrorShop = () => {
    axios.get('/checkerror').then((response) => {
      if (response.data.Tx_check_error) {
        setcheckTx_error(response.data.Tx_check_error)
      }
    })
    axios.get('/getusershop').then((response) => {
      if (response.data.shop) {
        setshop(response.data.shop)
      }
    })
    checkErrorUserShop()
  }

  const checkErrorUserShop = () => {
    var withdraw
    var deposit
    if (shop) {
      for (var i = 0; i < `${shop.length}`; i++) {
        withdraw = 0
        deposit = 0
        for (var j = 0; j < `${checkTx_error.length}`; j++) {
          // console.log(member[i].phone + ' ' + checkTx_error[j].transferor)
          if (shop[i].phone === checkTx_error[j].transferor) {
            withdraw += checkTx_error[j].amount
          } else if (shop[i].phone === checkTx_error[j].recipient) {
            deposit += checkTx_error[j].amount
          }
        }
        // console.log(member[i].point)
        // console.log(deposit - withdraw)
        //check คะแนนใน block เทียบกับ DB ของ member
        if (deposit - withdraw !== shop[i].point + shop[i].shoppoint) {
          setshop_error([
            {
              name: shop[i].name,
              phone: shop[i].phone,
              pointDB: shop[i].point,
              shoppointDB: shop[i].shoppoint,
              pointBC: deposit - withdraw,
              role: shop[i].role,
            },
            ...shop_error,
          ])
        }
      }
    }
  }

  /* ********************************** */

  const pageTransition = {
    in: {
      opacity: 1,
      x: 0,
    },
    out: {
      opacity: 0,
      x: '-100vh',
    },
  }

  /* ********************************** */

  useEffect(() => {
    const timer = setTimeout(() => {
      setactive(false)
      axios.get('/login').then((response) => {
        if (
          response.data.loggedIn === true &&
          response.data.user[0].role === 'admin'
        ) {
          axios.get('/admin').then((response) => {
            setname(response.data.user[0].name)
            setpoint(response.data.user[0].point)
          })
          axios.get('/getpromotionrequest').then((response) => {
            if (response.data) {
              setpendingpromotion(response.data)
            }
          })
        } else {
          history.push('/login')
        }
      })
      axios.get('/checksystemcoin').then((response) => {
        if (response.data.err) {
          setSystemCoin(response.data.system_coin)
          setErrSystemCoin(response.data.err)
        } else {
          // console.log(response.data.normal)
          setSystemCoin(response.data.normal)
          setErrSystemCoin(0)
        }
      })
      axios.get('/refreshTx').then((response) => {
        if (response.data.success) {
          console.log(response.data.success)
          setTxdata(response.data.success)
        }
      })
    }, 2000)
    return () => clearTimeout(timer)
  }, [])

  return (
    <div className="adminpage">
      <motion.div
        initial="out"
        animate="in"
        exit="out"
        variants={pageTransition}
      >
        <Loader active={active} />

        <div className="container mt-4">
          <div className="row">
            <div className="adminnav my-lg-2 my-md-1">
              <div id="sidebar" className="bg-purple">
                <div className="h4 text-white">Hi {name} [Admin]</div>
                <ul>
                  <li className="active" onClick={homepage}>
                    {' '}
                    <a
                      href="#"
                      className="text-decoration-none d-flex align-items-start"
                    >
                      <div className="fas fa-home pt-2 me-3"></div>
                      <div className="d-flex flex-column">
                        <div className="link">Home</div>
                        <div className="link-desc">ตรวจสอบข้อมูลต่างๆ</div>
                      </div>
                    </a>{' '}
                  </li>
                  <li className="active" onClick={logout}>
                    <a
                      href="#"
                      className="text-decoration-none d-flex align-items-start"
                    >
                      <div className="fas fa-arrow-circle-right pt-2 me-3"></div>
                      <div className="d-flex flex-column">
                        <div className="link">Logout</div>
                        <div className="link-desc">ออกจากระบบ</div>
                      </div>
                    </a>
                  </li>
                </ul>
              </div>
            </div>

            <div className="modaladmin">
              <Modal
                isOpen={modalIsOpen}
                onAfterOpen={afterOpenModal}
                onRequestClose={closeModal}
                style={customStyles}
                contentLabel="Example Modal"
              >
                <h2 className="h2h2h2memberprofile">ตรวจสอบคะแนน Blockchain</h2>

                <div className="table-wrapper-scroll-y my-custom-scrollbar">
                  <div className="px-0 bg-light">
                    <div className="table-responsive px-2">
                      <table className="table table-borderless">
                        <thead>
                          <tr>
                            <th className="th-table2" scope="col">
                              คนโอน
                            </th>
                            <th className="th-table2" scope="col">
                              คนรับ
                            </th>
                            <th className="th-table2" scope="col">
                              จำนวน
                            </th>
                          </tr>
                        </thead>

                        {Txdata.map((val) => {
                          return (
                            <tbody>
                              <tr>
                                <td>
                                  <p className="bg-blight">{val.transferor}</p>
                                </td>
                                <td>
                                  <p className="bg-bdark">{val.recipient}</p>
                                </td>
                                <td>
                                  <p className="bg-blight">{val.amount}</p>
                                </td>
                              </tr>
                            </tbody>
                          )
                        })}
                      </table>
                    </div>
                  </div>
                </div>
              </Modal>
            </div>

            <div className="my-lg-2 my-1">
              <div id="main-content" className="bg-white border">
                <div className="d-flex flex-column">
                  <div className="h5-Das">Dashboard</div>
                </div>
                <div className="d-flex my-4 flex-wrap">
                  <div className="box me-4 my-1 bg-light">
                    <i class="fas fa-wallet"></i>
                    <div className="d-flex align-items-center mt-2">
                      <div className="tag">Admin Point</div>
                      <div className="ms-auto number">{point}</div>
                    </div>
                  </div>

                  <div className="box me-4 my-1 bg-light">
                    <i class="fab fa-bitcoin"></i>
                    <div className="d-flex align-items-center mt-2">
                      <div className="tag">System Point</div>
                      <div className="ms-auto number">{system_coin}</div>
                    </div>
                  </div>

                  <div className="box me-4 my-1 bg-light">
                    <i class="fas fa-exclamation-triangle"></i>
                    <div className="d-flex align-items-center mt-2">
                      <div className="tag">Error Point</div>
                      <div className="ms-auto number">{err_system_coin}</div>
                    </div>
                  </div>
                </div>
              </div>
            </div>

            <div className="my-lg-2 my-1">
              <div id="main-content" className="bg-white border">
                <div className="text-uppercase">อนุมัติโปรโมชัน</div>

                {/* ตาราง */}

                <div className="table-wrapper-scroll-y my-custom-scrollbar">
                  <div className="px-0 bg-light">
                    <div className="table-responsive px-2">
                      <table className="table table-borderless">
                        <thead>
                          <tr>
                            <th className="th-table2" scope="col">
                              request_id
                            </th>
                            <th className="th-table2" scope="col">
                              shop_name
                            </th>
                            <th className="th-table2" scope="col">
                              product_name
                            </th>
                            <th className="th-table2" scope="col">
                              product_code
                            </th>
                            <th className="th-table2" scope="col">
                              point
                            </th>
                            <th className="th-table2" scope="col">
                              category
                            </th>
                            <th className="th-table2" scope="col">
                              expired
                            </th>
                            <th className="th-table2" scope="col">
                              ตัวเลือก
                            </th>
                          </tr>
                        </thead>

                        {pendingpromotion.map((show) => {
                          return (
                            <tbody>
                              <tr>
                                <td>
                                  <p className="bg-blight">{show.id}</p>
                                </td>
                                <td>
                                  <p className="bg-bdark">{show.shop_name}</p>
                                </td>
                                <td>
                                  <p className="bg-blight">
                                    {show.product_name}
                                  </p>
                                </td>
                                <td>
                                  <p className="bg-bdark">
                                    {show.product_code}
                                  </p>
                                </td>
                                <td>
                                  <p className="bg-blight">
                                    {show.amount_point}
                                  </p>
                                </td>
                                <td>
                                  <p className="bg-bdark">{show.category}</p>
                                </td>
                                <td>
                                  <p className="bg-blight">
                                    {moment(show.expired).format('DD/MM/YYYY')}
                                  </p>
                                </td>
                                <td>
                                  <div className="div12345">
                                    <button
                                      className="buttonadmin1234"
                                      onClick={() => {
                                        acceptpromotion(
                                          show.id,
                                          show.shop_name,
                                          show.product_name,
                                          show.product_code,
                                          show.amount_point,
                                          show.picture,
                                          show.category,
                                          show.expired,
                                        )
                                      }}
                                    >
                                      อนุมัติ
                                    </button>
                                    <button
                                      className="buttonadmin12345"
                                      onClick={() => {
                                        disacceptpromotion(show.id)
                                      }}
                                    >
                                      ไม่อนุมัติ
                                    </button>
                                  </div>
                                </td>
                              </tr>
                            </tbody>
                          )
                        })}
                      </table>
                    </div>
                  </div>
                </div>
              </div>
            </div>

            <div className="my-lg-2 my-1">
              <div id="main-content" className="bg-white border">
                <div className="text-uppercase1">ตรวจสอบ Transaction</div>
                <p
                  onClick={refreshtransaction}
                  className="p-refreshtransaction"
                >
                  refreshtransaction
                </p>
                {checkTxid.transferor}
                <div className="divinsearch-1">
                  <input
                    className="insearch-divinsearch"
                    placeholder="Tx-id หรือ เบอร์โทรศัพท์"
                    onChange={(e) => {
                      setkeys(e.target.value)
                    }}
                  ></input>
                  <i
                    type="summit"
                    className="fas fa-search"
                    onClick={checktransaction}
                  ></i>
                </div>

                <div className="table-wrapper-scroll-y my-custom-scrollbar">
                  <div className="px-0 bg-light">
                    <div className="table-responsive px-2">
                      <table className="table table-borderless">
                        <thead>
                          <tr>
                            <th className="th-table2" scope="col">
                              รหัสอ้างอิง
                            </th>
                            <th className="th-table2" scope="col">
                              เวลา
                            </th>
                            <th className="th-table2" scope="col">
                              คนโอน
                            </th>
                            <th className="th-table2" scope="col">
                              คนรับ
                            </th>
                            <th className="th-table2" scope="col">
                              จำนวน
                            </th>
                          </tr>
                        </thead>

                        {Txdata.map((val) => {
                          return (
                            <tbody>
                              <tr>
                                <td>
                                  <p
                                    className="bg-blight123"
                                    onClick={() => {
                                      checkidtransaction(val.Tx_id)
                                      openModal()
                                    }}
                                  >
                                    {val.Tx_id}
                                  </p>
                                </td>
                                <td>
                                  <p className="bg-bdark">
                                    {moment(val.time).format(
                                      'DD/MMMM/YYYY - LT',
                                    )}
                                  </p>
                                </td>
                                <td>
                                  <p className="bg-blight">{val.transferor}</p>
                                </td>
                                <td>
                                  <p className="bg-bdark">{val.recipient}</p>
                                </td>
                                <td>
                                  <p className="bg-blight">{val.amount}</p>
                                </td>
                              </tr>
                            </tbody>
                          )
                        })}
                      </table>
                    </div>
                  </div>
                </div>
              </div>
            </div>

            <div className="my-lg-2 my-1">
              <div id="main-content" className="bg-white border">
                <div className="text-uppercase1">ตรวจสอบข้อผิดพลาด Member</div>
                <button onClick={checkErrorMember} className="buttonadmin12345">
                  check
                </button>
                {/* ตาราง */}

                <div className="table-wrapper-scroll-y my-custom-scrollbar">
                  <div className="px-0 bg-light">
                    <div className="table-responsive px-2">
                      <table className="table table-borderless">
                        <thead>
                          <tr>
                            <th className="th-table2" scope="col">
                              ชื่อ-สกุล
                            </th>
                            <th className="th-table2" scope="col">
                              เบอร์โทรศัพท์
                            </th>
                            <th className="th-table2" scope="col">
                              คะแนนรวมในฐานข้อมูล
                            </th>
                            <th className="th-table2" scope="col">
                              คะแนนในบล็อกเชน
                            </th>
                            <th className="th-table2" scope="col">
                              สถานะ
                            </th>
                          </tr>
                        </thead>
                        {member_error.map((val) => {
                          return (
                            <tbody>
                              <tr>
                                <td>
                                  <p className="bg-blight">{val.name}</p>
                                </td>
                                <td>
                                  <p className="bg-bdark">{val.phone}</p>
                                </td>
                                <td>
                                  <p className="bg-blight">{val.pointDB}</p>
                                </td>
                                <td>
                                  <p className="bg-bdark">{val.pointBC}</p>
                                </td>
                                <td>
                                  <p className="bg-blight">{val.role}</p>
                                </td>
                              </tr>
                            </tbody>
                          )
                        })}
                      </table>
                    </div>
                  </div>
                </div>

                <div className="text-uppercase1">ตรวจสอบข้อผิดพลาด Shop</div>
                <button onClick={checkErrorShop} className="buttonadmin12345">
                  check
                </button>
                {/* ตาราง */}

                <div className="table-wrapper-scroll-y my-custom-scrollbar">
                  <div className="px-0 bg-light">
                    <div className="table-responsive px-2">
                      <table className="table table-borderless">
                        <thead>
                          <tr>
                            <th className="th-table2" scope="col">
                              ชื่อร้านค้า
                            </th>
                            <th className="th-table2" scope="col">
                              เบอร์โทรศัพท์
                            </th>
                            <th className="th-table2" scope="col">
                              คะแนนส่วนตัว
                            </th>
                            <th className="th-table2" scope="col">
                              คะแนนร้านค้า
                            </th>
                            <th className="th-table2" scope="col">
                              คะแนนรวมในฐานข้อมูล
                            </th>
                            <th className="th-table2" scope="col">
                              คะแนนในบล็อกเชน
                            </th>
                            <th className="th-table2" scope="col">
                              สถานะ
                            </th>
                          </tr>
                        </thead>
                        {shop_error.map((val) => {
                          return (
                            <tbody>
                              <tr>
                                <td>
                                  <p className="bg-blight">{val.name}</p>
                                </td>
                                <td>
                                  <p className="bg-bdark">{val.phone}</p>
                                </td>
                                <td>
                                  <p className="bg-blight">{val.pointDB}</p>
                                </td>
                                <td>
                                  <p className="bg-bdark">{val.shoppointDB}</p>
                                </td>
                                <td>
                                  <p className="bg-bdark">
                                    {val.pointDB + val.shoppointDB}
                                  </p>
                                </td>
                                <td>
                                  <p className="bg-bdark">{val.pointBC}</p>
                                </td>
                                <td>
                                  <p className="bg-blight">{val.role}</p>
                                </td>
                              </tr>
                            </tbody>
                          )
                        })}
                      </table>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </motion.div>
    </div>
  )
}
