import React, { useEffect } from 'react';
import axios from 'axios';
import {useState} from 'react';
import Registeration from '../pages/Registeration';
import { Link } from 'react-router-dom';
import { Redirect } from 'react-router';
import { response } from 'express';

export default function Member() {
    const [id,setId] = useState();
    const [name, setName] = useState("");
    const [phone, setPhone] = useState("");
    const [email, setEmail] = useState("");
    const [point, setPoint] = useState();

    const [newname, setnewName] = useState("");
    const [newphone, setnewPhone] = useState("");
    const [newemail, setnewEmail] = useState("");

    axios.defaults.withCredentials = true;
    const logout = () => {
      axios.get("http://localhost:3001/logout");
      window.location.replace('/');
    }
    const refresh = () => {
      axios.get("http://localhost:3001/member");
      window.location.replace('/');
    }
    const nameUpdate = () => {
      axios.put("http://localhost:3001/updatememberName",{name: newname,id: id});
      axios.get("http://localhost:3001/member");
      window.location.replace('/');
    }
    const phoneUpdate = () => {
      axios.put("http://localhost:3001/updatememberPhone",{phone: newphone,id: id}).then((response) =>{
        if(response){
          console.log(response.message);
        }
      });

      axios.get("http://localhost:3001/member");
      window.location.replace('/');
    }
    const emailUpdate = () => {
      axios.put("http://localhost:3001/updatememberEmail",{email: newemail,id: id});
      axios.get("http://localhost:3001/member");
      window.location.replace('/');
    }
    useEffect(() => {
        axios.get("http://localhost:3001/login").then((response) => {
          if (response.data.loggedIn == true) {
              setId(response.data.user[0].id);
              setName(response.data.user[0].name);
              setPhone(response.data.user[0].phone);
              setEmail(response.data.user[0].email);
              setPoint(response.data.user[0].point);
          }
        });
      }, []);

    return (
        <div>
            <h1>member</h1>
            <p>Hi  {name} </p>
            <p>Phone : {phone} <br/>Email : {email} <br/>Point :  {point} <button onClick={refresh}>refresh</button></p>
            <br/>
            <input  type="text" placeholder="name"
              onChange={(e) => 
              {setnewName(e.target.value)}
              }></input>
              <button className="btn" onClick={nameUpdate}>update</button>

            <input  type="text" placeholder="Phone"
              onChange={(e) => 
              {setnewPhone(e.target.value)}
              }></input>
              <button className="btn" onClick={phoneUpdate}>update</button>

            <input  type="text" placeholder="Email"
              onChange={(e) => 
              {setnewEmail(e.target.value)}
              }></input>
              <button className="btn" onClick={emailUpdate}>update</button>


            <br/><button className="logout" onClick={logout}>Logout</button>
        </div>
    )
}
