import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import { useEffect } from 'react';
import './Shopprofile.css';


function Shopprofile() {

  //----------------------------------------------
  const [id, setId] = useState();
  const [name, setName] = useState("");
  const [phone, setPhone] = useState("");
  const [email, setEmail] = useState("");
  const [point, setPoint] = useState();

  const [statusName, setStatusName] = useState("");
  const [statusPhone, setStatusPhone] = useState("");

  const [newname, setnewName] = useState("");
  const [newphone, setnewPhone] = useState("");

  axios.defaults.withCredentials = true;
  const logout = () => {
    axios.get("/logout");
    window.location.replace('/');
  }
  const refresh = () => {
    axios.get("/shop");
    window.location.replace('/shopprofile');
  }
  const nameUpdate = () => {
    if (newname == "") {
      setStatusName("ชื่อไม่ถูกต้อง");
    } else {
      axios.put("/updateshopName", { name: newname, id: id }).then((response) => {
        setStatusName("บันทึกสำเร็จ");
        return setName(response.data.user[0].name);
      });
    }
  }
  const phoneUpdate = () => {
    if (newphone == "") {
      setStatusPhone("เบอร์โทรไม่ถูกต้อง");
    } else {
      axios.put("/updateshopPhone", { phone: newphone, id: id }).then((response) => {
        if (response.data.message) {
          return setStatusPhone(response.data.message);
        }
        setStatusPhone("บันทึกสำเร็จ");
        setPhone(response.data.user[0].phone);
      });
    }
  }

  useEffect(() => {
    axios.get("/login").then((response) => {
      if (response.data.loggedIn == true) {
        setId(response.data.user[0].id);
        setName(response.data.user[0].name);
        setPhone(response.data.user[0].phone);
        setEmail(response.data.user[0].email);
        setPoint(response.data.user[0].point);
      }
      if (response.data.loggedIn == false || response.data.user[0].role != 'shop') {
        return (window.location.replace('/'));
      }
    });
  }, []);

  return (
    <>
      <nav class="navbar navbar-light bg-light justify-content-between">
        <Link to="/"  ><i className="fas fa-angle-left"><a className="bl">ย้อนกลับ</a></i></Link>
      </nav>

      <div >
        <h1>Shop</h1>
        <p>Hi  {name} </p>
        <p>Phone : {phone} <br />Email : {email} <br />Point :  {point} <button onClick={refresh}>refresh</button></p>
        <br />
        <input type="text" placeholder="name"
          onChange={(e) => { setnewName(e.target.value) }
          }></input>
        {statusName}
        <button className="btn" onClick={nameUpdate}>update</button>

        <input type="text" placeholder="Phone"
          onChange={(e) => { setnewPhone(e.target.value) }
          }></input>
        {statusPhone}
        <button className="btn" onClick={phoneUpdate}>update</button>

        <br /><button className="logout" onClick={logout}>Logout</button>
      </div>


    </>

  );
}
export default Shopprofile;
