import axios from 'axios'
import React, { useState, useEffect } from 'react'
import { useHistory } from 'react-router-dom'
import { Link } from 'react-router-dom'
import './Addstock.css'
import { motion } from 'framer-motion'

function Addstock() {
  //-----------------------------------------------
  const history = useHistory()
  const [name, setName] = useState('')
  const [product_name, setProductName] = useState('')
  const [product_code, setProductCode] = useState('')
  const [price, setPrice] = useState()
  const [category, setCategory] = useState('')

  const [statusUpload, setStatusUpload] = useState('')

  const [file, setFile] = useState('')
  const [UploadFile, setUploadFile] = useState('')
  //-----------------------------------------------
  const addproductstock = () => {
    if (
      !product_name ||
      !product_code ||
      !price ||
      !file ||
      !category ||
      category === 'null'
    ) {
      setStatusUpload('กรุณาใส่ข้อมูลให้ครบทุกช่อง')
    } else if (file.size > 2097152) {
      setStatusUpload('ไฟล์ภาพมีขนาดเกิน 2 MB')
    } else {
      setStatusUpload('')
      const formData = new FormData()
      formData.append('file', file)
      axios
        .post('/addproductstock', {
          shop_name: name,
          product_name: product_name,
          product_code: product_code,
          price: price,
          picture: '/upload/' + file.name,
          category: category,
        })
        .then((response) => {
          if (response.data.message) {
            setStatusUpload(response.data.message)
          } else if (response.data.success) {
            axios
              .post('/upload', formData, {
                headers: {
                  'Content-Type': 'multipart/form-data',
                },
              })
              .then((response) => {
                if (response) {
                  const { fileName, filePath } = response.data
                  setUploadFile({ fileName, filePath })
                }
              })
            alert(response.data.success)
            history.go()
          }
        })
    }
  }

  /* ********************************** */

  const pageTransition = {
    in: {
      opacity: 1,
      x: 0,
    },
    out: {
      opacity: 0,
      x: 100,
    },
  }

  /* ********************************** */

  useEffect(() => {
    axios.get('/login').then((response) => {
      if (response.data.loggedIn === true) {
        setName(response.data.user[0].name)
      }
      if (
        response.data.loggedIn === false ||
        response.data.user[0].role !== 'shop'
      ) {
        return history.push('/login')
      }
    })
  }, [])

  return (
    <div className="Addstockpage">
      <nav className="navbar navbar-light bg-light justify-content-between">
        <Link className="buttonblack" to="/product-stockpage">
          <i className="fas fa-angle-left">
            <a className="bl">ย้อนกลับ</a>
          </i>
        </Link>
        <div className="div-img-logo2">
          <img className="img-logoo" src="/images/logo.png" />
        </div>
      </nav>

      <motion.div
        initial="out"
        animate="in"
        exit="out"
        variants={pageTransition}
      >
        <div className="divh1-register-page">
          <img
            className="div-img-register-page"
            src="/images/add-to-basket.png"
          ></img>
          <h1 className="h1-register-page">เพิ่มสินค้า</h1>
        </div>
        {/*<h4 className="title-addstock">เพิ่มสินค้า</h4>*/}
        <div className="addstock-page-con">
          <div className="addstock-container">
            <div className="form-addstock"></div>
            <label className="form-addstock-name">ชื่อสินค้า</label>

            <div className="form2-addstock">
              <input
                className="input-addstock-name"
                onChange={(e) => setProductName(e.target.value)}
              />
            </div>

            <label className="form-addstock-name">รหัสสินค้า</label>
            <div className="form2-addstock">
              <input
                className="input-addstock-name"
                onChange={(e) => setProductCode(e.target.value)}
              />
            </div>

            <label className="form-addstock-name">ราคาสินค้า</label>
            <div className="form2-addstock">
              <input
                type="number"
                min="0"
                className="input-addstock-name"
                onChange={(e) => setPrice(e.target.value)}
              />
            </div>

            <label className="form-addstock-name">เพิ่มรูปสินค้า</label>

            <input
              className="input-addstock-name1"
              type="file"
              onChange={(e) => {
                setFile(e.target.files[0])
              }}
            />
            <p className="p-input-add-stock">หมวดหมู่</p>
            {/**----------------------------------------------------------------------------- */}
            <select
              className="menuaddstock"
              onChange={(e) => {
                setCategory(e.target.value)
              }}
              required
            >
              <option selected value="null" className="option-menuaddstock">
                เลือก...
              </option>
              <option value="ร้านทั่วไป" className="option-menuaddstock">
                ทั่วไป
              </option>
              <option value="ร้านทั่วไป" className="option-menuaddstock">
                อาหาร
              </option>
              <option value="ร้านทั่วไป" className="option-menuaddstock">
                เครื่องดื่ม
              </option>
              <option value="ร้านทั่วไป" className="option-menuaddstock">
                สุขภาพ/ความงาม
              </option>
              <option value="ร้านทั่วไป" className="option-menuaddstock">
                เสื้อผ้า
              </option>
            </select>
            {/*<div className="div-add-stock">
              <input
                className="input-add-stock"
                type="radio"
                id="normal"
                name="หมวดหมู่"
                value="ทั่วไป"
                onChange={(e) => {
                  setCategory(e.target.value);
                }}
              />
              <label className="label-input-add-stock" for="normal">
                ทั่วไป
              </label>
            </div>

            <div className="div-add-stock">
              <input
                className="input-add-stock"
                type="radio"
                id="food"
                name="หมวดหมู่"
                value="อาหาร"
                onChange={(e) => {
                  setCategory(e.target.value);
                }}
              />
              <label className="label-input-add-stock" for="food">
                อาหาร
              </label>
            </div>

            <div className="div-add-stock">
              <input
                className="input-add-stock"
                type="radio"
                id="drink"
                name="หมวดหมู่"
                value="เครื่องดื่ม"
                onChange={(e) => {
                  setCategory(e.target.value);
                }}
              />
              <label className="label-input-add-stock" for="drink">
                เครื่องดื่ม
              </label>
            </div>

            <div className="div-add-stock">
              <input
                className="input-add-stock"
                type="radio"
                id="health"
                name="หมวดหมู่"
                value="สุขภาพ/ความงาม"
                onChange={(e) => {
                  setCategory(e.target.value);
                }}
              />
              <label className="label-input-add-stock" for="health">
                สุขภาพ/ความงาม
              </label>
            </div>

            <div className="div-add-stock">
              <input
                className="input-add-stock"
                type="radio"
                id="clothes"
                name="หมวดหมู่"
                value="เสื้อผ้า"
                onChange={(e) => {
                  setCategory(e.target.value);
                }}
              />
              <label className="label-input-add-stock" for="clothes">
                เสื้อผ้า
              </label>
              </div>*/}

            <p className="p-button-addstock">{statusUpload}</p>
            <div className="divbutton-addstock">
              <button className="button-addstock" onClick={addproductstock}>
                เพิ่มสินค้า
              </button>
            </div>
          </div>
        </div>
      </motion.div>
    </div>
  )
}
export default Addstock
