import React, { useState } from "react";
import { Link, useHistory } from "react-router-dom";
import { useEffect } from "react";
import axios from "axios";
import "./Calculatepointshop.css";

function Calculatepointshop() {
  const history = useHistory();
  //-----------------------------------------------------

  const [id, setId] = useState();
  const [shoppoint, setShopPoint] = useState();
  const [phone, setPhone] = useState("");
  const [destPhone, setdestPhone] = useState("");
  const [amount, setamount] = useState();
  const [transferStatus, setTransferStatus] = useState("");

  const calPoint = () => {
    if (destPhone == "") {
      setTransferStatus("เบอร์ปลายทางไม่ถูกต้อง");
    } else if (phone == destPhone) {
      setTransferStatus("เบอร์ปลายทางไม่ถูกต้อง");
    } else if (amount > shoppoint) {
      setTransferStatus("คะแนนไม่เพียงพอสำหรับการโอน กรุณาเติมคะแนน");
    } else if (amount < 1) {
      setTransferStatus("ยอดซื้อขั้นต่ำ 50-.");
    } else {
      axios
        .put("/calPointTransfer", {
          id: id,
          phone: phone,
          destphone: destPhone,
          amount: amount,
        })
        .then((response) => {
          setTransferStatus(response.data.message);
          if (response.data.loggedIn == true) {
            setShopPoint(response.data.user[0].shoppoint);
            window.location.reload();
          }
        });
    }
  };

  const logout = () => {
    axios.get("/logout");
    window.location.replace("/");
  };

  const gotoTopupPage = () => {
    history.push("/top-up-point-shop");
  };

  useEffect(() => {
    axios.get("/login").then((response) => {
      if (response.data.loggedIn == true) {
        setId(response.data.user[0].id);
        setShopPoint(response.data.user[0].shoppoint);
        setPhone(response.data.user[0].phone);
      }
      if (
        response.data.loggedIn == false ||
        response.data.user[0].role != "shop"
      ) {
        return window.location.replace("/");
      }
    });
  }, []);

  return (
    <>
      <nav class="navbar navbar-light bg-light justify-content-between">
        <Link to="/">
          <i className="fas fa-angle-left">
            <a className="bl">ย้อนกลับ</a>
          </i>
        </Link>
      </nav>
      <button onClick={gotoTopupPage} className="top-up">
        <i class="fas fa-money-bill-alt"></i>
        เติมคะแนน
      </button>
      <div className="calculate">
        <h1>คำนวณคะแนน</h1>
        <h1>shoppoint : {shoppoint}</h1>
        <label>กรอกเบอร์</label>
        <input
          type="text"
          onChange={(e) => {
            setdestPhone(e.target.value);
          }}
        />

        <label>กรอกราคาสินค้า</label>
        <input
          type="number"
          onChange={(e) => {
            setamount(Math.floor(e.target.value / 50));
          }}
        />
        {transferStatus}
        <button onClick={calPoint}>ยืนยัน</button>
      </div>
    </>
  );
}
export default Calculatepointshop;
