import React, { useEffect } from 'react';
import axios from 'axios';
import { useState } from 'react';
import Registeration from '../pages/Registeration';
import { Link } from 'react-router-dom';
import { Redirect } from 'react-router';

export default function Shop() {
    const [id, setId] = useState();
    const [name, setName] = useState("");
    const [phone, setPhone] = useState("");
    const [email, setEmail] = useState("");
    const [point, setPoint] = useState();
    const [type,setType] = useState("");

    const [statusPhone,setStatusPhone] = useState("");

    const [newname, setnewName] = useState("");
    const [newphone, setnewPhone] = useState("");

    axios.defaults.withCredentials = true;
    const logout = () => {
        axios.get("/logout");
        window.location.replace('/');
    }
    const refresh = () => {
        axios.get("/shop");
        window.location.replace('/');
    }
    const nameUpdate = () => {
        axios.put("/updateshopName", { name: newname, id: id });
        axios.get("/shop");
        window.location.replace('/');
    }
    const phoneUpdate = () => {
        axios.put("/updateshopPhone",{phone: newphone,id: id}).then((response) => {
            if(response.data.message){
            return setStatusPhone(response.data.message);
            }
            return window.location.replace('/');
          });
    }
    useEffect(() => {
        axios.get("http://localhost:3001/login").then((response) => {
            if (response.data.loggedIn == true) {
                setId(response.data.user[0].id);
                setName(response.data.user[0].name);
                setPhone(response.data.user[0].phone);
                setEmail(response.data.user[0].email);
                setType(response.data.user[0].type);
                setPoint(response.data.user[0].point);
            }
        });
    }, []);

    return (
        <div>
            <h1>Shop</h1>
            <p>Hi  {name} </p>
            <p>Phone : {phone} <br />Email : {email} <br />Type : {type} <br />Point :  {point} <button onClick={refresh}>refresh</button></p>
            <br />
            <input type="text" placeholder="name"
                onChange={(e) => { setnewName(e.target.value) }
                }></input>
            <button onClick={nameUpdate}>update</button>

            <input type="text" placeholder="Phone"
                onChange={(e) => { setnewPhone(e.target.value) }
                }></input>
                {statusPhone}
            <button onClick={phoneUpdate}>update</button>

            <br /><button onClick={logout}>Logout</button>
        </div>
    )
}
