import React, { useState, useEffect } from 'react'
import { Link, useHistory } from 'react-router-dom'
import axios from 'axios'
import './Topuppointshop.css'
import { resolveMotionValue } from 'framer-motion'

function Topuppointshop() {
  const history = useHistory()
  const [id, setId] = useState()
  const [name, setname] = useState()
  const [phone, setPhone] = useState('')
  const [amount, setAmount] = useState()

  const [statusUpload, setStatusUpload] = useState('')

  const [file, setFile] = useState('')
  const [UploadFile, setUploadFile] = useState('')

  const uploadtransferpic = () => {
    if (!amount || !file) {
      setStatusUpload('กรุณาใส่ข้อมูลให้ครบทุกช่อง')
    } else if (file.size > 2097152) {
      setStatusUpload('ไฟล์ภาพมีขนาดเกิน 2 MB')
    } else {
      setStatusUpload('')
      const formData = new FormData()
      formData.append('file', file)
      if (amount < 1 || !amount || amount > 1000) {
        statusUpload('คะแนนไม่ถูกต้อง')
      } else {
        setStatusUpload('')
        axios
          .post('/uploadtransferpic', formData, {
            headers: {
              'Content-Type': 'multipart/form-data',
            },
          })
          .then((response) => {
            if (response) {
              const { fileName, filePath } = response.data
              setUploadFile({ fileName, filePath })
              axios
                .post('/topup-request', {
                  name: name,
                  shop_id: id,
                  phone: phone,
                  picture: '/transactionpic/' + file.name,
                  amount: amount,
                })
                .then((response) => {
                  if (response.data.success) {
                    alert(response.data.success)
                    history.goBack()
                  }
                })
            }
          })
      }
    }
  }

  useEffect(() => {
    axios.get('/login').then((response) => {
      if (response.data.loggedIn === true) {
        setId(response.data.user[0].id)
        setname(response.data.user[0].name)
        setPhone(response.data.user[0].phone)
      }
      if (
        response.data.loggedIn === false ||
        response.data.user[0].role !== 'shop'
      ) {
        return history.push('/login')
      }
    })
  }, [])

  return (
    <div className="topup-page">
      <nav className="navbar navbar-light bg-light justify-content-between">
        <Link className="buttonblack" to="/calculate-point-shop">
          <i className="fas fa-angle-left">
            <a className="bl">ย้อนกลับ</a>
          </i>
        </Link>
      </nav>
      <div className="topuppoint">
        <div className="topuppoint-up">
          <h2 className="h1topupoint">เติมคะแนน</h2>
          <div className="textbox-topupoint">
            <label className="labeltopupoint">
              กรอกจำนวนคะแนนที่ต้องการ (เติมได้ครั้งละไม่เกิน 1000)
            </label>
            <input
              className="input-addstock-name1"
              type="number"
              placeholder="กรอกจำนวนคะแนน"
              onChange={(e) => {
                setAmount(e.target.value)
              }}
              required
            />
            <label className="labeltopupoint">
              แนบสลิปการโอนเงิน
            </label>
            <input
              className="input-addstock-name1"
              type="file"
              onChange={(e) => {
                setFile(e.target.files[0])
              }}
            />
          </div>
          <div className="div-button-topupoint">
            <p style={{ color: 'red' }}>{statusUpload}</p>

            <button
              className="submit"
              className="button-topupoint"
              onClick={uploadtransferpic}
            >
              ยืนยัน
            </button>
          </div>
        </div>
      </div>
    </div>
  )
}
export default Topuppointshop
