import React, { useState } from "react";
import { Link } from "react-router-dom";
import Modal from "react-modal";
import axios from "axios";
import { useEffect } from "react";
import "./Transferpointshop.css";

const customStyles = {
  content: {
    top: "50%",
    left: "50%",
    right: "auto",
    bottom: "auto",
    marginRight: "-50%",
    transform: "translate(-50%, -50%)",
  },
};

function Transferpointshop() {
  let subtitle;
  const [modalIsOpenCheck, setIsOpenCheck] = useState(false);
  const [modalIsOpenConfirm, setIsOpenConfirm] = useState(false);
  const [destname, setDestName] = useState("");

  function openModalCheck() {
    axios
      .post("/transfer/check", {
        phone: destPhone,
      })
      .then((response) => {
        if (response.data.message) {
          alert(response.data.message);
        } else if (response.data) {
          setDestName(response.data[0].name);
          setIsOpenCheck(true);
        }
      });
  }

  function openModalConfirm() {
    axios
      .put("/shopPointTransfer", {
        id: id,
        phone: phone,
        destphone: destPhone,
        amount: amount,
      })
      .then((response) => {
        if (response.data.message) {
          alert(response.data.message);
        } else if (response.data.loggedIn == true) {
          setIsOpenConfirm(true);
          setPoint(response.data.user[0].point);
        }
      });
  }

  function afterOpenModal() {
    // references are now sync'd and can be accessed.
    subtitle.style.color = "black";
  }

  function closeModal() {
    setIsOpenCheck(false);
    setIsOpenConfirm(false);
  }
  //---------------------------------------------------
  axios.defaults.withCredentials = true;

  const [id, setId] = useState();
  const [point, setPoint] = useState();
  const [name, setName] = useState("");
  const [phone, setPhone] = useState("");
  const [destPhone, setdestPhone] = useState("");
  const [amount, setamount] = useState();

  const logout = () => {
    axios.get("/logout");
    window.location.replace("/");
  };
  const checkinput = () => {
    if (destPhone == "") {
      alert("เบอร์ปลายทางไม่ถูกต้อง");
    } else if (phone == destPhone) {
      alert("เบอร์ปลายทางไม่ถูกต้อง");
    } else if (amount == "") {
      alert("โปรดป้อนจำนวนคะแนน");
    } else if (amount > point) {
      alert("คะแนนไม่เพียงพอสำหรับการโอน");
    } else if (amount < 1) {
      alert("คะแนนไม่ถูกต้อง");
    } else {
      openModalCheck();
    }
  };

  useEffect(() => {
    axios.get("/login").then((response) => {
      if (response.data.loggedIn == true) {
        setId(response.data.user[0].id);
        setName(response.data.user[0].name);
        setPoint(response.data.user[0].point);
        setPhone(response.data.user[0].phone);
      }
      if (
        response.data.loggedIn == false ||
        response.data.user[0].role != "shop"
      ) {
        return window.location.replace("/");
      }
    });
  }, []);

  return (
    <>
      <div className="transfershop">
        <div className="div-transfershop">
        <Modal 
          isOpen={modalIsOpenCheck}
          onAfterOpen={afterOpenModal}
          onRequestClose={closeModal}
          style={customStyles}
          contentLabel="Example Modal"
        >
          <h2 className="h2transfershop" ref={(_subtitle) => (subtitle = _subtitle)}>ตรวจสอบข้อมูล</h2>
          <br></br><h3 className="h3transfershop">
            {name} โอนคะแนนให้ {destname} จำนวนคะแนน {amount}
          </h3><br></br>
          <button className="buttontransfershop" onClick={openModalConfirm}>ยืนยัน</button>
        </Modal>
      </div>
      </div>

      <div className="div-transferpointshop">
        <Modal 
          isOpen={modalIsOpenConfirm}
          onAfterOpen={afterOpenModal}
          onRequestClose={closeModal}
          style={customStyles}
          contentLabel="Example Modal"
        >
          <h2 className="h2transferpointshop" ref={(_subtitle) => (subtitle = _subtitle)}><i className="fas fa-check-circle"></i>โอนคะแนนสำเร็จ</h2>
        </Modal>
      </div>

      <nav className="navbar navbar-light bg-light justify-content-between">
        <Link className="buttonblack" to="/">
          <i className="fas fa-angle-left">
            <a className="bl">ย้อนกลับ</a>
          </i>
        </Link>
      </nav>

      <section className="credit-card">
        <div className="container">
          <div className="card-holder">
            <div className="card-box bg-news">
              <div className="row">
                <div className="col-lg-6">
                  <div className="img-box">
                    <img src="/images/transfer.png" className="img-fluid" />
                  </div>
                </div>

                <div className="col-lg-6">
                  <form>
                    <div className="card-details">
                      <h3 className="title">Point Transfer</h3>
                      <div className="row">
                        <div className="form-group col-sm-7">
                          <div className="inner-addon right-addon">
                            <label for="card-holder">เบอร์โทรศัพท์</label>
                            <i className="far fa-user"></i>
                            <input
                              id="card-holder"
                              type="text"
                              className="form-control"
                              placeholder="09xxxxxxxx"
                              aria-label="Phone"
                              aria-describedby="basic-addon1"
                              onChange={(e) => {
                                setdestPhone(e.target.value);
                              }}
                              required
                            />
                          </div>
                        </div>

                        <div className="form-group col-sm-8">
                          <div className="inner-addon right-addon">
                            <label for="card-number">จำนวนคะแนน</label>
                            <i className="far fa-credit-card"></i>
                            <input
                              id="card-number"
                              type="number"
                              className="form-control"
                              placeholder="0"
                              aria-label="amount"
                              aria-describedby="basic-addon1"
                              onChange={(e) => {
                                setamount(e.target.value);
                              }}
                              required
                            />
                          </div>
                        </div>

                        <div className="form-group col-sm-12">
                          <button
                            type="button"
                            className="btn btn-primary btn-block"
                            onClick={checkinput}
                          >
                            ตรวจสอบข้อมูล
                          </button>
                        </div>
                      </div>
                    </div>
                  </form>
                </div>
              </div>
            </div>
          </div>
        </div>
      </section>
      {/*<div className='transferpointshop'>

                <h1>โอนคะแนน</h1>
                <h1>phone : {phone}</h1>
                <h2>Point : {point}</h2>
                <h1>โอนคะแนน</h1>
                <label>กรอกเบอร์</label>
                <input
                    type="text"
                    onChange={(e) => {
                        setdestPhone(e.target.value);
                    }} />

                <label>กรอกจำนวนคะแนน</label>
                <input
                    type="number" min="0"
                    onChange={(e) => {
                        setamount(e.target.value);
                    }}
                />
                <button onClick={transferSubmit}>ยืนยัน</button>

                </div>*/}
    </>
  );
}
export default Transferpointshop;
