import React from "react";
import {
  BrowserRouter as Router,
  Route,
  Switch,
  useHistory,
} from "react-router-dom";

import axios from "axios";
import { useEffect } from "react";
import { useState } from "react";

import Registeration from "./pages/Registeration";
import Login from "./pages/Login";
import Main from "./pages/Main";
import Home from "./components/Home";
import Forgotpwd from "./components/Forgotpwd";

import Transferpoint from "./components/Transferpoint";
import Transaction from "./components/Transaction";
import Promotion from "./components/Promotion";
import Memberprofile from "./components/Memberprofile";

import Transferpointshop from "./components/Transferpointshop";
import Calculatepointshop from "./components/Calculatepointshop";
import Transactionshop from "./components/Transactionshop";
import Promotionshop from "./components/Promotionshop";
import Topuppointshop from "./components/Topuppointshop";
import Shopprofile from "./components/Shopprofile";
import Promotiondetails from "./components/Promotiondetails";
import Mypromotionshop from "./components/Mypromotionshop";
import Productstockpage from "./components/Productstockpage";
import Addstock from "./components/Addstock";
import Addpromotion from "./components/Addpromotion";

import Orderhistory from "./components/Orderhistory";

import ProtectedRoute from "./components/ProtectedRoute";

import "./App.css";

import { AnimatePresence } from "framer-motion";
import { useLocation } from "react-router-dom";

//axios.defaults.baseURL = "http://35.185.187.110:3001";
axios.defaults.baseURL = "http://localhost:3001";

function App() {
  const history = useHistory();
  axios.defaults.withCredentials = true;
  const [Auth, setAuth] = useState(false);

  useEffect(() => {
    axios.get("/login").then((response) => {
      if (response.data.loggedIn === true) {
        setAuth(true);
      } else {
        setAuth(false);
      }
    });
  }, []);

  /* ******************************** */
  const location = useLocation();

  /* ******************************** */
  return (
    <AnimatePresence exitBeforeEnter>
      <Router location={location} key={location.pathname}>
        <Route exact path="/home" component={Home} />
        <Route exact path="/" component={Main} />
        <Route exact path="/registration" component={Registeration} />
        <Route exact path="/login" component={Login} />
        <Route exact path="/forgotpwd" component={Forgotpwd} />
        {/**-----------------------------------member----------------------------------- */}
        <ProtectedRoute
          Auth={Auth}
          path="/transfer-point"
          component={Transferpoint}
        />
        <ProtectedRoute
          Auth={Auth}
          path="/transaction"
          component={Transaction}
        />
        <ProtectedRoute Auth={Auth} path="/promotion" component={Promotion} />
        <ProtectedRoute
          Auth={Auth}
          path="/memberprofile"
          component={Memberprofile}
        />
        {/**-----------------------------------shop----------------------------------- */}
        <ProtectedRoute
          Auth={Auth}
          path="/transfer-pointshop"
          component={Transferpointshop}
        />
        <ProtectedRoute
          Auth={Auth}
          path="/calculate-point-shop"
          component={Calculatepointshop}
        />
        <ProtectedRoute
          Auth={Auth}
          path="/transaction-shop"
          component={Transactionshop}
        />
        <ProtectedRoute
          Auth={Auth}
          path="/promotion-shop"
          component={Promotionshop}
        />
        <ProtectedRoute
          Auth={Auth}
          path="/top-up-point-shop"
          component={Topuppointshop}
        />
        <ProtectedRoute
          Auth={Auth}
          path="/shopprofile/"
          component={Shopprofile}
        />
        <ProtectedRoute
          Auth={Auth}
          path="/promotiondetails/:shopname"
          component={Promotiondetails}
        />
        <ProtectedRoute
          Auth={Auth}
          path="/myshop"
          component={Mypromotionshop}
        />
        <ProtectedRoute
          Auth={Auth}
          path="/addpromotion/:option"
          component={Addpromotion}
        />
        <ProtectedRoute
          Auth={Auth}
          path="/product-stockpage"
          component={Productstockpage}
        />
        <ProtectedRoute Auth={Auth} path="/addstock" component={Addstock} />
        <ProtectedRoute
          Auth={Auth}
          path="/order-history"
          component={Orderhistory}
        />
      </Router>
    </AnimatePresence>
  );
}

export default App;
