import React from "react";
import {
  BrowserRouter as Router,
  Redirect,
  Route,
  Switch,
} from "react-router-dom";
import "./App.css";
import axios from "axios";
import { useEffect } from "react";
import { useState } from "react";

import Registeration from "./pages/Registeration";
import Login from "./pages/Login";
import Main from "./pages/Main";

import Transferpoint from "./components/Transferpoint";
import Transaction from "./components/Transaction";
import Promotion from "./components/Promotion";
import Memberprofile from "./components/Memberprofile";

import Transferpointshop from "./components/Transferpointshop";
import Calculatepointshop from "./components/Calculatepointshop";
import Transactionshop from "./components/Transactionshop";
import Promotionshop from "./components/Promotionshop";
import Topuppointshop from "./components/Topuppointshop";
import Shopprofile from "./components/Shopprofile";
import Promotiondetails from "./components/Promotiondetails";
import Mypromotionshop from "./components/Mypromotionshop";

axios.defaults.baseURL = "http://localhost:3001";

function App() {
  axios.defaults.withCredentials = true;

  const [Auth, setAuth] = useState(false);
  useEffect(() => {
    axios.get("/login").then((response) => {
      if (response.data.loggedIn == true) {
        setAuth(response.data.loggedIn);
      }
      if (response.data.loggedIn == false) {
        setAuth(response.data.loggedIn);
      }
    });
  }, []);
  return (
    <Switch>
      <Route exact path="/" component={Main} />
      <Route path="/registration" exact render={(props) => <Registeration />} />
      <Route path="/login" exact render={(props) => <Login />} />

      <Route path="/transfer-point" component={Transferpoint} />
      <Route path="/transaction" component={Transaction} />
      <Route path="/promotion" component={Promotion} />
      <Route path="/memberprofile" component={Memberprofile} />

      <Route path="/transfer-point-shop" component={Transferpointshop} />
      <Route path="/calculate-point-shop" component={Calculatepointshop} />
      <Route path="/transaction-shop" component={Transactionshop} />
      <Route path="/promotion-shop" component={Promotionshop} />
      <Route path="/top-up-point-shop" component={Topuppointshop} />
      <Route path="/shopprofile" component={Shopprofile} />
      <Route path="/promotiondetails" component={Promotiondetails} />
      <Route path="/myshop" component={Mypromotionshop} />
    </Switch>
  );
}

export default App;
