import React from "react";
import { useEffect } from 'react';
import axios from 'axios';
import { useState } from 'react';
//import './styles.css';
import { Redirect } from "react-router";

export default function Registeration() {
  const [nameReg, setNameReg] = useState("");
  const [phoneReg, setPhoneReg] = useState("");
  const [emailReg, setEmailReg] = useState("");
  const [passwordReg, setPasswordReg] = useState("");
  const [typeReg, setTypeReg] = useState("");

  const [memberregisterStatus, setmemberRegisterStatus] = useState("");
  const [shopregisterStatus, setshopRegisterStatus] = useState("");
  const [isLoggedIn, setIsLoggedIn] = useState("flase");

  axios.defaults.withCredentials = true;
  //register
  const memberregister = () => {
    axios
      .post("http://localhost:3001/memberregister", {
        name: nameReg,
        phone: phoneReg,
        email: emailReg,
        password: passwordReg,
      })
      .then((response) => {
        setmemberRegisterStatus(response.data.message);
      });
  };
  const shopregister = () => {
    axios
      .post("http://localhost:3001/shopregister", {
        name: nameReg,
        phone: phoneReg,
        email: emailReg,
        password: passwordReg,
        type: typeReg
      })
      .then((response) => {
        setshopRegisterStatus(response.data.message);
      });
  };

  useEffect(() => {
    axios.get("http://localhost:3001/login").then((response) => {
      if (response.data.loggedIn == true) {
        setIsLoggedIn("true");
      }
    });
  }, []);

  if (isLoggedIn == "true") {
    return (
      <Redirect to="/" />
    );
  }
  return (
    <div className="App">
      <div className="base-container">
        <div className="header">Member Registration</div>
        <div className="content">
          <div className="image">
            <img className="login-img" src="/images/login-img.jpg" />
          </div>
          <div className="form">
          <div className="form-group">
            <label>Name</label>
              <input
                type="text"
                placeholder="Name"
                onChange={(e) => {
                  setNameReg(e.target.value);
                }}
              />
            </div>
            <div className="form-group">
              <label>Phone</label>
              <input
                type="text"
                placeholder="Phone"
                onChange={(e) => {
                  setPhoneReg(e.target.value);
                }}
              />
            </div>
            <div className="form-group">
            <label>Email</label>
              <input
                type="text"
                placeholder="Email"
                onChange={(e) => {
                  setEmailReg(e.target.value);
                }}
              />
            </div>
            <div className="form-group">
            <label>Password</label>
              <input
                type="text"
                placeholder="Password"
                onChange={(e) => {
                  setPasswordReg(e.target.value);
                }}
              />
            </div>

            <div className="notification">
              <p>{memberregisterStatus}</p>
            </div>

          </div>
        </div>
        <div className="footer">
          <button type="button" className="btn" onClick={memberregister} >
            Register
          </button>
        </div>
      </div>

      <div className="base-container">
        <div className="header">Shop Registration</div>
        <div className="content">
          <div className="image">
            <img className="login-img" src="/images/login-img.jpg" />
          </div>
          <div className="form">
          <div className="form-group">
            <label>Name</label>
              <input
                type="text"
                placeholder="Name"
                onChange={(e) => {
                  setNameReg(e.target.value);
                }}
              />
            </div>
            <div className="form-group">
              <label>Phone</label>
              <input
                type="text"
                placeholder="Phone"
                onChange={(e) => {
                  setPhoneReg(e.target.value);
                }}
              />
            </div>
            <div className="form-group">
              <label>Email</label>
              <input
                type="text"
                placeholder="Email"
                onChange={(e) => {
                  setEmailReg(e.target.value);
                }}
              />
            </div>
            <div className="form-group">
              <label>Shop Type</label>
              <input
                type="text"
                placeholder="Shop Type"
                onChange={(e) => {
                  setTypeReg(e.target.value);
                }}
              />
            </div>
            <div className="form-group">
            <label>Password</label>
              <input
                type="text"
                placeholder="Password"
                onChange={(e) => {
                  setPasswordReg(e.target.value);
                }}
              />
            </div>

            <div className="notification">
              <p>{shopregisterStatus}</p>
            </div>

          </div>
        </div>
        <div className="footer">
          <button type="button" className="btn" onClick={shopregister} >
            Register
          </button>

        </div>
      </div>

    </div>
  );
}
