import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { useEffect } from 'react';
import axios from 'axios';
import './Transferpoint.css';



function Transferpoint() {

    //-------------------------------------------------------
    axios.defaults.withCredentials = true;

    const [id, setId] = useState();
    const [point, setPoint] = useState();
    const [phone, setPhone] = useState("");
    const [destPhone, setdestPhone] = useState("");
    const [amount, setamount] = useState();

    const logout = () => {
        axios.get("/logout");
        window.location.replace('/');
    }
    const transferSubmit = () => {
        if (destPhone == "") {
            alert("เบอร์ปลายทางไม่ถูกต้อง");
        } else if (phone == destPhone) {
            alert("เบอร์ปลายทางไม่ถูกต้อง");
        } else if (amount > point) {
            alert("คะแนนไม่เพียงพอสำหรับการโอน");
        } else if (amount < 1) {
            alert("คะแนนไม่ถูกต้อง");
        } else {
            axios.put("/memberPointTransfer", {
                id: id,
                phone: phone,
                destphone: destPhone,
                amount: amount
            })
                .then((response) => {
                    if (response.data.message) {
                        alert(response.data.message);
                    }
                    //setTransferStatus(response.data.message);
                    else if (response.data.loggedIn == true) {
                        alert("โอนคะแนนสำเร็จ");
                        setPoint(response.data.user[0].point);
                    }
                });
        }
    }

    useEffect(() => {
        axios.get("/login").then((response) => {
            if (response.data.loggedIn == true) {
                setId(response.data.user[0].id);
                setPoint(response.data.user[0].point);
                setPhone(response.data.user[0].phone);
            }
            if (response.data.loggedIn == false || response.data.user[0].role != 'member') {
                return (window.location.replace('/'));
            }
        });
    }, []);

    return (
        <>
            <nav class="navbar navbar-light bg-light justify-content-between">
                <Link to="/"  ><i className="fas fa-angle-left"><a className="bl">ย้อนกลับ</a></i></Link>
            </nav>
            <section class="credit-card">
                <div class="container">
                    <div class="card-holder">
                        <div class="card-box bg-news">
                            <div class="row">
                                <div class="col-lg-6">
                                    <div class="img-box">
                                        <img src="https://bootdey.com/img/Content/avatar/avatar7.png" class="img-fluid" />
                                    </div>
                                </div>

                                <div class="col-lg-6">
                                    <form>
                                        <div class="card-details">
                                            <h3 class="title">Point Transfer</h3>
                                            <div class="row">

                                                <div class="form-group col-sm-7">
                                                    <div class="inner-addon right-addon">
                                                        <label for="card-holder">เบอร์โทรศัพท์</label>
                                                        <i class="far fa-user"></i>
                                                        <input id="card-holder" type="text" class="form-control" placeholder="09xxxxxxxx" aria-label="Phone" aria-describedby="basic-addon1"
                                                            onChange={(e) => {
                                                                setdestPhone(e.target.value);
                                                            }}
                                                            required
                                                        />
                                                    </div>
                                                </div>

                                                <div class="form-group col-sm-8">
                                                    <div class="inner-addon right-addon">
                                                        <label for="card-number">จำนวนคะแนน</label>
                                                        <i class="far fa-credit-card"></i>
                                                        <input id="card-number" type="number" class="form-control" placeholder="0" aria-label="amount" aria-describedby="basic-addon1"
                                                            onChange={(e) => {
                                                                setamount(e.target.value);
                                                            }}
                                                            required
                                                        />
                                                    </div>
                                                </div>

                                                <div class="form-group col-sm-12">
                                                    <button type="button" class="btn btn-primary btn-block">ตรวจสอบข้อมูล</button>
                                                </div>

                                            </div>
                                        </div>
                                    </form>
                                </div>

                            </div>
                        </div>
                    </div>
                </div>
            </section>

            <div className='transferpoint'>
                <h1>phone : {phone}</h1>
                <h1>โอนคะแนน</h1>
                <h2>Point : {point}</h2>
                <label>กรอกเบอร์</label>
                <input
                    type="text"
                    onChange={(e) => {
                        setdestPhone(e.target.value);
                    }} />

                <label>กรอกจำนวนคะแนน</label>
                <input
                    type="number" min="0"
                    onChange={(e) => {
                        setamount(e.target.value);
                    }}
                />
                <button onClick={transferSubmit}>ยืนยัน</button>

            </div>

        </>

    );
}
export default Transferpoint;
