import React from "react";
import axios from "axios";
import { useState } from "react";
import { useHistory } from "react-router";
import { useEffect } from "react";
import moment from "moment";
import { Accordion, Card } from "react-bootstrap";
import { Link } from "react-router-dom";
import { Loader } from "semantic-ui-react";
import "./Orderhistory.css";
import { motion } from "framer-motion";

function Orderhistory() {
  const [active, setactive] = useState(true); //loader
  //-----------------------------------------------------
  const renderAccordion = (list, index) => {
    return (
      <Accordion key={index}>
        <Card>
          <Accordion.Toggle
            as={Card.Header}
            eventKey={list}
            className="accordion-order"
          >
            <div className="div-order">
              {moment(list.time).format("DD/MM/YYYY")}{" "}
              <p className="p-orderhistory">
                รายละเอียดเพิ่มเติม <i className="fas fa-arrow-down"></i>
              </p>
            </div>
          </Accordion.Toggle>
          <Accordion.Collapse eventKey={list}>
            <Card.Body>
              {list.list.map((showlist) => {
                return (
                  <ul>
                    <li className="li-orderhistory">
                      ชื่อสินค้า : {showlist.product_name} X {showlist.number}{" "}
                      ราคา : {showlist.price * showlist.number}
                    </li>
                  </ul>
                );
              })}
            </Card.Body>
          </Accordion.Collapse>
        </Card>
      </Accordion>
    );
  };

  //-----------------------------------------------
  const history = useHistory();
  const [phone, setPhone] = useState("");
  const [Orderhistory, setMyorderHistory] = useState([]);
  //-----------------------------------------------

  /* ********************************** */

  const pageTransition = {
    in: {
      opacity: 1,
      x: 0,
    },
    out: {
      opacity: 0,
      x: 100,
    },
  };

  /* ********************************** */

  useEffect(() => {
    const timer = setTimeout(() => {
      setactive(false);
      axios.get("/login").then((response) => {
        if (response.data.loggedIn === true) {
          if (response.data.user[0].role === "member") {
            setPhone(response.data.user[0].phone);
          } else if (response.data.user[0].role === "shop") {
            setPhone(response.data.user[0].phone);
          }
        } else if (
          response.data.loggedIn === false ||
          !response.data.user[0].role
        ) {
          return history.push("/login");
        }
      });

      axios.get("/getmyorderhistory").then((response) => {
        if (response) {
          setMyorderHistory(response.data);
        }
      });
    }, 1000);
    return () => clearTimeout(timer);
  }, []);

  return (
    <div>
      <nav className="navbar navbar-light bg-light justify-content-between">
        <Link className="buttonblack" to="/">
          <i className="fas fa-angle-left">
            <a className="bl">ย้อนกลับ</a>
          </i>
        </Link>
        <div className="div-img-logo2">
          <img className="img-logoo" src="/images/logo.png" />
        </div>
      </nav>

      <motion.div
        initial="out"
        animate="in"
        exit="out"
        variants={pageTransition}
      >
        <div className="div-h1-Orderhistory">
          <h3 className="h1-Orderhistory">ประวัติการซื้อสินค้า</h3>
        </div>
        <hr />
        <Loader active={active} />
        {Orderhistory.map(renderAccordion)}
      </motion.div>
    </div>
  );
}
export default Orderhistory;
