import React, { useState } from 'react'
import { useHistory } from 'react-router'
import axios from 'axios'
import { useEffect } from 'react'
import { motion } from 'framer-motion'
import { Loader } from 'semantic-ui-react'
import './Menushop.css'

function Menushop() {
  //--------------------------------------------------
  const history = useHistory()
  const [active, setactive] = useState(false) //loader
  const [point, setPoint] = useState()
  const [name, setname] = useState('')

  const logout = () => {
    setactive(true)
    axios.get('/logout')
    const timer = setTimeout(() => {
      history.push('/home')
    }, 1000)
    return () => clearTimeout(timer)
  }

  const refresh = () => {
    axios.get('/shop')
    history.go()
  }

  const profilepage = () => {
    history.push('/shopprofile')
  }

  const transferpage = () => {
    history.push('/transfer-pointshop')
    history.go()
  }

  const calculatepage = () => {
    history.push('/calculate-point-shop')
    history.go()
  }

  const transactionpage = () => {
    history.push('/transaction-shop')
  }

  const promotionpage = () => {
    history.push('/promotion-shop')
  }

  const orderhistory = () => {
    history.push('/order-history')
  }

  const productstockpage = () => {
    history.push('/product-stockpage')
  }

  /* ********************************** */

  const pageTransition = {
    in: {
      opacity: 1,
      x: 0,
    },
    out: {
      opacity: 0,
      x: 100,
    },
  }

  const navTransition = {
    in: {
      opacity: 1,
      x: 0,
    },
    out: {
      opacity: 0,
      x: 0,
    },
  }
  /* ********************************** */

  useEffect(() => {
    axios.get('/login').then((response) => {
      if (response.data.loggedIn === true) {
        axios.get('/shop')
        setPoint(response.data.user[0].point)
      }
      if (
        response.data.loggedIn === false ||
        response.data.user[0].role !== 'shop'
      ) {
        return history.push('/login')
      }
    })
  }, [])

  return (
    <div className="menushop-page">
      <link
        rel="stylesheet"
        type="text/css"
        href="//netdna.bootstrapcdn.com/font-awesome/4.1.0/css/font-awesome.min.css"
      />
      <link
        href="//maxcdn.bootstrapcdn.com/font-awesome/4.2.0/css/font-awesome.min.css"
        rel="stylesheet"
      ></link>

      {/*<motion.div
        initial="out"
        animate="in"
        exit="out"
        variants={navTransition}
      >*/}
      <nav class="navbar navbar-light bg-light justify-content-between">
        <a className="nav-tnc">
          <div className="div-img-logo">
            <img className="img-logoo" src="/images/logo.png" />
          </div>
          T N C
        </a>
        <form class="form-inline">
          <button onClick={logout} className="bn632-hover bn24">
            ออกจากระบบ
          </button>
        </form>
      </nav>
      {/*</motion.div>*/}
      <Loader active={active} />

      <motion.div
        initial="out"
        animate="in"
        exit="out"
        variants={pageTransition}
      >
        <div className="head-frame">
          <div className="data-frame">
            <div className="bg-circle">
              <div className="div-point_point_1_2">
                <p className="point_point_1_2">คะแนนสะสม</p>
              </div>
              <p className="point_1">{point}</p>
              <i onClick={refresh} className="fas fa-sync-alt"></i>
            </div>
            <div>
              <p>ยินดีต้อนรับคุณ {name}</p>
            </div>
            <div className="profile-button">
              <button onClick={profilepage} className="bnmenumember">
                ข้อมูลของฉัน <i className="fas fa-user-edit"></i>
              </button>
            </div>
          </div>
        </div>
        {/* <hr />*/}
        <div className="container bootstrap snippets bootdey">
          <div className="row margin-bottom-10">
            <div className="col-md-4 col-sm-6">
              <div
                onClick={transferpage}
                className="servive-block servive-block-yellow"
              >
                <img
                  className="imgtransaction1"
                  src="images/transferpoint.png"
                ></img>
                {/*<i className="fas fa-exchange-alt"></i>*/}
                <h3 className="heading-md-h2">โอนคะแนน</h3>
              </div>
            </div>
            <div onClick={calculatepage} className="col-md-4 col-sm-6">
              <div className="servive-block servive-block-green">
                <img
                  className="imgtransaction1"
                  src="images/calculatepoint.png"
                ></img>
                {/*<i className="fas fa-cash-register"></i>*/}
                <h3 className="heading-md-h2">คิดเงิน</h3>
              </div>
            </div>
            <div onClick={transactionpage} className="col-md-4 col-sm-6">
              <div className="servive-block servive-block-purple">
                <img
                  className="imgtransaction1"
                  src="images/transaction.png"
                ></img>
                {/*<i className="fas fa-redo-alt"></i>*/}
                <h3 className="heading-md-h2">ประวัติการโอนคะแนน</h3>
              </div>
            </div>
            <div onClick={orderhistory} className="col-md-4 col-sm-6">
              <div className="servive-block servive-block-aqua">
                <img
                  className="imgtransaction1"
                  src="images/shophistory.png"
                ></img>
                {/*<i className="fas fa-cart-arrow-down"></i>*/}
                <h3 class="heading-md-h2">ประวัติการซื้อสินค้า</h3>
              </div>
            </div>
            <div className="col-md-4 col-sm-6">
              <div
                onClick={promotionpage}
                className="servive-block servive-block-yellow"
              >
                <img
                  className="imgtransaction1"
                  src="images/promotion.png"
                ></img>
                {/*<i className="fas fa-tag"></i>*/}
                <h3 className="heading-md-h2">โปรโมชัน</h3>
              </div>
            </div>

            <div onClick={productstockpage} className="col-md-4 col-sm-6">
              <div className="servive-block servive-block-green">
                <img className="imgtransaction1" src="images/stock.png"></img>
                {/*<i className="fas fa-shopping-basket"></i>*/}
                <h3 className="heading-md-h2">คลังสินค้า</h3>
              </div>
            </div>
          </div>
        </div>
      </motion.div>
    </div>
  )
}
export default Menushop
